INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4995, 'Tormeel 5 mg 30 f.c. tab.', 'تورميل 5 مجم 30 قرص', '126', '96', 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Solifenacin succinate', 'European egyptian pharm. ind.', 'Tab', 'أقراص', '5 mg', '3', NULL, NULL, NULL, NULL, 849, '2025-09-23', 0, 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank', 0, 0, 0);
