INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Occufloxine 0.3% eye drops 5ml', 9119, 'اوكيوفلوكسين 0.3 % قطرة للعين 5مل', '8', NULL, 'Antibiotic.quinolone', 'Antibiotic.quinolone', NULL, 'Ofloxacin', 'El nile.', 'Drops', 'نقط', '0.3%', '1', NULL, NULL, NULL, '6221077063413', 843, '2022-12-07', NULL, 0, 'For the treatment of infections (respiratory tract, kidney, skin, soft tissue, UTI), urethral and cervical gonorrhoea.', 'Ofloxacin acts on DNA gyrase and toposiomerase IV, enzymes which, like human topoisomerase, prevents the excessive supercoiling of DNA during replication or transcription. By inhibiting their function, the drug thereby inhibits normal cell division.', 'Ofloxacin is a quinolone/fluoroquinolone antibiotic. Ofloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Ofloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank');
