INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Anahal 100% inhalation solution 100 ml', 14093, 'اناهال 100% محلول استنشاق 100 مل', '693', '160', 'Anesthetic', 'Anesthetic', NULL, 'Isoflurane', 'Pharco', 'Solution', 'محلول', '100%', '1', 'يستعمل في عملية الحث على التخدير قبل البدء بالعملية، و للمحافظة على التخدير في أثنائها', NULL, 'description a stable non-explosive inhalation anesthetic relatively free from significant side effects. mechanism of action isoflurane induces a reduction in junctional conductance by decreasing gap junction channel opening times and increasing gap juncti', '6221151002482', 1857, '2025-03-11', NULL, 0, 'For induction and maintenance of general anesthesia.', 'Isoflurane induces a reduction in junctional conductance by decreasing gap junction channel opening times and increasing gap junction channel closing times. Isoflurane also activates calcium dependent ATPase in the sarcoplasmic reticulum by increasing the fluidity of the lipid membrane. Also appears to bind the D subunit of ATP synthase and NADH dehydogenase. Isoflurane also binds to the GABA receptor, the large conductance Ca<sup>2+</sup> activated potassium channel, the glutamate receptor and the glycine receptor.', 'Isoflurane is a general inhalation anesthetic used for induction and maintenance of general anesthesia. It induces muscle relaxation and reduces pains sensitivity by altering tissue excitability. It does so by decreasing the extent of gap junction mediated cell-cell coupling and altering the activity of the channels that underlie the action potential.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ventamor anti-aging serum 50 ml', 27420, 'فينتامور سيروم مضاد للتجاعيد 50 مل', '360', '320', 'Skin care', 'Skin care', NULL, 'Vitamin c 10% + hyaluronic acid + marine collagen + ginseng + caviar', 'Prime pharma', 'Serum', 'سيروم', '50 ml', '1', NULL, NULL, NULL, NULL, 965, '2025-02-24', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rheumaton 750mg 20 f.c. tabs', 4597, 'روماتون 750 مجم 20 قرص', '20', NULL, 'Nsaid.acetic acid derivatives', 'Nsaid.acetic acid derivatives', NULL, 'Nabumetone', 'Nsaid.acetic acid derivatives', 'Tab', 'أقراص', '750mg', '2', NULL, NULL, NULL, NULL, 727, '2025-06-18', NULL, 0, '**Indicated** for:[label]



1) Symptomatic relief in rheumatoid arthritis.



2) Symptomatic relief in osteoarthritis.', 'Nabumetone''s active metabolite, 6-MNA, is an inhibitor of both COX-1 and COX-2 although it exhibits some COX-2 selectivity.[label,A178903] Inhibition of COX-1 and COX-2 reduces conversion of arachidonic acid to PGs and thromboxane (TXA<sub>2</sub>). This reduction in prostanoid production is the common mechanism that mediates the effects of nambutone.



PGE<sub>2</sub> is the primary PG involved in modulation of nociception.[A179023] It mediates peripheral sensitization through a variety of effects.[T116,A179023] PGE<sub>2</sub> activates the G<sub>q</sub>-coupled EP<sub>1</sub> receptor leading to increased activity of the inositol trisphosphate/phospholipase C pathway. Activation of this pathway releases intracellular stores of calcium which directly reduces action potential threshold and activates protein kinase C (PKC) which contributes to several indirect mechanisms. PGE<sub>2</sub> also activates the EP<sub>4</sub> receptor, coupled to G<sub>s</sub>, which activates the adenylyl cyclase/protein kinase A (AC/PKA) signaling pathway. PKA and PKC both contribute to the potentiation of transient receptor potential cation channel subfamily V member 1 (TRPV1) potentiation, which increases sensitivity to heat stimuli. They also activate tetrodotoxin-resistant sodium channels and inhibit inward potassium currents. PKA further contributes to the activation of the P2X3 purine receptor and sensitization of T-type calcium channels. The activation and sensitization of depolarizing ion channels and inhibition of inward potassium currents serve to reduce the intensity of stimulus necessary to generate action potentials in nociceptive sensory afferents. PGE<sub>2</sub> act via EP<sub>3</sub> to increase sensitivity to bradykinin and via EP<sub>2</sub> to further increase heat sensitivity. Central sensitization occurs in the dorsal horn of the spinal cord and is mediated by the EP<sub>2</sub> receptor which couples to G<sub>s</sub>. Pre-synaptically, this receptor increases the release of pro-nociceptive neurotransmitters glutamate, CGRP, and substance P. Post-synaptically it increases the activity of AMPA and NMDA receptors and produces inhibition of inhibitory glycinergic neurons. Together these lead to a reduced threshold of activating, allowing low intensity stimuli to generate pain signals. PGI<sub>2</sub> is known to play a role via its G<sub>s</sub>-coupled IP receptor although the magnitude of its contribution varies. It has been proposed to be of greater importance in painful inflammatory conditions such as arthritis. By limiting sensitization, both peripheral and central, via these pathways NSAIDs can effectively reduce inflammatory pain.



PGI<sub>2</sub> and PGE<sub>2</sub> contribute to acute inflammation via their IP and EP<sub>2</sub> receptors.[T116,A179044] Similarly to β adrenergic receptors these are G<sub>s</sub>-coupled and mediate vasodilation through the AC/PKA pathway. PGE<sub>2</sub> also contributes by increasing leukocyte adhesion to the endothelium and attracts the cells to the site of injury.[T116] PGD<sub>2</sub> plays a role in the activation of endothelial cell release of cytokines through its DP<sub>1</sub> receptor.[A179044] PGI<sub>2</sub> and PGE<sub>2</sub> modulate T-helper cell activation and differentiation through IP, EP<sub>2</sub>, and EP<sub>4</sub> receptors which is believed to be an important activity in the pathology of arthritic conditions. By limiting the production of these PGs at the site of injury, NSAIDs can reduce inflammation.



PGE<sub>2</sub> can cross the blood-brain barrier and act on excitatory G<sub>q</sub> EP<sub>3</sub> receptors on thermoregulatory neurons in the hypothalamus.[T116] This activation triggers an increase in heat-generation and a reduction in heat-loss to produce a fever. NSAIDs prevent the generation of PGE<sub>2</sub> thereby reducing the activity of these neurons.



The adverse effects of NSAIDs stem from the protective and regulatory roles of prostanoids which have been well-characterized.[T116] PGI<sub>2</sub> and PGE<sub>2</sub> regulate blood flow to the kidney by similar mechanisms to the vasodilation they produce in inflammation. Prevention of this regulation by NSAIDs produces vasoconstriction which limits renal function by reducing blood flow and the hydrostatic pressure which drives filtration. PGE<sub>2</sub> also regulates gastric protection via EP<sub>3</sub> receptors which are, in this location, coupled to G<sub>i</sub> which inhibits the AC/PKA pathway. This reduces the secretion of protons by H<sup>+</sup>/K<sup>+</sup> ATPase in parietal cells and increases the secretion of mucus and HCO<sub>3</sub><sup>-</sup> by superficial endothelial cells. Disruption of this protective action by NSAIDs lead to ulceration of the gastric mucosa. Lastly, disruption of PGI<sub>2</sub>, which opposes platelet aggregation, generation by COX-2 selective agents leads to an imbalance with TXA<sub>2</sub> generated by COX-1, which promotes aggregation of platelets, leading to increased risk of thrombosis. Since nabumetone is somewhat COX-2 selective it is thought to promote this imbalance and increase thrombotic risk.[A178903]', 'NSAIDs, like nabumetone, are well established as analgesics. NSAIDs reduce both peripheral and central sensitization of nociceptive neurons due to inflammation which contribute to hyperalgesia and allodynia.[T116,A179023] This sensitization occurs through reducing the action potential threshold in peripheral neurons, reducing the intensity of painful stimuli needed to produce a painful sensation. Centrally, activation of dorsal horn neurons occurs along with increased release of glutamate, calcitonin gene-related peptide (CGRP), and substance P which increase the transmission of painful stimuli. Coupled with this is an inhibition glycinergic neurons which normally inhibit pain transmission, a phenomenon known as disinhibition. Increased activity ofn-methyl d-aspartate (NMDA) and α-amino-3-hydroxy-5-methyl-4-isoxazolepropionic acid (AMPA) receptors leads to the establishment of central sensitization, allowing both mild painful and innocuous stimuli to produce action potentials in nociceptive projection neurons. NSAIDs are effective in reducing mild-moderate acute and chronic nociceptive pain, however, the usefulness of NSAIDs in neuropathic pain is limited.



The anti-inflammatory effect of NSAIDs is mediated by preventing vasodilation, increases in vascular permeability, and the release of cytokines from endothelial cells.[T116,A179044] These three effects together prevent immunocompetent cells from migrating to the site of injury thereby preventing additional damage and inflammation due to activation of the immune system at the site of damage. PGs also modulate T-helper cell activation and differentiation, an activity which is thought to be of importance in arthritic conditions.



The anti-pyretic effect of NSAIDs is mediated through preventing increases in temperature by prostaglandins (PGs) via the hypothalamus.[T116] Activation of this process by other inflammatory mediators relies upon subsequent action by PGs, therefore NSAIDs are able to reduce fever due to these mediators as well.



The adverse effects of NSAIDs are related to their therapeutic effects.[T116] The same vasodilatory action which occurs in inflammation also serves to regulate blood flow to the kidneys through the afferent renal arteries. NSAIDs are widely known as nephrotoxic agents as the reduction in PGs produces vasoconstriction of these arteries resulting in reduced blood flow to the kidneys and a subsequent decline in renal function. Reductions in mucus and HCO<sub>3</sub><sup>-</sup> secretion in the stomach increases the risk of ulceration by limiting the protection mediated by PGs. Lastly, COX-2 selective agents like nabumetone can unbalance prothrombotic and antithrombotic prostanoid generation leading to increased platelet aggregation and increased risk of thrombosis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Apetryl 2mg 30 tab.', 836, 'ابتريل 2مجم 30 قرص', '45', '30', 'Anti-epileptic.benzodiazepines', 'Anti-epileptic.benzodiazepines', NULL, 'Clonazepam', 'Multi-apex', 'Tab', 'أقراص', '2mg', '3', NULL, NULL, NULL, '6223003200046', 8412, '2024-11-06', NULL, 0, 'Clonazepam is indicated as monotherapy or as an adjunct in the treatment of Lennox-Gastaut syndrome (petit mal variant), akinetic, and myoclonic seizures.[FDA Label][F3787] Furthermore, clonazepam may also be of some value in patients with absence spells (petit mal) who have failed to respond to succinimides.[FDA Label][F3787] Additionally, clonazepam is also indicated for the treatment of panic disorder, with or without agoraphobia, as defined in the DSM-V.[FDA Label]



Alternatively, some regional prescribing information note that clonazepam is indicated for all clinical forms of epileptic disease and seizures in adults, especially absence seizures (petit mal) including atypical absence; primary or secondarily generalised tonic-clonic (grand mal), tonic or clonic seizures; partial (focal) seizures with elementary or complex symptomatology; various forms of myoclonic seizures, myoclonus and associated abnormal movements.[L5572,F3796] Such regional label data also has clonazepam indicated for most types of epilepsy in infants and children, especially absences (petit mal), myoclonic seizures and tonic-clonic fits, whether due to primary generalized epilepsy or to secondary generalization of partial epilepsy.[F3796]                                                                                                                                                                 ', 'Gamma-Aminobutyric acid (GABA) is considered the principal inhibitory neurotransmitter in the human body [A175423, A175438, A175441]. When GABA binds to GABA(a) receptors found in neuron synapses, chloride ions are conducted across neuron cell membranes via an ion channel in the receptors [A175423, A175438, A175441, F3787]. With enough chloride ions conducted, the local, associated neuron membrane potentials are hyperpolarized - making it more difficult or less likely for action potentials to fire, ultimately resulting in less excitation of the neurons [A175423, A175438, A175441, F3787]. 



Subsequently, benzodiazepines like clonazepam can bind to benzodiazepine receptors that are components of various varieties of GABA(a) receptors [A175423, A175438, A175441, F3787, L5572, F3763]. This binding acts to enhance the effects of GABA by increasing GABA affinity for the GABA(a) receptor, which ultimately enhances GABA ligand binding at the receptors [A175423, A175438, A175441, F3787, L5572, F3763]. This enhanced ligand binding of the inhibitory neurotransmitter GABA to the receptors increases the aforementioned chloride ion conduction (perhaps reportedly via an increase in the frequency of the chloride channel opening), resulting in a hyperpolarized cell membrane that prevents further excitation of the associated neuron cells [A175423, A175438, A175441, F3787, L5572, F3763]. Combined with the notion that such benzodiazepine receptor associated GABA(a) receptors exist both peripherally and in the CNS, this activity consequently facilitates various effects like sedation, hypnosis, skeletal muscle relaxation, anticonvulsant activity, and anxiolytic action [A175423, A175438, A175441, F3787, L5572, F3763].



In particular, when out of the ordinary rapid and repetitive electrical signals are released in the CNS, it is proposed that the brain can become over-stimulated and ordinary functions are disrupted - resulting in seizure activity [A175441]. By enhancing the neuro-inhibitory activity of GABA, it is believed that clonazepam can facilitate in decreasing any excessive electrical nerve activity in the CNS that might be contributing to seizures [A175441]. Concurrently, it is also believed that clonazepam''s actions in enhancing GABA effects may inhibit neuronal activity proposed to occur in amygdala-centered fear circuits - therefore assisting in the management of anxiety or panic [A175438].', 'The pharmacodynamic properties of clonazepam are common among benzodiazepines and include anticonvulsive, sedative, muscle relaxing and anxiolytic effects [A175423, L5572, F3763, F3787]. Animal data and electroencephalographic investigations in man have shown that clonazepam rapidly suppresses many types of paroxysmal activity including the spike and wave discharge in absence seizures (petit mal), slow spike wave, generalized spike wave, spikes with temporal or other locations, as well as irregular spikes and waves [FDA Label] [A175423, L5572, F3763, F3787]. Moreover, the agent can also decrease the frequency, amplitude, duration, and spread of discharge in minor motor seizures [FDA Label] [F3763].



Generalized EEG abnormalities are more readily suppressed by clonazepam than are focal EEG abnormalities such as focal spikes [L5572]. Clonazepam has beneficial effects in generalized and focal epilepsies [L5572].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Spiramycin 1.5 m.i.u. 10 tab.', 10802, 'سبيراميسين 1.5 مليون وحدة دولية 10 قرص', '20', NULL, 'Antibiotic.macrolide', 'Antibiotic.macrolide', NULL, 'Spiramycin', 'Pharopharma', 'Tab', 'أقراص', NULL, '1', 'مضاد حيوي', NULL, 'about spiramycin macrolide antibiotic antibacterial (systemic antiprotozoal in toxoplasmosis cryptosporidiosis. mechanism of action of spiramycin spiramycin is a member of macrolide antibiotic. it binds to the 50s sub unit of bacterial ribosome and inhibi', '6221094077219', 1370, '2022-12-09', NULL, 0, 'Macrolide antibiotic for treatment of various infections.', 'The mechanism of action of macrolides has been a matter of controversy for some time. Spiramycin, a 16-membered macrolide, inhibits translocation by binding to bacterial 50S ribosomal subunits with an apparent 1 : 1 stoichiometry. This antibiotic is a potent inhibitor of the binding to the ribosome of both donor and acceptor substrates. The primary mechanism of action is done by stimulation of dissociation of peptidyl-tRNA from ribosomes during translocation.I ', 'The absolute bioavailability of oral spiramycin is generally within the range of 30 to 40%. After a 1 g oral dose, the maximum serum drug concentration was found to be within the range 0.4 to 1.4 mg/L.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ulcfree 150mg 20 caps.', 11826, 'الس فري 150مجم 20 كبسولة', '21', NULL, 'Peptic ulcer.anti-histamine.h2 antagonist', 'Peptic ulcer.anti-histamine.h2 antagonist', NULL, 'Nizatidine', 'Eva pharma', 'Cap', 'كبسولة', '150mg', '2', NULL, NULL, 'indication for the treatment of acid-reflux disorders (gerd) peptic ulcer disease active benign gastric ulcer and active duodenal ulcer. pharmacodynamics nizatidine is a competitive reversible inhibitor of histamine at the histamine h2-receptors particula', '6223002450848', 1019, '2022-12-13', NULL, 0, 'For the treatment of acid-reflux disorders (GERD), peptic ulcer disease, active benign gastric ulcer, and active duodenal ulcer.', 'Nizatidine competes with histamine for binding at the H<sub>2</sub>-receptors on the gastric basolateral membrane of parietal cells. Competitive inhibition results in reduction of basal and nocturnal gastric acid secretions. The drug also decreases the gastric acid response to stimuli such as food, caffeine, insulin, betazole, or pentagastrin.', 'Nizatidine is a competitive, reversible inhibitor of histamine at the histamine H2-receptors, particularly those in the gastric parietal cells. By inhibiting the action of histamine on stomach cells, nizatidine reduces stomach acid production. Nizatidine had no demonstrable antiandrogenic action. Full-dose therapy for the problems treated by nizatidine lasts no longer than 8 weeks. It has been demonstrated that treatment with a reduced dose of nizatidine is effective as maintenance therapy following healing of active duodenal ulcers.', 'DrugBank');
