INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ibidroxil 250mg 8 caps', 20673, 'ابيدروكسيل 250مجم 8 كبسولات', '8', NULL, 'Antibiotic.cephalosporin.first-generation', 'Antibiotic.cephalosporin.first-generation', NULL, 'Cefadroxil', 'Glaxo smithkline', 'Cap', 'كبسولة', '250mg', '1', NULL, NULL, NULL, NULL, 901, '2022-08-28', NULL, 0, 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank');
