INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5281, 'Azathioprine a.i.t. 50 mg 100 f.c.tabs.', 'ازاثيوبرين ايه اي تي 50مجم 100 قرص', '75', NULL, 'Immunosuppressant', 'Azathioprine', 'Arab caps', 'Tab', 'أقراص', '50 mg', '1', NULL, NULL, NULL, NULL, 744, '2023-05-06', 0, 'Azathioprine is indicated to treat rheumatoid arthritis and prevent renal transplant rejection.[L11214]', 'Azathioprine''s mechanism of action is not entirely understood but it may be related to inhibition of purine synthesis, along with inhibition of B and T cells.[A19872]



6-thioguanine triphosphate, a metabolite of azathioprine, modulates activation of rac1 when costimulated with CD28, inducing T cell apoptosis.[A19872] This may be mediated through rac1''s action on mitogen-activated protein kinase, NF-kappaB.[A19872]', 'Azathioprine is an immunosuppressive agent which functions through modulation of rac1 to induce T cell apoptosis, as well as other unknown immunosuppressive functions.[A19872] It has a long duration of action as it is given daily, and has a narrow therapeutic index.[L11214] Patients should be counselled regarding the risk of malignancies of the skin and lymphomas.[L11214]', 'DrugBank', 0, 0, 0);
