INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5408, 'Gemiflonce 320 mg 5 f.c. tab.', 'جيميفلونس 320مجم 5 اقراص', '36', NULL, 'Antibiotic.quinolone', 'Gemifloxacin', 'Eva pharma > next pharma', 'Tab', 'أقراص', '320 mg', '1', NULL, NULL, NULL, NULL, 843, '2022-08-01', 0, 'For the treatment of bacterial infection caused by susceptible strains such as <i>S. pneumoniae</i>, <i>H. influenzae</i>, <i>H. parainfluenzae</i>, or <i>M. catarrhalis</i>, <i>S. pneumoniae</i> (including multi-drug resistant strains [MDRSP]), <i>M. pneumoniae</i>, <i>C. pneumoniae</i>, or <i>K. pneumoniae</i>.', 'The bactericidal action of gemifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Gemifloxacin is a quinolone/fluoroquinolone antibiotic. Gemifloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Gemifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
