INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5477, 'Gervizine 20 caps', 'جيرفيزين 20 كبسولة', '12', NULL, 'Vitamins', 'Calcium pantothenate+folic acid+magnesium +manganese +nicotinamide+vitamin(a+b1+b2+b6+c+d3+e)+zinc', 'Rameda', 'Cap', 'كبسولة', NULL, '2', NULL, NULL, '6223003970086', NULL, 1055, '2022-12-05', 0, 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5480, 'Cortopect syrup 60 ml', 'كورتوبيكت شراب 60 مل', '50', NULL, 'Mucolytic', 'Acetylcysteine', 'Biomed', 'Syrup', 'شراب', '60 ml', '1', NULL, NULL, NULL, NULL, 836, '2025-06-27', 0, 'Acetylcysteine is indicated for mucolytic therapy and in the management of [acetaminophen] overdose.[L31378,L31383,L31388,L31478]', 'A number of possible mechanisms for the mucolytic activity of acetylcysteine have been proposed. Acetylcysteine''s sulfhydryl groups may hydrolize disulfide bonds within mucin, breaking down the oligomers, and making the mucin less viscous.[A228173,L31388] Acetylcysteine has also been shown to reduce mucin secretion in rat models.[A228173] It is an antioxidant in its own right but is also deacetylated to cysteine, which participates in the synthesis of the antioxidant glutathione.[A228173] The antioxidant activity may also alter intracellular redox reactions, decreasing phosphorylation of EGFR and MAPK, which decrease transcription of the gene MUC5AC which produces mucin.[A228173]



