INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6077, 'Metrogypton 500 mg 20 f.c. tabs.', 'ميتروجيبتون 500مجم 20 قرص', '34', NULL, 'Antiprotozoal.nitroimidazole', 'Metronidazole', 'Gypto pharma', 'Tab', 'أقراص', '500 mg', '2', NULL, NULL, NULL, NULL, 3258, '2025-07-20', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Metronidazole is a commonly used antibiotic, belonging to the nitroimidazole class of antibiotics.[L3754] It is frequently used to treat gastrointestinal infections as well as trichomoniasis and giardiasis, and amebiasis which are parasitic infections.[A181036,A181039] Metronidazole has been used as an antibiotic for several decades[L7429], with added antiparasitic properties that set it apart from many other antibacterial drugs, allowing it to treat a wide variety of infections. It is available in capsule form, tablet form, and topical form, and suppository preparations for the treatment of various infections.', '["A02BD11", "P01AB52", "D06BX01", "A02BD03", "J01XD01", "J01RA04", "A02BD01", "J01RA14", "A02BD15", "J01RA03", "A01AB17", "G01AF20", "J01RA10", "A02BD02", "A02BD13", "G01AF01", "P01AB51", "A02BD08", "P01AB01"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6080, 'Hexgabalin 150mg 30 caps.', 'هيكسجابالين 150مجم 30 كبسولة', '120', NULL, 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '150mg', '3', 'علاج حالات الصرع -- تقليل التشنجات الناتجة من الصرع', 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', '6223002145201', NULL, 908, '2022-08-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pregabalin is structurally similar to gamma-aminobutyric acid (GABA) - an inhibitory neurotransmitter.[A173995] It may be used to manage neuropathic pain, postherpetic neuralgia, and fibromyalgia among other conditions.[A187190] Although as per the FDA Label the mechanism of action has not been definitively defined, there is evidence that pregabalin exerts its effects by binding to the α2δ subunit of voltage-dependent calcium channels.[A187190][L7066] Pregabalin is marketed by Pfizer under the trade name Lyrica and Lyrica Cr (extended release).[L1006][L7066] It may have dependence liability if misused but the risk appears to be highest in patients with current or past substance use disorders.[A31161]', '["N03AX16"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6082, 'Hexitol 1.25mg/ml mouth wash 100 ml', 'هيكستول 1.25مجم/مل مضمضة للفم 100 مل', '50', '38.95', 'Antiseptic solution.oral mouth wash', 'Chlorhexidine', 'Arab drug company.', 'Mouth wash', 'غسول للفم', '1.25mg', '1', 'مضمضة مطهرة للفم وتستخدم لازالة الروائح الكريهة وتساعد فى مقاومة تسوس الاسنان', 'about chlorhexidine a bisbiguanide antimicrobial agent anti-infective antiseptic. mechanism of action of chlorhexidine this drug has positive charge so it attach to the negative charge cell wall. this destabilizes and destroys the integrity and penetrates', '6221060001606', NULL, 1999, '2024-10-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Chlorhexidine is a broad-spectrum antimicrobial biguanide used as a topical antiseptic and in dental practice for the treatment of inflammatory dental conditions caused by microorganisms.[L11512] It is one of the most common skin and mucous membrane antiseptic agents in use today.[A190417] The molecule itself is a cationic bis-guanide consisting of two 4-chlorophenyl rings and two biguanide groups joined by a central hexamethylene chain.[A190453] Topical chlorhexidine for disinfection, as well as oral rinses for dental use, carries activity against a broad range of pathogens including bacteria, yeasts, and viruses.[L11518,L11512,A190453]

Chlorhexidine was developed in the UK by Imperial Chemical Industries in the early 1950s[A190474] and was introduced to the US in the 1970s.[L11572] The FDA withdrew its approval for the use of chlorhexidine gluconate topical tincture 0.5%, due to a significant number of reports concerning chemical and thermal burns associated with the use of this product.[L43942,L44027] Other formulations of chlorhexidine continue to be available.', '["R02AA05", "S03AA04", "D08AC52", "D08AC02", "S01AX09", "D09AA12", "B05CA02", "A01AB03", "S02AA09"]', '[]');
