INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6386, 'Idixor 75mg 7 tab.', 'ايديكسور 75مجم 7 اقراص', '41', NULL, 'Psychiatric.antidepressants', 'Venlafaxine', 'International drug agency (idi) > novell pharma', 'Tab', 'أقراص', '75mg', '1', NULL, 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', '6223004070136', NULL, 965, '2023-01-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Venlafaxine is an antidepressant and a serotonin and norepinephrine reuptake inhibitor (SNRI). Its active metabolite, [desvenlafaxine], works by blocking the reuptake of serotonin and norepinephrine, which are key neurotransmitters in mood regulation. Venlafaxine is officially approved to treat major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder, and panic disorder in adults.[L43030] The immediate formulation of the drug, marketed as Effexor, was first approved by the FDA in 1993 and the extended-release formulation, Effexor XR, was later introduced in 1997.[A252065]

Venlafaxine has been used as a first-line treatment for MDD, GAD, social anxiety disorder, and panic disorder in Canada for many years. It was also considered a second-line treatment for obsessive-compulsive disorder (OCD).[A177226,A177235] Venlafaxine was also investigated in off-label uses for the prophylaxis of migraine headaches,[A413,A177229] for reduction of vasomotor symptoms associated with menopause,[A177238] and for the management of neuropathic pain (although there is only minimal evidence of efficacy for this condition).[A177232]', '["N06AX16"]', '[]');
