INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6403, 'Zolam 0.25 mg 10 tabs.', 'زولام 0.25مجم 10 قرص', '10', '6', 'Psychiatric.anxiolytics-hypnotics', 'Alprazolam', 'Amoun', 'Tab', 'أقراص', '0.25 mg', '1', NULL, NULL, NULL, NULL, 1428, '2025-10-15', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Alprazolam is a triazolobenzodiazepine indicated for the treatment of anxiety and panic disorders.[L34783, L34788] It is mainly metabolized by CYP3As and so is contraindicated with CYP3A inhibitors like ketoconazole and itraconazole.[L34783, L34788] Benzodiazepine treatment should be stopped gradually by tapering down a patient''s dose to avoid withdrawal symptoms.[A18125] Alprazolam''s adverse effects are generally related to the sedation it can cause.[A18125] Alprazolam has been mixed with alcohol as a drug of abuse to potentiate the sedative effects of the drug which may lead to coma and death.[A18125] Alprazolam was given FDA approval on October 16, 1981.[L6148]', '["N05BA12"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6404, 'Imipramine 25mg 50 tab.', 'اميبرامين 25مجم 50 قرص', '53', '32.5', 'Tricyclic antidepressant', 'Imipramine', 'Acdima international trading', 'Tab', 'أقراص', '25mg', '5', 'مضاد للاكتئاب', 'about imipramine tricyclic antidepressant (tertiary amine) dibenzazepine derivative antidepressant migraine prophylatic. mechanism of action of imipramine imipramine acts by inhibiting the re-uptake of noradrenaline and serotonin (5 ht) in the cns nerve t', '6224000588366', NULL, 6601, '2024-11-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Imipramine, the prototypical tricyclic antidepressant (TCA), is a dibenzazepine-derivative TCA. TCAs are structurally similar to phenothiazines. They contain a tricyclic ring system with an alkyl amine substituent on the central ring. In non-depressed individuals, imipramine does not affect mood or arousal, but may cause sedation. In depressed individuals, imipramine exerts a positive effect on mood. TCAs are potent inhibitors of serotonin and norepinephrine reuptake. Tertiary amine TCAs, such as imipramine and amitriptyline, are more potent inhibitors of serotonin reuptake than secondary amine TCAs, such as nortriptyline and desipramine. TCAs also block histamine H1receptors, α1-adrenergic receptors and muscarinic receptors, which accounts for their sedative, hypotensive and anticholinergic effects (e.g. blurred vision, dry mouth, constipation, urinary retention), respectively [A6584]. Imipramine has less sedative and anticholinergic effects than the tertiary amine TCAs, amitriptyline and clomipramine. Imipramine may be used to treat depression and nocturnal enuresis in children [FDA Label]. Unlabeled indications include chronic and neuropathic pain (including diabetic neuropathy), panic disorder, attention-deficit/hyperactivity disorder (ADHD), and post-traumatic stress disorder (PTSD) [L1349,L1348,A31900,L1351,L1352,L1353,A31904].', '["N06AA02"]', '[]');
