INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Feldene flash 20 mg 10 fast dissolving tab', 4765, 'فلدين فلاش 20مجم 10 اقراص سريعة الذوبان', '45', NULL, 'Nsaid.oxicam', 'Nsaid.oxicam', NULL, 'Piroxicam', 'Pfizer', 'Tab', 'أقراص', '20 mg', '1', NULL, NULL, NULL, '6221180000350', 1611, '2022-12-05', NULL, 0, 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank');
