INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Polytar hair shampoo 150 ml', 25053, 'بوليتار شامبو للشعر 150مل', '130', '100', 'Hair care', 'Hair care', NULL, 'Coal tar+cade oil', 'Weiser', 'Amp', 'أمبول', '150 ml', '1', NULL, NULL, NULL, '6223002261413', 1919, '2025-05-26', NULL, 0, 'Relieving itching, irritation, redness, dryness, scaling, and flaking of the skin caused by psoriasis, seborrhea, or eczema. ', 'Coal tar ointment is a keratolytic. It works by slowing bacterial growth and loosening and softening scales and crust.', 'Decrease bacterial growth. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Hyper oil 50 ml glass dropping bottle', 20632, 'هايبر اويل 50 مل زجاجة', '1,250', '1200', 'Healing topical', 'Healing topical', NULL, 'Hypericum+neem', 'Ri.mos > technomed group-egypt', 'Drop', 'نقط', '50 ml', '1', NULL, NULL, 'wound care treatment using a single product is possible thanks to hyperoilï-: advanced multi-functional and self-sufficient wound dressing. multi-functional because suitable for lesions of any type and nature. self-sufficient preventing the aid of disinfe', NULL, 1876, '2025-06-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Erdolytic 300 mg 20 caps.', 18510, 'اردوليتيك 300مجم 20 كبسولة', '124', '61', 'Mucolytic', 'Mucolytic', NULL, 'Erdosteine', 'Andalous pharma', 'Cap', 'كبسولة', '300 mg', '2', 'مذيب للبلغم -- يخفف من الكحة المصحوبة بالبلغم', NULL, 'mechanism of action erdosteine an orally mucolytic agent. erdosteine is a thiol derivative developed for the treatment of chronic obstructive bronchitis including acute infective exacerbation of chronic bronchitis. erdosteine contains 2 blocked sulfhydryl', '6223004690037', 4342, '2024-07-13', NULL, 0, 'Fro the treatment of chronic bronchitis in adults.', 'Erdosteine, is an orally administered mucolytic agent. It is classified as a thiol derivative and produced for the management of symptoms caused by chronic obstructive bronchitis.  Erdosteine contains sulfhydryl groups which are released after hepatic first-pass metabolism in the liver. Its active metabolites (3 in number) exert both mucolytic activity and scavenging activity against free radicals. Erdosteine acts to regulate the production of mucus in the airway and regulates its viscosity while enhancing mucociliary transport. This leads to an increase in expectoration. Erdosteine shows inhibition against the effects of free radicals from cigarette smoke. Clinical studies in patients with chronic obstructive lung disease (COPD) have shown that this drug is generally safe and well tolerated. Erdosteine 300mg twice daily reduced cough (both frequency and severity) and sputum viscosity more quickly and more effectively than placebo and reduced the adhesivity of sputum more effectively than ambroxol 30mg twice daily. Co-administration of erdosteine and amoxicillin in patients with acute infective exacerbation of chronic bronchitis resulted in higher concentrations of the antibiotic in the sputum, leading to earlier and more pronounced amelioration of clinical symptoms compared with placebo. Erdosteine is associated with a low incidence of adverse events, most of which are gastrointestinal and generally mild.', NULL, 'DrugBank');
