INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Diet-sweet 100 tabs', 3651, 'دايت-سويت 100 قرص', '34', '25', 'Sweetener', 'Sweetener', NULL, 'Aspartame', 'Amriya', 'Tab', 'أقراص', NULL, '1', 'سكر دايت', NULL, NULL, '6221075010112', 2402, '2024-03-20', NULL, 0, 'Used as a diet supplement and sugar substitute.', '180 to 200 times sweeter than sucrose, it is metabolized as a protein and its subsequent amino-acids used up in there respective mechanisms.', 'Aspartame (L-alpha-aspartyl-L-phenylalanine methyl ester) is a low-calorie sweetener used to sweeten a wide variety of low- and reduced-calorie foods and beverages, including low-calorie tabletop sweeteners. Aspartame is composed of two amino acids, aspartic acid and phenylalanine, as the methyl ester. Aspartic acid and phenylalanine are also found naturally in protein containing foods, including meats, grains and dairy products. Methyl esters are also found naturally in many foods such as fruits and vegetable and their juices. Upon digestion, aspartame breaks down into three components (aspartic acid, phenylalanine and methanol), which are then absorbed into the blood and used in normal body processes. Neither aspartame nor its components accumulates in the body. These components are used in the body in the same ways as when they are derived from common foods.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dolascar gel', 31760, 'دولاسكار جل', '150', NULL, 'Scar therapy', 'Scar therapy', NULL, 'Cyclopentasiloxane+cyclohexasiloxane+dimethicone+collagen+sodium hyaluronate+bee propolis+vite', 'Smartec > dolavet pharma', 'Gel', 'جل', NULL, '1', 'كريم يستخدم لازالة الندبات', NULL, 'specialized gel for management of keloids & hypertrophic scars helps in softening and flattening new and old hypertrophic scars and keloids resulting after surgeries or burns helps in improving scars appearance height texture and color', NULL, 1031, '2023-03-06', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fsh 75 i.u. 5 lyophilized pd. amps.', 19438, 'اف اس اتش 75 وحدة دولية 5 امبولات للحقن باودر مجفدة', '660', NULL, 'Infertility drugs.follicle stimulating hormone', 'Infertility drugs.follicle stimulating hormone', NULL, 'Follitropin (recombinant fsh)', 'Sedico', 'Amp', 'أمبول', NULL, '5', 'يستخدم في النساء المصابات بالعقم للمساعدة في نمو المبايض الصحية (الناضجة) وإطلاق بيضة للمساعدة في الحمل', NULL, NULL, '6221042422054', 2883, '2023-06-14', NULL, 0, 'In women having been diagnosed with primary ovarian failure, it is used in combination with human chorionic gonadotropin (hCG) to assist in ovulation and fertility. In men with hypogonadotrophic hypogonadism, it is used to induce spermatogenesis. Follitropin may also be used to cause the ovary to produce several follicles, which can then be harvested for use in gamete intrafallopian transfer (GIFT) or in vitro fertilization (IVF).', 'Follitropin is a recombinant form of endogenous follicle stimulating hormone (FSH). FSH binds to the follicle stimulating hormone receptor which is a G-coupled transmembrane receptor. Binding of the FSH to its receptor seems to induce phosphorylation and activation of the PI3K (Phosphatidylinositol-3-kinase) and Akt signaling pathway, which is known to regulate many other metabolic and related survival/maturation functions in cells.', 'Used for the treatment of female infertility, Follitropin beta or follicle stimulating hormone (FSH) stimulates ovarian follicular growth in women who do not have primary ovarian failure. FSH, the active component of Follitropin beta is the primary hormone responsible for follicular recruitment and development.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Aminosteril n hepa 8% i.v.infusion 500 ml', 582, 'امينوستريل ان هيبا 8% محلول 500 مل', '202', '100', 'Sterile solution', 'Sterile solution', NULL, 'Amino acids', 'Fresenius kabi', 'Unknown', 'غير محدد', '8%', '1', NULL, NULL, NULL, NULL, 1444, '2024-11-23', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tadatrona 20 mg 2 f.c. tabs.', 32131, 'تاداترونا 20مجم 2 قرص', '50', NULL, 'Tonic for men', 'Tonic for men', NULL, 'Tadalafil', 'Averroes pharma', 'Tab', 'أقراص', '20 mg', '1', 'مقوى للرجال', NULL, NULL, '6224008179849', 2907, '2023-04-23', NULL, 0, 'Tadalafil is indicated for the treatment of erectile dysfunction (ED) and either alone or in combination with [finasteride] for the treatment of benign prostatic hypertrophy (BPH).[L39095,L39439] It is also indicated for the treatment of pulmonary arterial hypertension (PAH) both alone and in combination with [macitentan] or other endothelin-1 antagonists.[L39100,L39105,L50622]', 'Tadalafil is a selective phosphodiesterase-5 (PDE5) inhibitor that produces several downstream effects with the most common therapeutic effect being smooth muscle relaxation. [L39095] Patients may experience ED due to a variety of causes including psychogenic, neurogenic, vasculogenic, iatrogenic, or endocrine. [A242392] These causes result in dysfunction of penile smooth muscle relaxation through either disrupted neuronal signaling or direct influence on smooth muscle cells. During sexual arousal, non-adrenergic non-cholinergic (NANC) neurons release nitric oxide (NO). Nitric oxide stimulates guanylate cyclase which converts guanosine triphosphate to cyclic guanosine monophosphate (cGMP).[A242287, A242377] cGMP activates the cGMP-dependent kinase (PKG) in a signal cascade which activates K+ channels leading to inhibition of Ca2+ channels, inhibits platelet activation, and inhibits smooth muscle cell proliferation while inducing apoptosis. This signal cascade is attenuated by PDE5 which breaks the phosphodiester bond of cGMP, converting it to GMP. Inhibition of PDE5 by tadalafil increases signaling via the PKG cascade which supports penile smooth muscle relaxation during sexual arousal by decreasing Ca2+ entry into smooth muscle cells. This smooth muscle relaxation allows blood to fill the corpus cavernosum thereby producing an erection.



In PAH, blood pressure in the pulmonary arteries is raised due to a variety of mechanisms stemming from endothelial dysfunction.[A242377] Decreased production of NO and prostacyclin reduce vasodilatory signaling while overproduction of endothelin-1 and thromboxane increase vasoconstriction. Inflammation, thromboses, and hypoxia later contribute to vascular remodeling which further reduces luminal size. The resultant increase in blood pressure reduces the capacity for gas exchange and increases afterload at the right ventricle, producing symptoms of dyspnea, fatigue, and dizziness as well as leading to right-sided heart failure. Tadalafil exerts its therapeutic effect in PAH through boosting NO-cGMP signaling to contribute to smooth muscle relaxation as with ED.



Lastly, tadalafil is used to treat BPH.[L39095] BPH produces urinary dysfunction through hyperproliferation of the epithelial and smooth muscle layers of the prostate.[A242382] The increased size of the prostate blocks urine flow through the urethra resulting in higher residual volumes due to incomplete emptying. Tadalafil does not appear to exert its benefit via smooth muscle relaxation of the prostate. It may instead exert its effect through a mix of increased oxygenation and decreased inflammation, which decreases tissue remodeling, and inhibition of cell proliferation through the cGMP cascade.



The decreased affinity for PDE6 compared to other PDE5 inhibitors may explain the decreased incidence of visual side effects as PDE6 is present in the eye and contributes to color vision.[L39100, L39105, A242287]', 'Tadalafil exerts a therapeutic effect in ED by increasing sexual stimulation-dependant smooth muscle relaxation in the penis, allowing the corpus cavernosum to fill with blood to produce an erection.[A242287, A242377] Smooth muscle relaxation in the pulmonary vasculature helps to produce vasodilation in PAH which reduces blood pressure in the pulmonary arteries.[A242377] In BPH, tadalafil may contribute to decreased smooth muscle cell proliferation which may reduce the size of the prostate and relieve the anatomical obstruction which produces urinary symptoms of BPH.[A242382] The decreased affinity of tadalafil for PDE6 compared to other PDE5 inhibitors may explain the reduced incidence of visual side effects.[L39100, L39105, A242287]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mepacal d f 30 tablets', 7962, 'ميباكال دي اف 30 قرص', '14', NULL, 'Calcium supplement', 'Calcium supplement', NULL, 'Calcium+vitamin d3+flourine', 'Mepaco', 'Tablet', 'أقراص', NULL, '3', 'مكمل غذائى لدعم صحة العظام والجهاز المناعى والوقاية من الكساح وهشاشة العظام', NULL, NULL, '6223002217304', 1901, '2022-12-06', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cellcept 250 mg 100 caps.', 16093, 'سيلسيبت 250 مجم 100 كبسولة', '1,460', '910', 'Immunosuppressants', 'Immunosuppressants', NULL, 'Mycophenolate mofetil', 'F.hoffman la roche', 'Cap', 'كبسولة', '250 mg', '1', NULL, NULL, 'indication for the prophylaxis of organ rejection in patients receiving allogeneic renal cardiac or hepatic transplants. mycophenolate mofetil should be used concomitantly with cyclosporine and corticosteroids. mechanism of action mycophenolate mofetil is', '7640128011569', 2114, '2024-11-05', NULL, 0, 'Mycophenolate mofetil is indicated in combination with other immunosuppressants to prevent the rejection of kidney, heart, or liver transplants in adult and pediatric patients ≥3 months old.[L42020] Mycophenolate mofetil may also be used off-label as a second-line treatment for autoimmune hepatitis that has not responded adequately to first-line therapy.[A180814] Other off-label uses of this drug include lupus-associated nephritis and dermatitis in children.[A180817]', 'The active metabolite of mycophenolate, mycophenolic acid, prevents T-cell and B-cell proliferation and the production of cytotoxic T-cells and antibodies. Lymphocyte and monocyte adhesion to endothelial cells of blood vessels that normally part of inflammation is prevented via the glycosylation of cell adhesion molecules by MPA.[A180892] MPA inhibits de novo purine biosynthesis (that promotes immune cell proliferation) by inhibiting inosine 5’-monophosphate dehydrogenase enzyme (IMPDH), with a preferential inhibition of IMPDH II.[A180826] IMPDH normally transforms inosine monophosphate (IMP) to xanthine monophosphate (XMP), a metabolite contributing to the production of guanosine triphosphate (GTP).[A180799,A180805,A180817] GTP is an important molecule for the synthesis of ribonucleic acid (RNA), deoxyribonucleic acid (DNA), and protein. As a result of the above cascade of effects, mycophenolate mofetil reduces de-novo production of guanosine nucleotides, interfering with the synthesis of DNA, RNA, and protein required for immune cell production.[A180826] Further contributing to the above anti-inflammatory effects, MMF depletes tetrahydrobiopterin, causing the decreased function of inducible nitric oxide synthase enzyme, in turn decreasing the production of peroxynitrite, a molecule that promotes inflammation.[A180907]', 'Mycophenolate mofetil is a prodrug of mycophenolic acid (MPA). The active form of mycophenolate, MPA, prevents the proliferation of immune cells and the formation of antibodies that cause transplant rejection.[A180805] The above effects lead to higher rates of successful transplantation, avoiding the devastating effects of graft rejection.



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Viradin facial cleanser 200 ml', 29394, 'فيرادين غسول منظف للوجه 200 مل', '60', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Hi-care > roventis pharma', 'Cleanser', 'منظف', '200 ml', '1', NULL, NULL, '*** indications ï- all types of acne and black heads. ï- protection from further breakouts of acne. ï- managing acne scars and spots. ï- deep facial cleanser for all skin types. ***directions for use twice daily gently massage on the face in circular moti', '6224000201715', 677, '2022-12-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nevilob plus 5/12.5 mg 20 tab', 23511, 'نيفيلوب بلس 5/12.5مجم 20 قرص', '86', '65', 'Antihypertensive.combined beta blocker with diuretic', 'Antihypertensive.combined beta blocker with diuretic', NULL, 'Nebivolol hydrochloride+hydrochlorothiazide', 'Marcyrl co.', 'Tab', 'أقراص', '12.5 mg', '2', 'علاج ارتفاع ضغط الدم', NULL, NULL, '6223003571290', 11091, '2024-06-13', NULL, 0, 'Nebivolol is indicated to treat hypertension.[A2762,A182579,L7985,L7988]', 'Nebivolol is a highly selective beta-1 adrenergic receptor antagonist[A182579] with weak beta-2 adrenergic receptor antagonist activity.[A182585] Blocking beta-1 adrenergic receptors by d-nebivolol leads to decreased resting heart rate, exercise heart rate, myocardial contracility, systolic blood pressure, and diastolic blood pressure.[A182603,A182579,A182585,A182615] The selectivity of d-nebivolol limits the magnitude of beta blocker adverse effects in the airways or relating to insulin sensitivity.[A182615] Nebivolol also inhibits aldosterone, and beta-1 antagonism in the juxtaglomerular apparatus also inhibits the release of renin.[A182615] Decreased aldosterone leads to decreased blood volume, and decreased renin leads to reduced vasoconstriction.[A182615] l-nebivolol is responsible for beta-3 adrenergic receptor agonist activity that stimulates endothelial nitric oxide synthase, increasing nitric oxide levels; leading to vasodilation, decreased peripheral vascular resistance, increased stroke volume, ejection fraction, and cardiac output.[A2762,A182603,A182579,A182585,A182615] The vasodilation, reduced oxidative stress, and reduced platelet volume and aggregation of nebivolol may lead to benefits in heart failure patients.[A182603]', 'Nebivolol is a selective beta-1 adrenergic receptor antagonist that decreases vascular resistance, increases stroke volume and cardiac output, and does not negatively affect left ventricular function.[A182579,A182585] It has a long duration of action as effects can be seen 48 hours after stopping the medication and a wide therapeutic window as patients generally take 5-40mg daily.[A182579,L7985] Patients should not abruptly stop taking this medication as this may lead to exacerbation of coronary artery disease.[L7985] Diabetic patients should monitor their blood glucose levels as beta blockers may mask signs of hypoglycemia.[L7985]', 'DrugBank');
