INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tormeel 10 mg 10 f.c. tab.', 28356, 'تورميل 10مجم 10 اقراص', '28', NULL, 'Urinary incontinence.antispasmodics.muscarinic antagonists', 'Urinary incontinence.antispasmodics.muscarinic antagonists', NULL, 'Solifenacin succinate', 'European egyptian pharm. ind.', 'Tab', 'أقراص', '10 mg', '1', NULL, NULL, 'indication for the treatment of overactive bladder with symptoms of urge urinary incontinence urgency and urinary frequency. mechanism of action solifenacin is a competitive muscarinic acetylcholine receptor antagonist. the binding of acetylcholine to the', NULL, 1356, '2022-06-22', NULL, 0, 'Solifenacin tablets are indicated to treat an overactive bladder with urinary incontinence, urgency, and frequency.[L7511]', 'Solifenacin is a competitive muscarinic receptor antagonist.[L7511] It has the highest affinity for M3, M1, and M2 muscarinic receptors.[A181247] 80% of the muscarinic receptors in the bladder are M2, while 20% are M3.[A181262] Solifenacin''s antagonism of the M3 receptor prevents contraction of the detrusor muscle, while antagonism of the M2 receptor may prevent contraction of smooth muscle in the bladder.[A181262]', 'Solifenacin antagonizes the M2 and M3 muscarinic receptors in the bladder to treat an overactive bladder.[L7511,A181262] It has a long duration of action as it is usually taken once daily.[L7511] Patients taking solifenacin should be aware of the risks of angioedema and anaphylaxis.[L7511]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rexsol cellulite control body shaping cream 240 ml', 25985, 'ريكسول سيليوليت كريم للتحكم في شكل الجسم 240 مل', '279', NULL, 'Firming topical', 'Firming topical', NULL, NULL, 'Rexsol usa > global health', 'Cream', 'كريم', '240 ml', '1', 'كريم نحت الجسم', NULL, 'an amazing cellulite control body cream helps avoid apparent slackening visible signs of premature skin aging. minimizes skin s spongy appearance. helps refine tighten and firm skin s appearance making the figure younger. for all skin types. apply to thig', NULL, 673, '2022-05-22', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Oxitan 50 mg / 10 ml vial', 24404, 'اوكسيتان 50مجم/10مل فيال', '802', '581', 'Antineoplastic', 'Antineoplastic', NULL, 'Oxaliplatin', 'Fresenius kabi > egyptian group for import and export', 'Vial', 'فيال', '50 mg', '1', NULL, NULL, 'about oxaliplatin alkylating-like; a diaminocyclohexane platinum analog antineoplastic. mechanism of action of oxaliplatin oxaliplatin is a diaminocyclohexane platinum derivative with a broad spectrum of activity. it undergoes rapid non-enzymatic activati', NULL, 675, '2025-06-18', NULL, 0, 'Oxaliplatin, in combination with infusional fluorouracil and leucovorin, is indicated for the treatment of advanced colorectal cancer and adjuvant treatment of stage III colon cancer in patients who have undergone complete resection of the primary tumor.[L47206]', 'Oxaliplatin undergoes nonenzymatic conversion in physiologic solutions to active derivatives via displacement of the labile oxalate ligand. Several transient reactive species are formed, including monoaquo and diaquo DACH platinum, which covalently bind with macromolecules. Both inter and intrastrand Pt-DNA crosslinks are formed. Crosslinks are formed between the N7 positions of two adjacent guanines (GG), adjacent adenine-guanines (AG), and guanines separated by an intervening nucleotide (GNG). These crosslinks inhibit DNA replication and transcription. Cytotoxicity is cell-cycle nonspecific.[L47206]



', 'In vivo studies have shown antitumor activities of oxaliplatin against colon carcinoma. In combination with fluorouracil, oxaliplatin exhibits in vitro and in vivo antiproliferative activity greater than either compound alone in several tumor models (HT29 [colon], GR [mammary], and L1210 [leukemia]).[L47206]', 'DrugBank');
