INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Xeracent emollient cream 75 ml', 29741, 'زيراسنت كريم مرطب 75 مل', '70', NULL, 'Topical emollient', 'Topical emollient', NULL, 'Bee propolis+chamomile+aloe vera+panthenol+zinc oxide+papaya+avocado', 'Cosmopack for cosmetics > spire', 'Cream', 'كريم', '75 ml', '1', 'مرطب للجلد', NULL, 'emollient ointment', NULL, 3650, '2023-02-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Marnys protect 10 oral bottles 10 ml', 22318, 'مارنيز بروتكت 10 زجاجات بالفم 10 مل', '320', '300', 'Multivitamins', 'Multivitamins', NULL, 'Royal jelly+bee propolis+vitamins(b1+b3+b5+b6+b7+b9+b12+c+d+e)', 'Marnys > walfarm pharmaceuticals', 'Bottle', 'زجاجة', '10 ml', '10', 'فيتامينات متعددة', NULL, 'dose: -children 4-6 years: 5 ml / day. -children 7-12 years: 7.5 ml / day. -children above 12 years and adults: 10 ml / day', NULL, 1709, '2025-10-07', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Kerium ds anti-dandruff shampoo 125 ml', 21355, 'كيريوم شامبو دي إس ضد القشرة 125 مل', '225', NULL, 'Hair care', 'Hair care', NULL, NULL, 'La roche-posay > united company for trading & distribution', 'Amp', 'أمبول', '125 ml', '1', NULL, NULL, '*properties micro-exfoliating treatment shampoo. removes persistent dandruff. deeply cleanses and relieves the scalp. abundant or persistent dandruff. intense itching. lhaï- procures fine intense micro-exfoliation to quickly eliminate persistent dandruff.', NULL, 476, '2022-08-05', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nimbex 2mg/ml (10ml=20mg) 5amps.', 23600, 'نيمبكس 2مجم/مل (10مل=20مجم) 5 امبول', '250', NULL, 'Anesthetic.muscle relaxant.non depolarizing', 'Anesthetic.muscle relaxant.non depolarizing', NULL, 'Cisatracurium besylate', 'Glaxo smithkline > phoenix-egypt', 'Amp', 'أمبول', '2mg', '5', NULL, NULL, NULL, NULL, 920, '2022-08-16', NULL, 0, 'Cisatracurium is indicated as an adjunct to general anesthesia to facilitate tracheal intubation in adults and pediatric patients 1 month to 12 years of age. Cisatracurium is also indicated to provide skeletal muscle relaxation during surgery in adults and pediatric patients 2 to 12 years of age as a bolus or infusion maintenance and for mechanical ventilation in the ICU in adults.[L43577]', 'Like other non-depolarising neuromuscular blocking agents, cisatracurium binds competitively to cholinergic receptors in motor end-plate neurons, blocking acetylcholine from accessing the receptors.[L43577] Therefore, in the presence of cisatracurium, an end-plate potential cannot be developed. Ion channels remain closed, the cell does not depolarize, and an action potential is not transmitted.[A243416]', 'The dose required to produce 95% suppression of twitch response to nerve stimulation (ED<sub>95</sub>) of cisatracurium is 0.05 mg/kg in adults receiving opioid/nitrous oxide/oxygen anesthesia.[A253597,L43577] The degree and duration of the neuromuscular block produced by cisatracurium increases in a dose-dependent manner, while the time to maximum neuromuscular block decreases.[A253597] Compared to other neuromuscular blocking agents, it is intermediate in its onset and duration of action.[A253592] Cisatracurium acts on cholinergic receptors, blocking neuromuscular transmission. This action is antagonized by acetylcholinesterase inhibitors such as neostigmine.[A243416]



The use of cisatracurium may lead to residual paralysis, as well as a higher risk of seizure. Medication errors increase the risk of death, and the use of certain drugs may potentiate the neuromuscular blocking action of cisatracurium.[L43577]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Brexin 20mg 10 tab.', 15475, 'بركسين 20 مجم 10 اقراص', '51', '34', 'Nsaid.oxicam', 'Nsaid.oxicam', NULL, 'Piroxicam', 'El nile. > chiesi italy', 'Tab', 'أقراص', '20mg', '1', NULL, NULL, 'about piroxicam nsaid oxicam derivative analgesic antiinflammatory. mechanism of action of piroxicam drug inhibits the biosynthesis and release of prostaglandins by inhibiting cyclooxygenase enzyme in a reversible manner. in this way it reduces the pg (pr', '6221077015115', 2626, '2024-12-06', NULL, 0, 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Plemazol 10mg 20 tab.', 25001, 'بليمازول 10مجم 20 قرص', '3', NULL, 'Antiemetic', 'Antiemetic', NULL, 'Metoclopramide', 'Cid', 'Tab', 'أقراص', '10mg', '2', NULL, NULL, 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', NULL, 929, '2022-07-08', NULL, 0, 'Metoclopramide in the oral tablet form is used for symptomatic treatment of both acute and recurrent diabetic gastroparesis, in addition to the treatment of gastroesophageal reflux disease (GERD) in patients who have failed to respond to traditional therapy.[L8414] A nasal spray formulation is also indicated to treat adults with acute, recurrent diabetic gastroparesis.[L14381]



In the intravenous injection form, it is indicated for the above conditions as well as for the prevention of vomiting that may follow emetogenic chemotherapy or nausea and vomiting after surgery. Intravenous metoclopramide facilitates intubation of the small bowel and stimulates gastric emptying and barium flow in patients who require radiological examination of the stomach or small intestine.  In some cases, the delay of gastrointestinal emptying interferes with the radiographic visualization of the gastrointestinal tract, and metoclopramide is used to facilitate emptying in these cases, allowing for adequate diagnostic visualization.[L8417]



Some off-label uses of metoclopramide include the management of radiation-induced nausea and vomiting, gastric bezoars, intractable hiccups, and migraine pain.[A184961,A184964,A184967,A40105]', 'Metoclopramide causes antiemetic effects by inhibiting dopamine D2 and serotonin 5-HT3 receptors in the chemoreceptor trigger zone (CTZ) located in the area postrema of the brain.[A184880,T683] Administration of this drug leads to prokinetic effects via inhibitory actions on presynaptic and postsynaptic D2 receptors, agonism of serotonin 5-HT4 receptors, and antagonism of muscarinic receptor inhibition. This action enhances the release of acetylcholine, causing increased lower esophageal sphincter (LES) and gastric tone, accelerating gastric emptying and transit through the gut. Metoclopramide antagonizes the dopamine D2 receptors. Dopamine exerts relaxant effect on the gastrointestinal tract through binding to muscular D2 receptors.[A184880,T683,L8414]', 'Metoclopramide increases gastric emptying by decreasing lower esophageal sphincter (LES) pressure. It also exerts effects on the area postrema of the brain, preventing and relieving the symptoms of nausea and vomiting. In addition, this drug increases gastrointestinal motility without increasing biliary, gastric, or pancreatic secretions.[T683,L8414,L8417]



Because of its antidopaminergic activity, metoclopramide can cause symptoms of tardive dyskinesia (TD), dystonia, and akathisia, and should therefore not be administered for longer than 12 weeks.[A184886,L8414]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Queenlept 75 mg 30 caps.', 25532, 'كوينليبت 75مجم 30 كبسول', '93', NULL, 'Anti-epileptic.gaba analogs', 'Anti-epileptic.gaba analogs', NULL, 'Pregabalin', 'Future pharmaceutical industries (fpi) > aspin pharmaceutical', 'Cap', 'كبسولة', '75 mg', '3', NULL, NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', NULL, 820, '2022-07-21', NULL, 0, 'Pregabalin is indicated for the management of neuropathic pain associated with diabetic peripheral neuropathy, postherpetic neuralgia, fibromyalgia, neuropathic pain associated with spinal cord injury, and as adjunctive therapy for the treatment of partial-onset seizures in patients 1 month of age and older.[L7066]', 'Although the mechanism of action has not been fully elucidated, studies involving structurally related drugs suggest that presynaptic binding of pregabalin to voltage-gated calcium channels is key to the antiseizure and antinociceptive effects observed in animal models.[L7066]  



By binding presynaptically to the alpha2-delta subunit of voltage-gated calcium channels in the central nervous system, pregabalin modulates the release of several excitatory neurotransmitters including glutamate, substance-P, norepinephrine, and calcitonin gene related peptide.[A31163] In addition, pregabalin prevents the alpha2-delta subunit from being trafficked from the dorsal root ganglia to the spinal dorsal horn, which may also contribute to the mechanism of action.[A36628] 



Although pregabalin is a structural derivative of the inhibitory neurotransmitter gamma-aminobutyric acid (GABA), it does not bind directly to GABA or benzodiazepine receptors.[A31165]  ', 'Although the structure of pregabalin is similar to gamma-aminobutyric acid (GABA), it does not bind to GABA receptors.[A31163,A187190] Instead, it binds the alpha2-delta subunit of presynaptic voltage-gated calcium channels in the central nervous system.[A31163,A187190] Pregabalin does not modulate dopamine receptors, serotonin receptors, opiate receptors, sodium channels or cyclooxygenase activity.[A187190] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Exosirylic 500 mg 20 f.c.tabs.', 32828, 'ايكسوسيريليك 500 مجم 20 قرص', '194', '160', 'Hepatitis', 'Hepatitis', NULL, 'Ursodeoxycholic acid', 'Gypto pharma', 'Tab', 'أقراص', '500 mg', '2', 'اذابة حصى المرارة', NULL, 'about ursodeoxycholic acid gallstone dissolution agent a bile acid prophylaxis and treatment of gall stones chronic hepatitis liver transplant rejection. mechanism of action of ursodeoxycholic acid ursodeoxycholic acid is a bile acid. normally there is a', '6224008875673', 4947, '2024-10-15', NULL, 0, 'Ursodeoxycholic acid is indicated for the treatment of patients with primary biliary cholangitis.[L44627] 



It is used for the short-term treatment of radiolucent, noncalcified gallbladder stones in patients selected for elective cholecystectomy. It is also used to prevent gallstone formation in obese patients experiencing rapid weight loss.[L44793]', 'Endogenous hydrophobic bile acids such as deoxycholic acid and chenodeoxycholic acid can exert hepatotoxic effects. Ursodeoxycholic acid is a hydrophilic bile acid that mediates its biological effects via several mechanisms. Ursodeoxycholic acid (UDCA) protects hepatocytes and cholangiocytes from bile acid-induced damage, such as reactive oxygen species (ROS)-induced inflammation and mitochondrial dysfunction.[A256267] UDCA was shown to reserve hepatocyte cell structures and stimulate anti-apoptotic pathways.[A256267,A256272,A256458,A256463] It was also shown to prevent the production of ROS by Kupffer cells and resident macrophages in the liver, thus attenuating oxidative stress in the liver.[A256267] 



UDCA can also change the hydrophobicity index of the bile acid pool: following oral administration, UDCA forms a major fraction of the human bile acid pool and competitively displaces the hydrophobic or more toxic bile acids.[A256272,A256267] It increases the absorption of hydrophilic bile acids.[A256267] There are several proposed mechanisms of choleretic actions of UDCA: UDCA increases intracellular calcium levels, stimulating transport proteins and vesicular exocytosis in cholestatic hepatocytes.[A256267] UDCA may also upregulate the expression of membrane transport proteins like the chloride-bicarbonate anion exchanger (AE2),[A256267,A256458] which is involved in biliary secretion and is often observed to be defective in primary biliary cholangitis.[A256448] In rats, UDCA reduced hepatic expression of major histocompatibility complex (MHC) class I antigens, suggesting immunomodulating effects.[A256267] UDCA acts as a partial agonist at the bile acid receptor, also known as the farnesoid X receptor (FXR), and has negligible effects on cholesterol and lipid synthesis.[A256272]', 'Ursodeoxycholic acid (UDCA) is a secondary bile acid with cytoprotectant, immunomodulating, and choleretic effects. It reduces the cholesterol fraction of biliary lipids.[A256267]



UDCA inhibits the absorption of cholesterol in the intestine and the secretion of cholesterol into bile, decreasing biliary cholesterol saturation.[A256267] UDCA increases bile acid flow and promotes the secretion of bile acids.[A256267,A256277,A256463]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mercava 30 sachets', 1686, 'ميركافا 30 كيس', '450', NULL, 'Nootropics - cerebral circulatory enhancer', 'Nootropics - cerebral circulatory enhancer', NULL, 'Aniracetam+l-carnitine+vitamin e', 'Biobots', 'Sachet', 'أكياس', NULL, '1', 'مطهر ومضاد للبكتيريا والفطريات', NULL, NULL, NULL, 1706, '2023-05-01', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Noctorelief 10mg 10 tab.', 23670, 'نوكتوريليف 10مجم 10 اقراص', '10', NULL, 'Bronchodilator.long acting beta 2 agonist', 'Bronchodilator.long acting beta 2 agonist', NULL, 'Bambuterol', 'Pharopharma', 'Tab', 'أقراص', '10mg', '1', NULL, NULL, 'about bambuterol long acting beta 2-adrenoceptor agonist anti asthmatic-bronchodilators. mechanism of action of bambuterol this prodrug of the 2-agonist terbutaline undergoes oxidation and hydrolysis in the body and converted into terbutaline. this terbut', NULL, 746, '2022-07-22', NULL, 0, 'For the prevention and reversal of bronchospasm in patients 12 years of age and older with asthma and reversible bronchospasm associated with bronchitis and emphysema.', 'The pharmacologic effects of bambuterol are at least in part attributable to stimulation through beta-adrenergic receptors (beta 2 receptors) of intracellular adenyl cyclase, the enzyme that catalyzes the conversion of adenosine triphosphate (ATP) to cyclic AMP. Increased cyclic AMP levels are associated with relaxation of bronchial smooth muscle and inhibition of release of mediators of immediate hypersensitivity from cells, especially from mast cells.', 'Bambuterol is a long acting beta2-adrenoceptor agonist used in the treatment of asthma. It is a prodrug of terbutaline. Bambuterol causes smooth muscle relaxation, resulting in dilation of bronchial passages.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Fitamart feet cream 50 gm', 32852, 'فيتامارت فيت كريم 50 جم', '90', '70', 'Skin care', 'Skin care', NULL, 'Urea 10%+panthenol+glycerin+allantoin+glutathione+vitamin e+coconut oil+sunflower seed oil+prunus kernel oil', 'Egyptian co. for cosmetics > pharma mart', 'Cream', 'كريم', '50 gm', '1', NULL, NULL, 'offers full protection for diabetic and dry feet. -rapid onset of action & long lasting -optimum hydration of skin -rapidly absorbed without greasiness -improves the general condition of feet -protects the skin from external factors without harming its na', '745114711991', 989, '2025-06-19', NULL, 0, 'Urea is used topically for debridement and promotion of normal healing of hyperkeratotic surface lesions, particularly where healing is retarded by local infection, necrotic tissue, fibrinous or purulent debris or eschar. Urea is useful for the treatment of hyperkeratotic conditions such as dry, rough skin, dermatitis, psoriasis, xerosis, ichthyosis, eczema, keratosis, keratoderma, corns and calluses, as well as damaged, devitalized and ingrown nails.[L41484]', NULL, 'Urea is a keratolytic emollient that works to treat or prevent dry, rough, scaly, itchy skin.[L41484]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sudkids cordoba 240 gm cream', 12049, 'سودكيدز قرطبة 240 جم كريم', '230', '210', 'Soothing cream for diaper area', 'Soothing cream for diaper area', NULL, 'Zinc oxide + cetearyl alcohol + paraffinum liquid + propylene glycol + dimethicon + lanolin + triethanolamine + panthenol + phenoxyethanol + sodium benzoate', 'Cordoba pharma', 'Cream', 'كريم', '240 gm', '1', NULL, NULL, NULL, NULL, 786, '2023-11-12', NULL, 0, 'Zinc can be used for the treatment and prevention of zinc deficiency/its consequences, including stunted growth and acute diarrhea in children, and slowed wound healing. It is also utilized for boosting the immune system, treating the common cold and recurrent ear infections, as well as preventing lower respiratory tract infections [L2172].  



', '**Zinc has three primary biological roles**: _catalytic_, _structural_, and _regulatory_. The catalytic and structural role of zinc is well established, and there are various noteworthy reviews on these functions. For example, zinc is a structural constituent in numerous proteins, inclusive of growth factors, cytokines, receptors, enzymes, and transcription factors for different cellular signaling pathways. It is implicated in numerous cellular processes as a cofactor for approximately 3000 human proteins including enzymes, nuclear factors, and hormones [L2096].



Zinc promotes resistance to epithelial apoptosis through cell protection (cytoprotection) against reactive oxygen species and bacterial toxins, likely through the antioxidant activity of the cysteine-rich metallothioneins [A32419].



In HL-60 cells (promyelocytic leukemia cell line), zinc enhances the up-regulation of A20 mRNA, which, via TRAF pathway, decreases NF-kappaB activation, leading to decreased gene expression and generation of tumor necrosis factor-alpha (TNF-alpha), IL-1beta, and IL-8 [A32418].



There are several mechanisms of action of zinc on acute diarrhea. Various mechanisms are specific to the gastrointestinal system: zinc restores mucosal barrier integrity and enterocyte brush-border enzyme activity, it promotes the production of antibodies and circulating lymphocytes against intestinal pathogens, and has a direct effect on ion channels, acting as a potassium channel blocker of adenosine 3-5-cyclic monophosphate-mediated chlorine secretion. Cochrane researchers examined the evidence available up to 30 September 2016 [L2106].



Zinc deficiency in humans decreases the activity of serum _thymulin_ (a hormone of the thymus), which is necessary for the maturation of T-helper cells. T-helper 1 (Th(1)) cytokines are decreased but T-helper 2 (Th(2)) cytokines are not affected by zinc deficiency in humans [A32417].



The change of _Th(1)_ to _Th(2)_ function leads to cell-mediated immune dysfunction. Because IL-2 production (Th(1) cytokine) is decreased, this causes decreased activity of natural-killer-cell (NK cell) and T cytolytic cells, normally involved in killing viruses, bacteria, and malignant cells [A32424]. 



In humans, zinc deficiency may lead to the generation of new CD4+ T cells, produced in the thymus. In cell culture studies (HUT-78, a Th(0) human malignant lymphoblastoid cell line), as a result of zinc deficiency, nuclear factor-kappaB (NF-kappaB) activation, phosphorylation of IkappaB, and binding of NF-kappaB to DNA are decreased and this results in decreased Th(1) cytokine production [A32417].



In another study, zinc supplementation in human subjects suppressed the gene expression and production of pro-inflammatory cytokines and decreased oxidative stress markers [A32424]. In HL-60 cells (a human pro-myelocytic leukemia cell line), zinc deficiency increased the levels of TNF-alpha, IL-1beta, and IL-8 cytokines and mRNA. In such cells, zinc was found to induce A20, a zinc finger protein that inhibited NF-kappaB activation by the tumor necrosis factor receptor-associated factor pathway. This process decreased gene expression of pro-inflammatory cytokines and oxidative stress markers [A32417].



The exact mechanism of zinc in acne treatment is poorly understood. However, zinc is considered to act directly on microbial inflammatory equilibrium and facilitate antibiotic absorption when used in combination with other agents. Topical zinc alone as well as in combination with other agents may be efficacious because of its anti-inflammatory activity and ability to reduce P. acnes bacteria by the inhibition of P. acnes lipases and free fatty acid levels [L2102].', 'Zinc is involved in various aspects of cellular metabolism. It has been estimated that approximately 10% of human proteins may bind zinc, in addition to hundreds of proteins that transport and traffic zinc. It is required for the catalytic activity of more than 200 enzymes, and it plays a role in immune function wound healing, protein synthesis, DNA synthesis, and cell division. Zinc is an essential element for a proper sense of taste and smell and supports normal growth and development during pregnancy, childhood, and adolescence. It is thought to have antioxidant properties, which may be protective against accelerated aging and helps to speed up the healing process after an injury; however, studies differ as to its effectiveness. Zinc ions are effective antimicrobial agents even if administered in low concentrations [L2211].



Studies on oral zinc for specific conditions shows the following evidence in various conditions [L2095]:



**Colds:** Evidence suggests that if zinc lozenges or syrup are taken within 24 hours after cold symptoms start, the supplement may shorten the length of colds. The use intranasal zinc has been associated with the loss of the sense of smell, in some cases long-term or permanently [L2095].



**Wound healing:** Patients with skin ulcers and decreased levels of zinc may benefit from oral zinc supplements [L2095]. 



**Diahrrea**: Oral zinc supplements can reduce the symptoms of diarrhea in children with low levels of zinc, especially in cases of malnutrition [L2095].

', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Magvital-b6 20 tabs.', 22233, 'ماجفيتال ب6 20اقراص', '11', NULL, NULL, NULL, NULL, 'Magnesium+pyridoxine', 'Eva pharma', 'Tab', 'أقراص', NULL, '2', 'فيتامينات متعددة -- ب6 -- مغنسيوم', NULL, NULL, NULL, 2343, '2022-06-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dentso mouth spray 60 ml', 32022, 'دينتسو سبراي للفم 60 مل', '40', NULL, 'Oral care', 'Oral care', NULL, 'Menthol+thyme oil+camphor+sorbitol+eucalyptol+peppermint oil+cinnamon oil+clove oil+sodium saccharin', 'Egyptian co . for cosmetic (ecc) > vida international pharma', 'Spray', 'بخاخ', '60 ml', '1', 'بخاخ مطهر ومعطر للفم يُساعد علي محاربة الجراثيم المسببة لرائحة الفم', NULL, 'mouth refreshner', NULL, 1165, '2023-03-09', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Advagraf 0.5 mg 50 prolonged r. caps. (n/a yet)', 13515, 'ادفاجراف 0.5 مجم 50 كبسولة', '720', NULL, 'Immunosuppressants', 'Immunosuppressants', NULL, 'Tacrolimus', 'Astellas > egyptian pharmaceutical trading company', 'Cap', 'كبسولة', '0.5 mg', '1', NULL, NULL, NULL, NULL, 795, '2022-07-20', NULL, 0, 'Immediate-release formulations of tacrolimus are indicated for the prophylaxis of organ rejection in adult and pediatric patients receiving allogeneic liver, kidney, heart, or lung transplants, in combination with other immunosuppressants.[L8162] Extended-release formulations of tacrolimus are indicated for the prophylaxis of organ rejection in adult and pediatric patients receiving kidney transplants, in combination with other immunosuppressants,[L20034,L41005] and may be used in patients converted from immediate-release formulations.[L41005]



Topical tacrolimus ointment is indicated as second-line therapy for short-term and non-continuous treatment of moderate-to-severe atopic dermatitis in non-immunocompromised adults and children who have failed to respond adequately to other topical treatments or for whom alternative treatments are not advisable.[L3935] Both available strengths are indicated in adult patients, while only the lower strength (0.03%) formulation is indicated in pediatric patients between 2 and 15 years of age.[L3935]', 'The mechanism of action of tacrolimus in atopic dermatitis is not known. While the following have been observed, the clinical significance of these observations in atopic dermatitis is not known. It has been demonstrated that tacrolimus inhibits T-lymphocyte activation by first binding to an intracellular protein, FKBP-12. A complex of tacrolimus-FKBP-12, calcium, calmodulin, and calcineurin is then formed and the phosphatase activity of calcineurin is inhibited. This prevents the dephosphorylation and translocation of nuclear factor of activated T-cells (NF-AT), a nuclear component thought to initiate gene transcription for the formation of lymphokines. Tacrolimus also inhibits the transcription for genes which encode IL-3, IL-4, IL-5, GM-CSF, and TNF-, all of which are involved in the early stages of T-cell activation. Additionally, tacrolimus has been shown to inhibit the release of pre-formed mediators from skin mast cells and basophils, and to downregulate the expression of FceRI on Langerhans cells.', 'Tacrolimus acts by reducing peptidyl-prolyl isomerase activity by binding to the immunophilin FKBP-12 (FK506 binding protein) creating a new complex. This inhibits both T-lymphocyte signal transduction and IL-2 transcription. Tacrolimus has similar activity to cyclosporine but rates of rejection are lower with tacrolimus. Tacrolimus has also been shown to be effective in the topical treatment of eczema, particularly atopic eczema. It suppresses inflammation in a similar way to steroids, but is not as powerful. An important dermatological advantage of tacrolimus is that it can be used directly on the face; topical steroids cannot be used on the face, as they thin the skin dramatically there. On other parts of the body, topical steroid are generally a better treatment.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Hair-clinic hair mask 150 gm', 20208, 'هير كلينك ماسك للشعر 150 جم', '110', NULL, 'Hair care', 'Hair care', NULL, NULL, 'Borg', 'Unknown', 'غير محدد', '150 gm', '1', NULL, NULL, 'hair -clinic hair mask ideal hair mask for damaged & brittle hair *unique formula needed for hair rebuilding and promote hair growth. *repairs damaged and brittle hair. *excellent moisturizing agent for hair or scalp dryness. *restores healthy natural loo', NULL, 808, '2022-08-01', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mycophenolate mofetil-sandoz 500 mg 50 tabs.', 23156, 'ميكوفينوليت موفتيل-ساندوز 500مجم 50 قرص', '1,020', '550', 'Immunosuppressant', 'Immunosuppressant', NULL, 'Mycophenolate mofetil', 'Sandoz', 'Tab', 'أقراص', '500 mg', '5', NULL, NULL, 'indication for the prophylaxis of organ rejection in patients receiving allogeneic renal cardiac or hepatic transplants. mycophenolate mofetil should be used concomitantly with cyclosporine and corticosteroids. mechanism of action mycophenolate mofetil is', '6221035003062', 3628, '2025-05-03', NULL, 0, 'Mycophenolate mofetil is indicated in combination with other immunosuppressants to prevent the rejection of kidney, heart, or liver transplants in adult and pediatric patients ≥3 months old.[L42020] Mycophenolate mofetil may also be used off-label as a second-line treatment for autoimmune hepatitis that has not responded adequately to first-line therapy.[A180814] Other off-label uses of this drug include lupus-associated nephritis and dermatitis in children.[A180817]', 'The active metabolite of mycophenolate, mycophenolic acid, prevents T-cell and B-cell proliferation and the production of cytotoxic T-cells and antibodies. Lymphocyte and monocyte adhesion to endothelial cells of blood vessels that normally part of inflammation is prevented via the glycosylation of cell adhesion molecules by MPA.[A180892] MPA inhibits de novo purine biosynthesis (that promotes immune cell proliferation) by inhibiting inosine 5’-monophosphate dehydrogenase enzyme (IMPDH), with a preferential inhibition of IMPDH II.[A180826] IMPDH normally transforms inosine monophosphate (IMP) to xanthine monophosphate (XMP), a metabolite contributing to the production of guanosine triphosphate (GTP).[A180799,A180805,A180817] GTP is an important molecule for the synthesis of ribonucleic acid (RNA), deoxyribonucleic acid (DNA), and protein. As a result of the above cascade of effects, mycophenolate mofetil reduces de-novo production of guanosine nucleotides, interfering with the synthesis of DNA, RNA, and protein required for immune cell production.[A180826] Further contributing to the above anti-inflammatory effects, MMF depletes tetrahydrobiopterin, causing the decreased function of inducible nitric oxide synthase enzyme, in turn decreasing the production of peroxynitrite, a molecule that promotes inflammation.[A180907]', 'Mycophenolate mofetil is a prodrug of mycophenolic acid (MPA). The active form of mycophenolate, MPA, prevents the proliferation of immune cells and the formation of antibodies that cause transplant rejection.[A180805] The above effects lead to higher rates of successful transplantation, avoiding the devastating effects of graft rejection.



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Prostride 5mg 30 caps.', 25403, 'بروستريد 5مجم 30 كبسولة', '183', '136.5', '5 alpha-reductase inhibitor', '5 alpha-reductase inhibitor', NULL, 'Finasteride', 'Adwia', 'Cap', 'كبسولة', '5mg', '3', 'علاج اورام البروستاتا', NULL, 'about finasteride 5 alpha-reductase inhibitor synthetic anti-androgen. mechanism of action of finasteride this synthetic 4-azasteroid derivative competitively inhibits steroid 5alpha reductase which is an enzyme responsible for active androgen dehydrotest', '6223002971343', 7560, '2024-09-05', NULL, 0, 'Finasteride is indicated for the treatment of symptomatic benign prostatic hyperplasia (BPH) in men with an enlarged prostate to improve symptoms, reduce the risk of acute urinary retention, and reduce the risk of the need for surgery including transurethral resection of the prostate (TURP) and prostatectomy.[L10565] A combination product with [tadalafil] is also used for the symptomatic treatment of BPH for up to 26 weeks.[L39439]



Finasteride is also indicated for the treatment of male pattern hair loss (androgenetic alopecia, hereditary alopecia, or common male baldness) in male patients.[L6235]', 'Finasteride acts as a competitive and specific inhibitor of Type II 5α-reductase, a nuclear-bound steroid intracellular enzyme primarily located in the prostatic stromal cell that converts the androgen testosterone into the more active metabolite, 5α-dihydrotestosterone (DHT).[A2132] DHT is considered to be the primary androgen playing a role in the development and enlargement of the prostate gland. It serves as the hormonal mediator for the hyperplasia upon accumulation within the prostate gland.[A178270] DHT displays a higher affinity towards androgen receptors in the prostate gland compared to testosterone[T28] and by acting on the androgen receptors, DHT modulates genes that are responsible for cell proliferation.[A178279] Responsible for the production of DHT together with type I 5α-reductase, the type II 5α-reductase isozyme is primarily found in the prostate, seminal vesicles, epididymides, and hair follicles as well as liver.[L6235] Although finasteride is 100-fold more selective for type II 5α-reductase than for the type I isoenzyme,[A178183] chronic treatment with this drug may have some effect on type I 5α-reductase, which is predominantly expressed in sebaceous glands of most regions of skin, including the scalp, and liver. It is proposed that the type I 5α-reductase and type II 5α-reductase is responsible for the production of one-third and two-thirds of circulating DHT, respectively. 



The mechanism of action of Finasteride is based on its preferential inhibition of Type II 5α-reductase through the formation of a stable complex with the enzyme _in vitro_ and _in vivo_.[L10565] Finasteride works selectively, where it preferentially displays a 100-fold selectivity for the human Type II 5α-reductase over type I enzyme.[L6235] Inhibition of Type II 5α-reductase blocks the peripheral conversion of testosterone to DHT, resulting in significant decreases in serum and tissue DHT concentrations, minimal to moderate increase in serum testosterone concentrations, and substantial increases in prostatic testosterone concentrations. As DHT appears to be the principal androgen responsible for stimulation of prostatic growth, a decrease in DHT concentrations will result in a decrease in prostatic volume (approximately 20-30% after 6-24 months of continued therapy). It is suggested that increased levels of DHT can lead to potentiated transcription of prostaglandin D2, which promotes the proliferation of prostate cancer cells.[A178189] In men with androgenic alopecia, the mechanism of action has not been fully determined, but finasteride has shown to decrease scalp DHT concentration to the levels found in the hairy scalp, reduce serum DHT, increase hair regrowth, and slow hair loss. Another study suggests that finasteride may work to reduce bleeding of prostatic origin by inhibiting vascular endothelial growth factor (VEGF) in the prostate, leading to atrophy and programmed cell death.[A178183] This may bestow the drug therapeutic benefits in patients idiopathic prostatic bleeding, bleeding during anticoagulation, or bleeding after instrumentation.[A178183]', 'Finasteride is an antiandrogenic compound that works by suppressing the production of serum and intraprostatic dihydrotestosterone (DHT) in men via inhibiting the enzyme responsible for the biosynthesis of DHT. The maximum effect of a rapid reduction in serum DHT concentration is expected to be observed 8 hours following administration of the first dose.[L10565] In a single man receiving a single oral dose of 5 mg finasteride for up to 4 years, there was a reduction in the serum DHT concentrations by approximately 70% and the median circulating level of testosterone increased by approximately 10-20% within the physiologic range. [L10565] In a double-blind, placebo-controlled study, finasteride reduced intraprostatic DHT level by 91.4% but finasteride is not expected to decrease the DHT levels to castrate levels since circulating testosterone is also converted to DHT by the type 1 isoenzyme expressed in other tissues.[A2132] It is expected that DHT levels return to normal within 14 days upon discontinuation of the drug.[L6244] In a study of male patients with benign prostatic hyperplasia prior to prostatectomy, the treatment with finasteride resulted in an approximate 80% lower DHT content was measured in prostatic tissue removed at surgery compared to placebo.[L10565] While finasteride reduces the size of the prostate gland by 20%, this may not correlate well with improvement in symptoms.[A178273] The effects of finasteride are reported to be more pronounced in male patients with enlarged prostates (>25 mL) who are at the greatest risk of disease progression.[A2132]



In phase III clinical studies, oral administration of finasteride in male patients with male pattern hair loss promoted hair growth and prevented further hair loss by 66% and 83% of the subjects, respectively, which lasted during two years'' treatment.[A178222] The incidences of these effects in treatment groups were significantly higher than that of the group receiving a placebo.[A178222] Following finasteride administration, the levels of DHT in the scalp skin was shown to be reduced by more than 60%, indicating that the DHT found in scalp is derived from both local DHT production and circulating DHT.[A178195] The effect of finasteride on scalp DHT is likely seen because of its effect on both local follicular DHT levels as well as serum DHT levels.[A178195]. There is evidence from early clinical observations and controlled studies that finasteride may reduce bleeding of prostatic origin.[A178183]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Svr sebiaclear gel moussant 200 ml', 27754, 'اس في ار سيبياكلير جل 200مل', '199', NULL, 'Skin care', 'Skin care', NULL, NULL, 'Svr laboratoire dermatologique > biotech egypt', 'Gel', 'جل', '200 ml', '1', NULL, NULL, 'purifying and exfoliating soap-free cleanser sebiaclear gel moussant is a soap-free cleanser that purifies and unclogs the skin gently. it eliminates impurities and excess sebum without drying out the skin. use apply to wet skin lather and rinse.', NULL, 643, '2022-08-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cranberry 30 capsules', 3356, 'كرانبيري 30 كبسولة', '55', NULL, 'Uti infections', 'Uti infections', NULL, 'Cranberry extract', 'Future pharmaceutical industries (fpi) > ema pharm', 'Capsule', 'كبسولة', NULL, '1', NULL, NULL, NULL, NULL, 1063, '2022-07-15', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Starlidine 60mg 10 f.c.tab.', 27466, 'ستارليدين 60مجم 10 اقراص', '10', NULL, 'Anti-diabetic.secretagogues.glinides', 'Anti-diabetic.secretagogues.glinides', NULL, 'Nateglinide', 'International drug agency (idi)', 'Tab', 'أقراص', '60mg', '1', NULL, NULL, 'about nateglinide meglitinide derivative oral antidiabetic agent. mechanism of action of nateglinide it is a quick and short acting antidiabetic drug which exerts it`s action by stimulating insulin release from ?-cells of pancreas. it acts by binding to s', NULL, 965, '2022-07-15', NULL, 0, 'For the treatment of non-insulin dependent-diabetes mellitus in conjunction with diet and exercise.', 'Nateglinide activity is dependent on the presence functioning β cells and glucose. In contrast to sulfonylurea insulin secretatogogues, nateglinide has no effect on insulin release in the absence of glucose. Rather, it potentiates the effect of extracellular glucose on ATP-sensitive potassium channel and has little effect on insulin levels between meals and overnight. As such, nateglinide is more effective at reducing postprandial blood glucose levels than fasting blood glucose levels and requires a longer duration of therapy (approximately one month) before decreases in fasting blood glucose are observed. The insulinotropic effects of nateglinide are highest at intermediate glucose levels (3 to 10 mmol/L) and it does not increase insulin release already stimulated by high glucose concentrations (greater than 15 mmol/L). Nateglinide appears to be selective for pancreatic β cells and does not appear to affect skeletal or cardiac muscle or thyroid tissue.', 'Insulin secretion by pancreatic β cells is partly controlled by cellular membrane potential. Membrane potential is regulated through an inverse relationship between the activity of cell membrane ATP-sensitive potassium channels (ABCC8) and extracellular glucose concentrations. Extracellular glucose enters the cell via GLUT2 (SLC2A2) transporters. Once inside the cell, glucose is metabolized to produce ATP. High concentrations of ATP inhibit ATP-sensitive potassium channels causing membrane depolarization. When extracellular glucose concentrations are low, ATP-sensitive potassium channels open causing membrane repolarization. High glucose concentrations cause ATP-sensitive potassium channels to close resulting in membrane depolarization and opening of L-type calcium channels. The influx of calcium ions stimulates calcium-dependent exocytosis of insulin granules. Nateglinide increases insulin release by inhibiting ATP-sensitive potassium channels in a glucose-dependent manner. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Maestro cream 100 gm', 22185, 'مايسترو كريم 100 جم', '80', '65', 'Massage', 'Massage', NULL, 'Methyl salicylate+eucalyptus oil+menthol+camphor+capsicum oil+peppermint oil', 'Plant pharma', 'Cream', 'كريم', '100 gm', '1', NULL, NULL, 'massage cream', NULL, 1709, '2025-11-27', NULL, 0, 'For topical use, in combination with 570 to 670 nm wavelength red light illumination, in the treatment of non-hyperkeratotic actinic keratoses of the face and scalp in immunocompetent patients when used in conjunction with lesion preparation (debridement using a sharp dermal curette).', 'Photosensitization following application of methyl aminolevulinate cream occurs through the metabolic conversion of methyl aminolevulinate (prodrug) to photoactive porphyrins (PAP), which accumulates in the skin lesions to which the cream has been applied. When exposed to light of appropriate wavelength and energy, the accumulated photoactive porphyrins produce a photodynamic reaction, resulting in a cytotoxic process dependent upon the simultaneous presence of oxygen. The absorption of light results in an excited state of porphyrin molecules, and subsequent spin transfer from photoactive porphyrins to molecular oxygen generates singlet oxygen, which can further react to form superoxide and hydroxyl radicals.', 'After topical application of methyl aminolevulinate, porphyrins will accumulate intracellularly in the treated skin lesions. The intracellular porphyrins (including PpIX) are photoactive, fluorescing compounds and, upon light activation in the presence of oxygen, singlet oxygen is formed which causes damage to cellular compartments, in particular the mitochondria. Light activation of accumulated porphyrins leads to a photochemical reaction and thereby phototoxicity to the light-exposed target cells.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lingmin 20 tabs', 4863, 'لينجمين 20 قرص', '160', NULL, 'Multivitamin', 'Multivitamin', NULL, 'Vitamin b6+vitamin b12+folate 800', 'Balance pharma', 'Tab', 'أقراص', NULL, '1', NULL, NULL, NULL, NULL, 1121, '2025-06-21', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Polar frost cold gel 150 ml', 25026, 'بولار فروست كولد جل 150 مل', '125', NULL, 'Analgesic', 'Analgesic', NULL, 'Menthol', 'Niva medical > al kamal importing & marketing co', 'Gel', 'جل', '150 ml', '1', 'مسكن.', NULL, 'polar frost cold gel can be used by muscular disorders and injuries like: strains sprains and ruptures stressed muscles and tension chronic neck- shoulder and low back pains and aches work and occupation related muscular disorders cramps and spasms bruise', NULL, 2403, '2022-05-21', NULL, 0, NULL, NULL, NULL, NULL);
