INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Magiclastine 10 mg 20 f.c. tabs.', 22199, 'ماجيكلاستين 10مجم 20اقراص', '28', NULL, 'Antihistamine.second-generation', 'Antihistamine.second-generation', NULL, 'Mizolastin', 'Medizen pharmaceutical industries > magic pharma-egypt', 'Tab', 'أقراص', '10 mg', '2', 'مضاد للحساسية يستخدم في حالات سيلان الأنف أو انسداده.العطس والحكة، والعيون الدامعة ، احتقان الأنف والتهاب الجيوب الأنفية', NULL, 'mizolastine (mizollen) is a once daily non-sedating antihistamine. it blocks h1 receptors and is commonly fast-acting. it does not prevent the actual release of histamine from mast cells just prevents it binding to receptors. side effects can include dry', NULL, 822, '2022-07-09', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Go lite sweetener 1 gm*25 sachets', 19988, 'جو لايت سكر دايت 1 جم * 25 كيس', '18', NULL, 'Sweetener', 'Sweetener', NULL, 'Sucralose+maltodextrin+dextrose', 'Marcyrl co. > pharma chem', 'Sachet', 'أكياس', '1 gm', '1', NULL, NULL, NULL, NULL, 720, '2022-07-25', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Gentamicin 40mg/ml i.v./i.m. 6 amps.', 5434, 'جنتاميسين 40مجم/مل 6 امبولات', '7', NULL, 'Aminoglycoside antibiotic', 'Aminoglycoside antibiotic', NULL, 'Gentamicin', 'Memphis', 'Amp', 'أمبول', '40mg', '6', NULL, NULL, 'about gentamicin aminoglycoside antibiotic. mechanism of action of gentamicin gentamicin exerts its bactericidal action against gram negative organisms & some of gram positive organisms by inhibiting bacterial protein synthesis. the process involves 1. pe', '6221172002058', 1159, '2023-05-02', NULL, 0, NULL, 'There are 3 key phases of aminoglycoside entry into cells.[A232294] The first “ionic binding phase” occurs when polycationic aminoglycosides bind electrostatically to negatively charged components of bacterial cell membranes including with lipopolysaccharides and phospholipids within the outer membrane of Gram-negative bacteria and to teichoic acids and phospholipids within the cell membrane of Gram-positive bacteria. This binding results in displacement of divalent cations and increased membrane permeability, allowing for aminoglycoside entry.[A232294, A232304, A232309, A232314]

The second “energy-dependent phase I” of aminoglycoside entry into the cytoplasm relies on the proton-motive force and allows a limited amount of aminoglycoside access to its primary intracellular target - the bacterial 30S ribosome.[A232294, A232314] This ultimately results in the mistranslation of proteins and disruption of the cytoplasmic membrane.[A233320] Finally, in the “energy-dependent phase II” stage, concentration-dependent bacterial killing is observed. Aminoglycoside rapidly accumulates in the cell due to the damaged cytoplasmic membrane, and protein mistranslation and synthesis inhibition is amplified.[A232294, A232314, A232319] The necessity of oxygen-dependent active transport explains why aminoglycosides are ineffective against anaerobic bacteria.[A234130]

Hence, aminoglycosides have both immediate bactericidal effects through membrane disruption and delayed bactericidal effects through impaired protein synthesis; observed experimental data and mathematical modeling support this two-mechanism model.[A232294, A232299]

Inhibition of protein synthesis is a key component of aminoglycoside efficacy. Structural and cell biological studies suggest that aminoglycosides bind to the 16S rRNA in helix 44 (h44), near the A site of the 30S ribosomal subunit, altering interactions between h44 and h45. This binding also displaces two important residues, A1492 and A1493, from h44, mimicking normal conformational changes that occur with successful codon-anticodon pairing in the A site.[A232324, A232329] Overall, aminoglycoside binding has several negative effects including inhibition of translation, initiation, elongation, and ribosome recycling.[A232294, A232334, A232339] Recent evidence suggests that the latter effect is due to a cryptic second binding site situated in h69 of the 23S rRNA of the 50S ribosomal subunit.[A232329, A232339] Also, by stabilizing a conformation that mimics correct codon-anticodon pairing, aminoglycosides promote error-prone translation.[A232344] Mistranslated proteins can incorporate into the cell membrane, inducing the damage discussed above.[A232294, A232319]', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cefrone 2 gm i.m/i.v vial', 16060, 'سيفرون 2 جم فيال', '48', '32', 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Cefoperazone', 'Mup', 'Vial', 'فيال', '2 gm', '1', NULL, NULL, 'about cefoperazone sodium a third generation cephalosporin antibiotic parenteral antibiotic. mechanism of action of cefoperazone sodium cefoperazone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram n', '6221508161718', 1017, '2024-12-13', NULL, 0, 'Indicated for the treatment of following infections caused by susceptible bacteria:[label]



1) Respiratory tract infections caused by _S. pneumoniae_, _H. influenzae_, _S. aureus_ (penicillinase and non-penicillinase producing strains), _S. pyogenes_ (Group A beta-hemolytic streptococci), _P. aeruginosa_, _Klebsiella pneumoniae_, _E. coli_, _Proteus mirabilis_, and Enterobacter species.



2) Peritonitis and other intra-abdominal infections caused by _E. coli_, _P. aeruginosa_, and anaerobic gram-negative bacilli (including _Bacteroides fragilis_).



3) Bacterial septicemia caused by _S. pneumoniae_, _S. agalactiae_, _S. aureus_, _Pseudomonas aeruginosa_, _E. coli_, _Klebsiella_ spp., _Klebsiella pneumoniae_, Proteus species (indole-positive and indole-negative), _Clostridium_ spp. and anaerobic gram-positive cocci.



4) Infections of the skin and skin structures caused by S. aureus (penicillinase and non-penicillinase producing strains), S. pyogenes, and P. aeruginosa.



5) Pelvic Inflammatory Disease, Endometritis, and Other Infections of the Female Genital Tract caused by N. gonorrhoeae, S. epidermidis, S. agalactiae, E. coli, Clostridium spp., Bacteroides species (including Bacteroides fragilis), and anaerobic gram-positive cocci.



6) Urinary tract infections caused by Escherichia coli and Pseudomonas aeruginosa.



7) Enterococcal Infections. Although cefoperazone has been shown to be clinically effective in the treatment of infections caused by enterococci in cases of peritonitis and other intra-abdominal infections, infections of the skin and skin structures, pelvic inflammatory disease, endometritis and other infections of the female genital tract, and urinary tract infections, the majority of clinical isolates of enterococci tested are not susceptible to cefoperazone but fall just at or in the intermediate zone of susceptibility, and are moderately resistant to cefoperazone. However, _in vitro_ susceptibility testing may not correlate directly with _in vivo_ results. Despite this, cefoperazone therapy has resulted in clinical cures of enterococcal infections, chiefly in polymicrobial infections. Cefoperazone should be used in enterococcal infections with care and at doses that achieve satisfactory serum levels of cefoperazone.', 'Like all beta-lactam antibiotics, cefoperazone binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins.', 'Cefoperazone is a third generation cephalosporin antibiotic. Cefoperazone exerts its bactericidal effect by inhibiting the bacterial cell wall synthesis', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Modasomil 200 mg 30 tabs.', 22882, 'مودازوميل 200مجم 30 قرص', '240', '180', 'Mind booster', 'Mind booster', NULL, 'Modafinil', 'Mash premiere', 'Tab', 'أقراص', '200 mg', '3', 'تحسن الحالة المزاجية -- تحسين الذاكرة والانتباه -- يساعد علي الافاقة', NULL, 'modafinil is a wakefulness-promoting agent (or eugeroic) for treatment of wakefulness disorders such as narcolepsy shift work sleep disorder and excessive daytime sleepiness associated with obstructive sleep apnea. dose: one dose in the morning or 2 doses', '6222001403824', 5071, '2024-07-18', NULL, 0, 'To improve wakefulness in patients with excessive daytime sleepiness (EDS) associated with narcolepsy.', 'The exact mechanism of action is unclear, although <i>in vitro</i> studies have shown it to inhibit the reuptake of dopamine by binding to the dopamine reuptake pump, and lead to an increase in extracellular dopamine. Modafinil activates glutamatergic circuits while inhibiting GABA. Modafinil is thought to have less potential for abuse than other stimulants due to the absence of any significant euphoric or pleasurable effects. It is possible that modafinil acts by a synergistic combination of mechanisms including direct inhibition of dopamine reuptake, indirect inhibition of noradrenalin reuptake in the VLPO and orexin activation. Modafinil has partial alpha 1B-adrenergic agonist effects by directly stimulating the receptors.', 'Modafinil is a stimulant drug marketed as a ''wakefulness promoting agent'' and is one of the stimulants used in the treatment of narcolepsy. Narcolepsy is caused by dysfunction of a family of wakefulness-promoting and sleep-suppressing peptides, the orexins, whose neurons are activated by modafinil. The prexin neuron activation is associated with psychoactivation and euphoria. Modafinil is not indicated for complaints of lack of energy or fatigue; but it appears to be very helpful for some patients. Also, it has been used in the treatment of hypersomnia, a disorder in which patients lack the capacity for meaningful sleep and may require ten or more hours per day. Recent studies have have found that modafinil may help recovering cocaine addicts fight their addiction.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Derma-clinic sebo-pure gel 50 gm', 17324, 'ديرما كلينك جل سيبو بيور 50 جم', '120', NULL, 'Anti-acne', 'Anti-acne', NULL, NULL, 'Borg', 'Gel', 'جل', '50 gm', '1', NULL, NULL, '*it provides a proven leave-on cleanser action to your skin and helps to avoid excess deposition of sebum via its unique sebo-regulating effect. *directions: apply gel on dry clean skin day & night.', '6223002572052', 900, '2022-12-04', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Omegar 30 caps.', 9217, 'اوميجار 30 كبسولة', '38', NULL, 'Omega 3', 'Omega 3', NULL, 'Omega-3 fish oil 1000 mg+garlic oil', 'Arab caps > royal group-egypt', 'Cap', 'كبسولة', NULL, '3', 'مكمل غذائى يعمل على زيادة التركيز وامداد الجسم بالطاقة وتحسين ضربات القلب ومستوى الكوليسترول بالدم', NULL, NULL, '6224000743246', 3629, '2023-05-18', NULL, 0, 'Provided as daily supplements. Aa preparation of omega-3-acid ethyl esters is licensed in UK for prevention of recurrent events after myocardial infarction in addition to treatment of hypertriglyceridaemia. ', 'Omega-3 fatty acids mediate anti-inflammatory effects and increased levels of EPA or DHA has shown to decrease the levels of PGE2 and 4 series-LT. 

Eicosapentaenoic acids compete with constitutive levels of arachidonic acid in cell membranes for the same desaturation enzymes and produce 3-series prostaglandins and thromboxanes, and 5-series leukotrienes which have low pro-inflammatory potential. The alteration in leukotriene biosynthesis due to higher concentration of omega-3 fatty acids compared to arachidonic acid underlies the anti-inflammatory effects. 

EPA and DHA also give rise to resolvins and related lipid signalling molecules such as protectins via cyclooxygenase and lipoxygenase pathways, which have anti-inflammatory effects. They inhibit transendothelial migration of neutrophils and inhibit TNF and IL-1β production. Omega-3 fatty acids also decrease adhesion molecule expression on leukocytes and on endothelial cells and decrease intercellular adhesive interactions.

Omega-3 (or n-3) polyunsaturated fatty acids (PUFAs) and their metabolites are natural ligands for peroxisome proliferator-activated receptor (PPAR) gamma that regulates inflammatory gene expression and NFκB activation. PPAR alpha activation is also associated with induction of COX-2 expression. 

The role of EPA and DHA in reducing triglyceride levels include inhibition of acyl-CoA:1,2-diacylglycerol acyltransferase, increased mitochondrial and peroxisomal-beta-oxidation in the liver, decreased lipogenesis in the liver, and increased plasma lipoprotein lipase activity. They also may reduce triglyceride synthesis because they are poor substrates for the enzymes responsible for TG synthesis, and EPA and DHA inhibit esterification of other fatty acids. ', 'Omega-3 fatty acids are triglycerides that get broken down into smaller fatty acid units. They act to reduce plasma triglyceride levels however increase the cholesterol levels and are thought to possess potent antiarrythmic effects. Polyunsaturated fatty acids including eicosapentaenoic and docosahexaenoic acid mediate important cellular function such as inhibition of platelet function, prolongation of bleeding time, anti-inflammatory effects and reduction of plasma fibrinogen. Polyunsaturated fatty acids are components of the phospholipids that form the structures of the cell membranes and also serve as energy source. They form eicosanoids which are important signalling molecules with wide-ranging functions in the body''s cardiovascular, pulmonary, immune and endocrine systems. DHA tends to exist in high concentrations in the retina, brain (via uptake by Mfsd2a as a transporter), and sperm.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Kolagra oily skin cleanser 200 ml', 22379, 'كولاجرا منظف للبشرة الدهنية 200 مل', '159', '149', 'Skin care.eye contour', 'Skin care.eye contour', NULL, 'Caffeine+vitamin k+vitamin c+vitamin e', 'Bio stream', 'Cleanser', 'منظف', '200 ml', '1', NULL, NULL, NULL, '6224001714269', 1065, '2025-06-01', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Primalac lf milk 400 gm', 25238, 'بريمالاك لبن 400مجم', '95', NULL, 'Lactose free milk', 'Lactose free milk', NULL, 'Lactose free milk formula', 'Triscom > multimedia international company', 'Unknown', 'غير محدد', '400 gm', '1', NULL, NULL, 'used mainly for diarrhea. not to replace mother s milk. for infants from 0 age onwards. to be used within one month from opening.', '8714563001405', 1075, '2022-07-29', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Amristatin 20mg 10 f.c. tab.', 687, 'امريستاتين 20مجم 10 اقراص', '36', NULL, 'Antihyperlipidemic.statins', 'Antihyperlipidemic.statins', NULL, 'Simvastatin', 'Amriya', 'Tab', 'أقراص', '20mg', '1', NULL, NULL, 'about simvastatin statins hmg-coa reductase inhibitor a hypolipidemic agent. mechanism of action of simvastatin it is a precursor lactone converted in to active form in the body which competitively inhibits the conversion of 3-hydroxy-3-methyl glutaryl co', '6221075010082', 1351, '2022-12-03', NULL, 0, 'Simvastatin is indicated for the treatment of hyperlipidemia to reduce elevated total cholesterol (total-C), low-density lipoprotein cholesterol (LDL‑C), apolipoprotein B (Apo B), and triglycerides (TG), and to increase high-density lipoprotein cholesterol (HDL-C).[F4655, F4658]



This includes the treatment of primary hyperlipidemia (Fredrickson type IIa, heterozygous familial and nonfamilial), mixed dyslipidemia (Fredrickson type IIb), hypertriglyceridemia (Fredrickson type IV hyperlipidemia), primary dysbetalipoproteinemia (Fredrickson type III hyperlipidemia), homozygous familial hypercholesterolemia (HoFH) as an adjunct to other lipid-lowering treatments, as well as adolescent patients with Heterozygous Familial Hypercholesterolemia (HeFH).[F4655, F4658]



Simvastatin is also indicated to reduce the risk of cardiovascular morbidity and mortality including myocardial infarction, stroke, and the need for revascularization procedures. It is primarily used in patients at high risk of coronary events because of existing coronary heart disease, diabetes, peripheral vessel disease, history of stroke or other cerebrovascular disease.[F4655, F4658]



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Simvastatin is a prodrug in which the 6-membered lactone ring of simvastatin is hydrolyzed <i>in vivo</i> to generate the beta,delta-dihydroxy acid, an active metabolite structurally similar to HMG-CoA (hydroxymethylglutaryl CoA). Once hydrolyzed, simvastatin competes with HMG-CoA for HMG-CoA reductase, a hepatic microsomal enzyme, which catalyzes the conversion of HMG-CoA to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A181421] Simvastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low density lipoprotein (LDL) receptors which increases hepatic uptake of LDL. Simvastatin also inhibits hepatic synthesis of very low density lipoprotein (VLDL).[F4655, F4658] The overall effect is a decrease in plasma LDL and VLDL. 



At therapeutic doses, the HMG-CoA enzyme is not completely blocked by simvastatin activity, thereby allowing biologically necessary amounts of mevalonate to remain available. As mevalonate is an early step in the biosynthetic pathway for cholesterol, therapy with simvastatin would also not be expected to cause any accumulation of potentially toxic sterols. In addition, HMG-CoA is metabolized readily back to acetyl-CoA, which participates in many biosynthetic processes in the body.[F4658]



In vitro and in vivo animal studies also demonstrate that simvastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] This includes improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response.



Statins have also been found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an important role in leukocyte trafficking and in T cell activation.[A181559]', 'Simvastatin is an oral antilipemic agent which inhibits HMG-CoA reductase. It is used to lower total cholesterol, low density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apoB), non-high density lipoprotein-cholesterol (non-HDL-C), and trigleride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease and high ratios are associated with higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, rosuvastatin reduces the risk of cardiovascular morbidity and mortality.[A181087, A181406]



Elevated cholesterol levels, and in particular, elevated low-density lipoprotein (LDL) levels, are an important risk factor for the development of CVD.[A181087] Use of statins to target and reduce LDL levels has been shown in a number of landmark studies to significantly reduce the risk of development of CVD and all-cause mortality.[A181090,A181093,A181096,A181427,A181475] Statins are considered a cost-effective treatment option for CVD due to their evidence of reducing all-cause mortality including fatal and non-fatal CVD as well as the need for surgical revascularization or angioplasty following a heart attack.[A181087, A181406] Evidence has shown that even for low-risk individuals (with <10% risk of a major vascular event occurring within 5 years) statins cause a 20%-22% relative reduction in major cardiovascular events (heart attack, stroke, coronary revascularization, and coronary death) for every 1 mmol/L reduction in LDL without any significant side effects or risks.[A181397, A181403]



**Skeletal Muscle Effects**



Simvastatin occasionally causes myopathy manifested as muscle pain, tenderness or weakness with creatine kinase (CK) above ten times the upper limit of normal (ULN). Myopathy sometimes takes the form of rhabdomyolysis with or without acute renal failure secondary to myoglobinuria, and rare fatalities have occurred. Predisposing factors for myopathy include advanced age (≥65 years), female gender, uncontrolled hypothyroidism, and renal impairment. Chinese patients may also be at increased risk for myopathy. In most cases, muscle symptoms and CK increases resolved when treatment was promptly discontinued.[F4655, F4658]



In a clinical trial database of 41,413 patients, the incidence of myopathy was approximately 0.03% and 0.08% at 20 and 40 mg/day, respectively, while the risk of myopathy with simvastatin 80 mg (0.61%) was disproportionately higher than that observed at the lower doses. It''s therefore recommended that the 80mg dose of simvastatin should be used only in patients who have been taking simvastatin 80 mg chronically (e.g., for 12 months or more) without evidence of muscle toxicity. As well, patients already stabilized on simvastatin 80mg should be monitored closely for evidence of muscle toxicity; if they need to be initiated on an interacting drug that is contraindicated or is associated with a dose cap for simvastatin, that patient should be switched to an alternative statin with less potential for the drug-drug interaction.[F4655, F4658]



The risk of myopathy during treatment with simvastatin may be increased with concurrent administration of interacting drugs such as [fenofibrate], [niacin], [gemfibrozil], [cyclosporine], and strong inhibitors of the CYP3A4 enzyme.  Cases of myopathy, including rhabdomyolysis, have been reported with HMG-CoA reductase inhibitors coadministered with [colchicine], and caution should therefore be exercised when prescribing these two medications together.[F4655, F4658]



**Liver Enzyme Abnormalities**



Persistent increases (to more than 3X the ULN) in serum transaminases have occurred in approximately 1% of patients who received simvastatin in clinical studies. When drug treatment was interrupted or discontinued in these patients, the transaminase levels usually fell slowly to pretreatment levels. The increases were not associated with jaundice or other clinical signs or symptoms. [F4655, F4658]



In the Scandinavian Simvastatin Survival Study (4S),[A181538] the number of patients with more than one transaminase elevation to >3 times the ULN, over the course of the study, was not significantly different between the simvastatin and placebo groups (14 [0.7%] vs. 12 [0.6%]). The frequency of single elevations of ALT to 3 times the ULN was significantly higher in the simvastatin group in the first year of the study (20 vs. 8, p=0.023), but not thereafter. In the HPS (Heart Protection Study),[A181475] in which 20,536 patients were randomized to receive simvastatin 40 mg/day or placebo, the incidences of elevated transaminases (>3X ULN confirmed by repeat test) were 0.21% (n=21) for patients treated with simvastatin and 0.09% (n=9) for patients treated with placebo.[F4655, F4658] 



**Endocrine Effects**



Increases in HbA1c and fasting serum glucose levels have been reported with HMG-CoA reductase inhibitors, including simvastatin.[F4655]



Although cholesterol is the precursor of all steroid hormones, studies with simvastatin have suggested that this agent has no clinical effect on steroidogenesis. Simvastatin caused no increase in biliary lithogenicity and, therefore, would not be expected to increase the incidence of gallstones.[F4658]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Kidsirovit syrup 60 ml', 21417, 'كيدزيروفيت شراب 60 مل', '17', NULL, 'Multivitamins', 'Multivitamins', NULL, NULL, 'Multi-apex', 'Syrup', 'شراب', '60 ml', '1', NULL, NULL, 'about vitamins vitamins are substances that are found in foods we eat. the term vitamin is derived from the words vital and amine because vitamins are required for life and were originally thought to be amines. all vitamins are not amines. vitamins are di', NULL, 635, '2022-08-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vesanoid 10 mg 100 caps.', 12248, 'فيزانويد 10مجم 100 كبسولة', '15,300', '15300', 'Antineoplastic', 'Antineoplastic', NULL, 'Tretinoin', 'F.hoffman la roche >', 'Cap', 'كبسولة', '10 mg', '10', 'مضاد للأورام والسرطان', NULL, NULL, NULL, 3020, '2025-03-18', NULL, 0, 'Oral tretinoin is indicated for induction of remission in adults and pediatric patients one year of age and older with acute promyelocytic leukemia (APL), characterized by the presence of t(15;17) translocation or presence of PML/RARα gene expression and who are refractory to or who have relapsed from anthracycline chemotherapy or for whom anthracycline-based chemotherapy is contraindicated.[L45349]



Topical tretinoin is also indicated alone [L45389] or in combination with [benzoyl peroxide] [L34869] or [clindamycin] [L45384] for the treatment of acne vulgaris. It is also used in prescription and over-the-counter for treating various skin conditions such as melasma,[L45394] hyperpigmentation,[A257474] and photoaging [A258180] alone or in combination with other drugs.', 'The exact mechanism of action of tretinoin in skin conditions and acute promyelocytic leukemia (APL) has not been fully elucidated; however, several proposed mechanisms exist. Tretinoin is believed to exert its pharmacological actions by binding to and activating two types of nuclear receptors - retinoic acid receptors (RARs) alpha, beta, and gamma and retinoid X receptors (RXRs). In the human skin, RARs (especially RAR-alpha) form heterodimers with RXR to act as inducible transcription regulators of genes involved in cell differentiation by binding to retinoic acid response elements.[A257724,A258185] Tretinoin binds to RXRs to promote epidermal proliferation. It also blocks the actions of inflammatory mediators, enhancing procollagen production and collagen type I and III formations.[A257474,A257629,A257609,A257699] Some animal and human studies suggest that tretinoin induces the expression of transforming growth factor beta (TGF-β), which stimulates the transcription of several types of collagen messenger RNA. Collagen formation curtails further solar UV-induced skin damage and aging processes.[A257699]



Acne is associated with abnormal follicular formation from excessive keratinization of epithelial cells. Tretinoin promotes cornified cell detachment and enhances keratinocyte shedding. It also stimulates mitotic activity and loosely-adherent corneocyte turnover to expel comedo contents, reducing microcomedo precursor lesions of acne vulgaris.[A257474,L34869] Tretinoin may reduce epidermal melanin and pigmentation by increasing keratinocyte turnover and reducing tyrosinase activity.[A257609,A258180]



RAR-alpha and -beta have also been implicated in APL.[A257724] APL is characterized by a t(15;17) chromosomal translocation, which fuses the promyelocytic myeloid leukemia (PML) gene with the RAR-alpha gene.[A257689,A257694] The resulting PML-RAR-alpha fusion protein plays a role in the pathogenesis of APL by aberrating promyelocyte differentiation. The PML-RAR-alpha fusion protein is found to be predominant in leukemic cells, exerting a dominant negative effect on RAR, RXR and PML function.[A257689] Tretinoin induces terminal differentiation in hemopoietic precursor cell lines and APL cells.[A257694] Tretinoin is believed to promote caspase-mediated cleavage and proteasome-dependent degradation to cause apoptosis and degradation of the PML-RAR-alpha fusion protein.[A257474] It may also convert the fusion protein from a transcription repressor to an activator.[A257474]', 'Tretinoin is a vitamin A derivative that promotes cell production, proliferation, and differentiation. When used topically, tretinoin regulates epidermal cell turnover and collagen production. It also prevents collagen loss, reduces inflammation, and blocks the induction of matrix metalloproteinase (MMP), which are enzymes that disrupt collagen and elastic fibres.[A257474,A257629,A257609,A258180] In short-term and long-term studies, topical application of tretinoin at doses ranging from 0.001% to 0.1% was associated with improvements in clinical signs of photoaging and fine wrinkles, increased epidermal thickness, compaction of the stratum corneum, and decreased melanin content.[A257684,A258180,A258185,A257699] It also improved melanocyte differentiation and distribution, promotion of epidermal hyperplasia, and angiogenesis.[A257699]



Tretinoin exhibits antineoplastic activities when given orally.[L45349] Tretinoin was shown to induce differentiation in tumour cells.[A257694] It induced cytodifferentiation and decreased acute promyelocytic leukemia (APL) cell proliferation in culture and _in vivo_. In patients with APL, tretinoin promoted the initial maturation of the primitive promyelocytes derived from the leukemic clone, followed by a repopulation of the bone marrow and peripheral blood by normal, polyclonal hematopoietic cells in patients achieving complete remission.[L45349]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Hicillin 125mg/5ml susp. 80ml', 20458, 'هيسيلين 125مجم/5مل معلق 80 مل', '3', NULL, 'Penicillins.monocomponent', 'Penicillins.monocomponent', NULL, 'Amoxicillin', 'El nasr', 'Susp', 'معلق', '125mg', '1', NULL, NULL, '(indications): is a penicillin-like beta-lactam antibiotic. it is used to treat bacterial infections caused by susceptible microorganisms. it prevents the bacterial growth and is used in the treatment of a number of infections such as pneumonia bronchitis', NULL, 743, '2022-12-03', NULL, 0, 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]



Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', 'Amoxicillin competitively inhibits penicillin-binding protein 1 and other high molecular weight penicillin binding proteins.[A6205,A190678]  Penicillin bind proteins are responsible for glycosyltransferase and transpeptidase reactions that lead to cross-linking of D-alanine and D-aspartic acid in bacterial cell walls.[A190678] Without the action of penicillin binding proteins, bacteria upregulate autolytic enzymes and are unable to build and repair the cell wall, leading to bacteriocidal action.[A190642,A190678]', 'Amoxicillin competitively inhibit penicillin binding proteins, leading to upregulation of autolytic enzymes and inhibition of cell wall synthesis.[A6205,A190678,A190642] Amoxicillin has a long duration of action as it is usually given twice daily.[L7880] Amoxicillin has a wide therapeutic range as mild overdoses are not associated with significant toxicity.[L11650] Patients should be counselled regarding the risk of anaphylaxis, _Clostridium difficile_ infections, and bacterial resistance.[L11656]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Helozin topical cream 30 gm', 5988, 'هيلوزين كريم موضعي 30 جم', '45', '40', 'Soothing topical', 'Soothing topical', NULL, NULL, 'Hi-care > roventis pharma', 'Cream', 'كريم', '30 gm', '1', 'حالات جفاف الجلد -- حروق الشمس -- لدغة الحشرات -- الإكزيما -- الحكة وإلتهاب الجلد -- للحماية من الشمس', NULL, NULL, '6224011042161', 2391, '2025-01-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tavoniza 20 mg 20 f.c.tabs.', 6489, 'تافونيزا 20مجم 20 قرص', '198', '136', 'Peptic ulcer.potassium-competitive acid blocker', 'Peptic ulcer.potassium-competitive acid blocker', NULL, 'Vonoprazan', 'Atco pharma', 'Tab', 'أقراص', '20 mg', '2', 'لعلاج اضطرابات المعدة والجهاز الهضمي وقرحة المعدة', NULL, NULL, '6223004042720', 12432, '2025-03-27', NULL, 0, 'Vonoprazan is indicated for the following conditions: 



- for healing of all grades of erosive esophagitis and relief of heartburn associated with erosive esophagitis in adults.[L51224]

- to maintain healing of all grades of erosive esophagitis and relief of heartburn associated with erosive esophagitis in adults.[L51224]

- for the relief of heartburn associated with non-erosive gastroesophageal reflux disease in adults.[L51224]

- in combination with [amoxicillin] and [clarithromycin] for the treatment of Helicobacter pylori (_H. pylori_) infection in adults.[L41695, L51224]

- in combination with amoxicillin for the treatment of H. pylori infection in adults.[L51224]', 'Vonoprazan is a potassium-competitive acid blocker (PCAB) that inhibits the H<sup>+</sup>, K<sup>+</sup>-ATPase enzyme system in a potassium-competitive manner. Through this mechanism, vonoprazan suppresses basal and stimulated gastric acid secretion at the secretory surface of gastric parietal cells.[L41695] 



Although both classes of drugs inhibit the H<sup>+</sup>, K<sup>+</sup>-ATPase, the mechanism of action of PCABs differs from that of proton-pump inhibitors (PPIs). PPIs form a covalent disulphide bond with a cysteine residue on the H<sup>+</sup>, K<sup>+</sup>-ATPase, which leads to the inactivation of the enzyme, while PCABs interfere with the binding of K<sup>+</sup> to the H<sup>+</sup>, K<sup>+</sup>-ATPase.[A253702]', 'The use of vonoprazan leads to an increase in intragastric pH. The inhibitory effect of vonoprazan on acid secretion increases with repeated daily dosing. Although the antisecretory effect of vonoprazan decreases after drug discontinuation, intragastric pH remains elevated for 24 to 48 hours. Vonoprazan does not have a clinically significant effect on QT prolongation.[L41695] 



Compared to other potassium-competitive acid blockers (PCABs), vonoprazan has a higher point-positive charge (pKa of 9.06). This allows vonoprazan to accumulate at higher concentrations in the canalicular space of the gastric parietal cells, where it binds H<sup>+</sup>, K<sup>+</sup>-ATPase in a  K<sup>+</sup>-competitive and reversible manner. Compared to other PCABs, such as SCH28080, or proton-pump inhibitors, such as lansoprazole, vonoprazan has a more potent H<sup>+</sup>, K<sup>+</sup>-ATPase inhibitory activity.[A253702]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Neurimax 6 i.m. amp.', 23456, 'نيوريماكس 6 امبول للحقن العضلي', '60', '36', 'Multivitamins', 'Multivitamins', NULL, 'Vitamin(b1+b2+b6+b12)', 'Chemipharm', 'Amp', 'أمبول', NULL, '6', NULL, NULL, 'contains lidocaine .... 10 mg each 2 ml solution contains: -thiamine hcl (vitamin b1) ... 200 mg -riboflavin sodium phosphate (vitamin b2) ... 4 mg -pyridoxine hcl (vitamin b6) ... 100 mg -cyanocobalamine (vitamin b12) ... 1000 mcg (1 mg)', '6222006502546', 4456, '2024-07-30', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nicotinell tts 20 cm square', 23569, 'نيكوتينيل تتس 20سم.مربع', '8', NULL, 'Smoking cessation.nicotinic agonist', 'Smoking cessation.nicotinic agonist', NULL, 'Nicotine', 'Novartis > glaxo smithkline', 'Unknown', 'غير محدد', NULL, '1', NULL, NULL, NULL, NULL, 1191, '2022-07-14', NULL, 0, 'For the relief of nicotine withdrawal symptoms and as an aid to smoking cessation.', 'Nicotine is a stimulant drug that acts as an agonist at nicotinic acetylcholine receptors. These are ionotropic receptors composed up of five homomeric or heteromeric subunits. In the brain, nicotine binds to nicotinic acetylcholine receptors on dopaminergic neurons in the cortico-limbic pathways. This causes the channel to open and allow conductance of multiple cations including sodium, calcium, and potassium. This leads to depolarization, which activates voltage-gated calcium channels and allows more calcium to enter the axon terminal. Calcium stimulates vesicle trafficking towards the plasma membrane and the release of dopamine into the synapse. Dopamine binding to its receptors is responsible the euphoric and addictive properties of nicotine.

Nicotine also binds to nicotinic acetylcholine receptors on the chromaffin cells in the adrenal medulla. Binding opens the ion channel allowing influx of sodium, causing depolarization of the cell, which activates voltage-gated calcium channels. Calcium triggers the release of epinephrine from intracellular vesicles into the bloodstream, which causes vasoconstriction, increased blood pressure, increased heart rate, and increased blood sugar.', 'Nicotine, the primary alkaloid in tobacco products binds stereo-selectively to nicotinic-cholinergic receptors on autonomic ganglia, the adrenal medulla, neuromuscular junctions and in the brain. Nicotine exerts two effects, a stimulant effect exerted at the locus ceruleus and a reward effect in the limbic system. Itranvenous administration of nicotine causes release of acetylcholine, norepinephrine, dopamine, serotonine, vasopressin, beta-endorphin and ACTH. Nicotine is a highly addictive substance. Nicotine also induces peripheral vasoconstriction, tachycardia and elevated blood pressure. Nicotine inhalers and patches are used to treat smoking withdrawl syndrome. Nicotine is classified as a stimulant of autonomic ganglia.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lunner cream 50 gm', 7611, 'لونر كريم 50 جم لتفتيح البشرة', '90', '70', 'Whitening topical', 'Whitening topical', NULL, 'Glycerin+titanium dioxide+licorice extract', 'Leader cosmetics > nova pharm', 'Cream', 'كريم', '50 gm', '1', 'كريم تفتيح للبشرة -- ترطيب و نعومة الجلد -- حماية الجلد من الحروق واثار الشمس', NULL, 'lightening cream', '2586545562671', 5332, '2024-09-25', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Privigen 5gm/50ml vial', 25276, 'بريفيجين 5جم/50مل فيال حقن', '7,920', '5200', 'Immunoglobulin', 'Immunoglobulin', NULL, 'Immunoglobulin g', 'Csl behring ag > 2s pharma group', 'Vial', 'فيال', '5gm', '1', 'للمناعة', NULL, NULL, NULL, 3846, '2025-02-01', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zeina hair shampoo 125 ml', 29862, 'زينا شامبو للشعر 125مل', '23', NULL, 'Hair care', 'Hair care', NULL, NULL, 'Hi-care > chemi health', 'Amp', 'أمبول', '125 ml', '1', NULL, NULL, 'anti-dandruff', NULL, 666, '2022-07-24', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Daryan hair shampoo 100 ml', 17119, 'داريان شامبو للشعر 100 مل', '95', NULL, 'Hair care', 'Hair care', NULL, 'Selenium sulfide+zinc pyrithione+chamomile+calendula+aloe vera+tea tree oil+panthenol', 'El-helou perfumes & cosmetics > medifact group', 'Amp', 'أمبول', '100 ml', '1', 'شامبو للشعر لنظافته -- ترطيب الشعر وتنعيمه -- تقوية الشعر وتغذيته', NULL, 'anti-dandruff shampoo', '6224007745090', 3945, '2023-03-15', NULL, 0, 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Viterra plus 30 caps.', 12524, 'فيتيرا بلاس 30 كبسولة', '8', NULL, 'Vitamins', 'Vitamins', NULL, 'Vitamins', 'Pfizer', 'Cap', 'كبسولة', NULL, '3', 'فيتامينات متعددة', NULL, NULL, NULL, 1421, '2022-10-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Azotec max gel 100 gm', 18229, 'ازوتك ماكس جل 100 جم', '250', '199', 'Massage cream', 'Massage cream', NULL, 'Tocopherol (vit e)+laurocapram+menthol+eucalyptus globulus leaf (eucalyptus)+eugenia caryophyllus flower extract (clove)+allantoin+magnesium sulfate+(camphora oil)+panax ginseng root extract+persea gratissima oil (avocado oil)+butyrospermum parkii butter (shea butter)+glyceryl stearate+arctium lappa root extract+carbomer+phenoxyethanol+10-hydroxystearic acid+triethanolamine', 'Koncieve', 'Gel', 'جل', '100 gm', '1', NULL, NULL, NULL, '6224002386069', 3346, '2025-12-14', NULL, 0, 'Tocopherol can be used as a dietary supplement for patients with a deficit of vitamin E; this is mainly prescribed in the alpha form.[A32443] Vitamin E deficiency is rare, and it is primarily found in premature babies of very low birth weight, patients with fat malabsorption or patients with abetalipoproteinemia.[L2120]



Tocopherol, due to its antioxidant properties, is studied for its use in prevention or treatment in different complex diseases such as cancer,[A32436] atherosclerosis, cardiovascular diseases,[A32442] and age-related macular degeneration.[A32444]', 'Tocopherol acts as a radical scavenger. It mainly acts as an antioxidant for lipid bilayers. Tocopherol''s functions depend on the H-atom donating ability, location, and movement within the membrane, as well as the efficiency in the radical recycling by some cytosolic reductants such as ascorbate.[A32445] Tocopherol actions are related to the trap of radicals, and it has been shown that even in the absence of substituents in the ortho-positions, tocopherol can trap more than two radicals. The type of radicals available for tocopherol are alkyl and peroxy.[L2123] ', 'The antioxidant effects of tocopherol can be translated into different changes at the pharmacodynamic level. In vitro studies have shown that this antioxidant activity can produce modification in protein kinase C (PKC) which will later be translated into an inhibition of cell death. Some other derivate effects are the anti-inflammatory properties of tocopherol which can be related to the modulation of cytokines or prostaglandins, prostanoids and thromboxanes.[A32445]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('No-migrain z 2.5mg 2 f.c.tab', 8951, 'نو-ميجران زد 2.5مجم 2 قرص', '22', NULL, 'Antimigraine.vasoconstrictor agents.selective serotonin agonist', 'Antimigraine.vasoconstrictor agents.selective serotonin agonist', NULL, 'Zolmitriptan', 'Amoun', 'Tab', 'أقراص', '2.5mg', '1', NULL, NULL, 'about zolmitriptan selective serotonin (5ht1) agonists anti migraine mechanism of action of zolmitriptan zolmitriptan is a selective agonist for serotonin (5ht1 receptors). it relieves migraine by selective constriction of intracranial blood vessels neuro', '6221025017147', 1144, '2022-10-28', NULL, 0, 'Zolmitriptan is indicated for the acute treatment of migraine with or without auras in patients aged 18 and over.[L12978]', 'Migraines are complex physiological events characterized by unilateral throbbing headaches combined with photophobia and other aversions to sensory input. Migraine attacks are generally divided into phases: the premonitory phase, which typically involves irritability, fatigue, yawning, and stiff neck; the headache phase, which lasts for between four and 72 hours; and the postdrome phase, which lasts for up to a day following resolution of pain and whose symptoms are similar to those of the premonitory phase. In addition, neurological deficits, collectively termed migraine aura, may precede the headache phase.[A193797, A193794]



The underlying pathophysiology of migraines is a matter of active research but involves both neurological and vascular components. The head pain associated with migraine is thought to be a consequence of activation of the nociceptive nerves comprising the trigeminocervical complex (TCC).[A193794] Terminals of nociceptive nerves that innervate the dura matter release vasoactive peptides, such as calcitonin gene-related peptide (CGRP), resulting in cranial vasodilation. Finally, when present, migraine aura appears to correlate with a transient wave(s) of cortical depolarization, termed cortical spreading depression (CSD).[A193794,A193851]



Triptans, including zolmitriptan, are proposed to act in three ways. The main mechanism is through modulation of nociceptive nerve signalling in the central nervous system through 5-HT<sub>1B/1D</sub> receptors throughout the TCC and associated areas of the brain. In addition, triptans can enhance vasoconstriction, both through direct 5-HT<sub>1B</sub>-mediated dilation of cranial blood vessels,[A462, A198741] as well as through 5-HT<sub>1D</sub>-mediated suppression of CGRP release.[A193794, L12978]



Although triptans are classically described solely in terms of their effects on 5-HT<sub>1B/1D</sub> receptors, they also act as 5-HT<sub>1F</sub> agonists as well. This 5-HT subtype is also found throughout the TCC, but is not present appreciably in cerebral vasculature; the significance of triptan-mediated 5-HT<sub>1F</sub> activation is currently not well described.[A193791] Additionally, CSD that initiates in the ipsilateral parietal region may exert its effects in a manner that relies on 5-HT<sub>1B/1D</sub> receptor activation, suggesting that triptans may have some effect on CSD-mediated symptoms.[A193794, A198738]', 'Zolmitriptan, like other triptans, is a serotonin (5-hydroxytryptamine; 5-HT) receptor agonist, with enhanced specificity for the 5-HT<sub>1B</sub> and 5-HT<sub>1D</sub> receptor subtypes. It is through the downstream effects of 5-HT<sub>1B/1D</sub> activation that triptans are proposed to provide acute relief of migraines.[L12978,A462,A193791] Zolmitriptan is also a vasoconstrictor,[A193803] leading to possible adverse cardiovascular effects such as myocardial ischemia/infarction, arrhythmias, cerebral and subarachnoid hemorrhage, stroke, gastrointestinal ischemia, and peripheral vasospastic reactions. In addition, chest/throat/neck/jaw pain, tightness, and/or pressure has been reported, along with the possibility of medication overuse headaches and serotonin syndrome. Patients with phenylketonuria should be advised that ZOMIG-ZMT contains phenylalanine.[L12978]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Rotaplanin 200 mg vial for i.v/i.m inj.', 31261, 'روتابلانين 200مجم فيال للحقن عضل/وريد', '287', '200', 'Glycopeptide antibiotic', 'Glycopeptide antibiotic', NULL, 'Teicoplanin', 'Rameda > egypharm', 'Inj', 'حقن', '200 mg', '1', NULL, NULL, 'about teicoplanin glycopeptide antibiotic antibiotic similar to vancomycin. mechanism of action of teicoplanin teicoplanin binds to the terminal dipeptide (amino d alanyl d alanine residue) sequence of peptidoglycan and inhibits the association and cross', NULL, 1161, '2024-12-15', NULL, 0, 'For the treatment of bacterial infections caused by susceptible microorganisms.', 'Teicoplanin inhibits peptidoglycan polymerization, resulting in inhibition of bacterial cell wall synthesis and cell death.', 'It is a glycopeptide antiobiotic extracted from <i>Actinoplanes teichomyceticus</i>, with a similar spectrum of activity to vancomycin. Its mechanism of action is to inhibit bacterial cell wall synthesis. Oral teicoplanin has been demonstrated to be effective in the treatment of pseudomembranous colitis and Clostridium difficile-associated diarrhoea, with comparable efficacy to vancomycin.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Endlixaban 2.5 mg 20 f.c. tabs', 18304, 'اندليكسابان 2.5مجم 20 قرص', '166', NULL, 'Anticoagulant', 'Anticoagulant', NULL, 'Apixaban', 'Atco pharma', 'Tab', 'أقراص', '2.5 mg', '2', 'مضاد للتجلط', NULL, NULL, '6223004041624', 5503, '2023-02-04', NULL, 0, 'Apixaban is indicated for reducing the risk of stroke and systemic embolism in patients who have nonvalvular atrial fibrillation, prophylaxis of deep vein thrombosis(DVT) leading to pulmonary embolism(PE) in patients after a hip or knee replacement surgery, and treatment of DVT and PE to reduce the risk of recurrence[Label,A177565,A6897].', 'Apixaban selectively inhibits factor Xa in its free and bound forms, independant of antithrombin III[Label]. Apixaban also inhibits prothrominase[Label]. These effects prevent the formation of a thrombus[Label].', 'Apixaban selectively inhibits factor Xa in its free and bound forms, independant of antithrombin III[Label]. Apixaban also inhibits prothrominase[Label]. These effects prevent the formation of a thrombus[Label].', 'DrugBank');
