INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Top-flam cream 15 gm', 11531, 'توب-فلام كريم 15 جم', '22', '14', NULL, NULL, NULL, 'Gramicidin+neomycin+nystatin+triamcinolone', 'Hikma pharma', 'Cream', 'كريم', '15 gm', '1', NULL, NULL, 'about gramicidin gramicidin is active against most of the gram positive bacteria and some gram negative bacteria. its use is limited to topical use only since systemic use may produce haemolysis.gramicidin produces its action by increasing the permeabilit', '6221000001222', 3477, '2024-10-14', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Raspberry ketone complex 20 caps', 19503, 'راسبيري كيتون كومبلكس 20 كبسولة', '499', NULL, 'Weight loss', 'Weight loss', NULL, 'Green tea+raspberry ketone powder+caffeine', 'Vitacare', 'Cap', 'كبسولة', NULL, '2', NULL, NULL, NULL, NULL, 743, '2022-09-20', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bisadyl 5mg 30 enteric coated tab.', 1589, 'بيساديل 5مجم 30 قرص', '29', '15', 'Laxative', 'Laxative', NULL, 'Bisacodyl', 'Amriya', 'Tab', 'أقراص', '5mg', '3', NULL, NULL, NULL, '6221075012086', 4234, '2024-11-06', NULL, 0, 'Bisacodyl is indicated to relieve occasional constipation and irregularity.[L13362]', 'Bisacodyl is deacetylated to the active bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM) by an intestinal deacetylase.[A233290,A233300,A207700] BHPM can stimulate parasympathetic nerves in the colon directly to increase motility and secretions.[A233300]



Bisacodyl stimulates adenylate cyclase, increasing cyclic AMP, leading to active transport of chloride and bicarbonate out of cells.[A233300] Sodium ions, potassium ions, and water passively leave the cell; while sodium and chloride ions are unable to be reabsorbed.[A233300]



Water is also be transported from the luminal side of cells into the vasculature by aquaporin 3.[A233395] Bisacodyl decreases expression of aquaporin 3, preventing water from moving into the vasculature, which may contribute to increased water in the colon.[A233395]



Bisacodyl directly stimulates parasympathetic nerves in the colon, stimulating contraction of longitudinal smooth muscle but not circular smooth muscle.[A233280,A233300]', 'Patients should be counselled regarding abdominal pain, nausea, vomiting, or a change in bowel function that lasts longer than 2 weeks.[L13362] It has a wide therapeutic index, as patients can take 5-15 mg orally.[L13362] Patients taking bisacodyl should be counselled before taking the medication if they are already experiencing abdominal pain, nausea, vomiting, or a change in bowel function lasting longer than 2 weeks.[L13362] Patients should also be counselled to stop taking the medication if they experience rectal bleeding or no bowel movement in 12 hours.[L13362]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Yohimbe 2000 mg 50 caps. (illegal import)', 29801, 'يومب 2000مجم 50 كبسول', '0', NULL, 'Aphrodisiac', 'Aphrodisiac', NULL, 'Yohimbe', 'Puritans pride', 'Cap', 'كبسولة', '2000 mg', '5', NULL, NULL, 'yohimbe 2000 has been shown to increase the libido in men and women with low and even normal sex drives. dose: for adult men take 2 capsules daily preferably with a meal.', NULL, 1352, '2022-07-09', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Elocortin 0.1% cream 15 gm', 4090, 'ايلوكورتين 0.1% كريم 15 جم', '11', NULL, 'Anti-inflammatory.glucocorticoid.', 'Anti-inflammatory.glucocorticoid.', NULL, 'Mometasone furoate', 'Sigma', 'Cream', 'كريم', '0.1%', '1', 'مضاد للالتهاب.', NULL, 'about mometasone a glucocorticoid steroid anti psoriatic anti asthma anti inflammatory. mechanism of action of mometasone mometasone is a topical medium potency glucocorticoid. the drug exerts its pharmacological action by penetrating and binding to cytop', '6221051080054', 2228, '2023-04-28', NULL, 0, 'The inhaler is indicated for the maintenance treatment of asthma as prophylactic therapy. The nasal spray is indicated for the treatment of the nasal symptoms of seasonal allergic and perennial allergic rhinitis.', 'Unbound corticosteroids cross cell membranes and bind with high affinity to specific cytoplasmic receptors. Inflammation is decreased by diminishing the release of leukocytic acid hydrolases, prevention of macrophage accumulation at inflamed sites, interference with leukocyte adhesion to the capillary wall, reduction of capillary membrane permeability, reduction of complement components, inhibition of histamine and kinin release, and interference with the formation of scar tissue. The antiinflammatory actions of corticosteroids are thought to involve phospholipase A<sub>2</sub> inhibitory proteins, lipocortins, which control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes. Mometasone furoate has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone.', 'Mometasone is a medium-potency synthetic corticosteroid with antiinflammatory, antipruritic, and vasoconstrictive properties. Studies in asthmatic patients have demonstrated that mometasone provides a favorable ratio of topical to systemic activity due to its primary local effect along with the extensive hepatic metabolism and the lack of active metabolites. Though effective for the treatment of asthma, glucocorticoids do not affect asthma symptoms immediately. Maximum improvement in symptoms following inhaled administration of mometasone furoate may not be achieved for 1 to 2 weeks or longer after starting treatment. When glucocorticoids are discontinued, asthma stability may persist for several days or longer. Mometasone has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone. The clinical significance of these findings is unknown.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Herceptin 150 mg vial for i.v. inf.', 20406, 'هيرسبتين 150مجم فيال للتسريب الوريدي', '7,846', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Trastuzumab', 'F.hoffman la roche', 'Vial', 'فيال', '150 mg', '1', 'مضاد للاورام', NULL, NULL, NULL, 1274, '2022-04-06', NULL, 0, 'For the adjuvant treatment of HER2-overexpressing breast cancer, trastuzumab is indicated in several clinical settings: as part of a treatment regimen consisting of doxorubicin, cyclophosphamide, and either paclitaxel or docetaxel; as part of a treatment regimen with docetaxel and carboplatin; or as monotherapy following multi-modality anthracycline-based therapy.[L14015]



Trastuzumab is indicated as a first-line treatment, in combination with paclitaxel, for metastatic HER2-overexpressing breast cancer, and as monotherapy in patients who have previously received one or more chemotherapy regimens in the metastatic setting.[L14015] In Europe, trastuzumab can also be used in combination with paclitaxel or docetaxel for the treatment of metastatic HER2-positive breast cancer in adult patients and with an aromatase inhibitor in postmenopausal patients.[L49324]



For HER2-positive early breast cancer, the EMA approved trastuzumab as monotherapy following surgery, chemotherapy (neoadjuvant or adjuvant), and radiation or following adjuvant chemotherapy with doxorubicin and cyclophosphamide in combination with paclitaxel or docetaxel. It can also be used in combination with adjuvant chemotherapy consisting of docetaxel and carboplatin or with neoadjuvant chemotherapy followed by adjuvant trastuzumab therapy for locally advanced (including inflammatory) disease or tumors > 2 cm in diameter.[L49324]



Trastuzumab is also indicated, in combination with cisplatin and capecitabine or 5-fluorouracil, for the treatment of patients with HER2-overexpressing metastatic gastric or gastroesophageal junction adenocarcinoma who have not received prior treatment for metastatic disease by the FDA and EMA.[L14015]



Trastuzumab is indicated for subcutaneous administration - in combination with either [hyaluronidase][L14132] or both hyaluronidase and [pertuzumab][L14510] - for the treatment of adults with HER2-positive breast cancers.', 'Trastuzumab is a recombinant humanized IgG1 monoclonal antibody against the HER-2 receptor, a member of the epidermal growth factor receptors which is a photo-oncogene. Over-expressed in breast tumour cells, HER-2 overamplifies the signal provided by other receptors of the HER family by forming heterodimers [A121]. The HER-2 receptor is a transmembrane tyrosine kinase receptor that consists of an extracellular ligand-binding domain, a transmembrane region, and an intracellular or cytoplasmic tyrosine kinase domain. It is activated by the formation of homodimers or heterodimers with other EGFR proteins, leading to dimerization and autophosphorylation and/or transphosphorylation of specific tyrosine residues in EGFR intracellular domains [A121]. Further downstream molecular signaling cascades are activated, such as the Ras/Raf/mitogen-activated protein kinase (MAPK), the phosphoinositide 3-kinase/Akt, and the phospholipase Cγ (PLCγ)/protein kinase C (PKC) pathways that promote cell growth and survival and cell cycle progression [A121]. Due to upregulation of HER-2 in tumour cells, hyperactivation of these signaling pathways and abnormal cell proliferation is observed. Trastuzumab binds to the extracellular ligand-binding domain and blocks the cleavage of the extracellular domain of HER-2 to induce its antibody-induced receptor downmodulation [A121], and subsequently inhibits HER-2-mediated intracellular signaling cascades. Inhibition of MAPK and PI3K/Akt pathways lead to an increase in cell cycle arrest, and the suppression of cell growth and proliferation [A121]. Trastuzumab also mediates the activation of antibody-dependent cell-mediated cytotoxicity (ADCC) [A40276] by attracting the immune cells, such as natural killer (NK) cells, to tumor sites that overexpress HER-2 [A121]. While the drug alone has a minimal potential to induce complement-dependent cytotoxicity (CDC),[A201902] one study demonstrated increased therapeutic effectiveness and a synergistic effect on uterine serous carcinoma cells _in vitro_ when used in combination with [pertuzumab], which also has minor effects on CDC alone. This study showed that only the combination of both cell-bound antibodies would be sufficient to bind and activate the complement component 1q (C1q) required to initiate the complement cascade reaction.[A201896]



Intrinsic trastuzumab resistance has been noted for some patients with HER-2 positive breast cancer. Mechanisms involving trastuzumab resistance include deficiency of phosphatase and tensin homologue and activation of phosphoinositide 3-kinase, and the overexpression of other surface receptors, such as insulin-like growth factor [A40276].', 'Trastuzumab exerts an antitumour activity and is used in the treatment of HER2-positive breast cancer. HER2 protein overexpression is observed in 20%-30% of primary breast cancers [A40277] thus HER2 presents as a useful therapeutic target for the treatment of breast cancers. Trastuzumab has been shown, in both _in vitro_ assays and in animals, to inhibit the proliferation of human tumour cells that overexpress HER2. It works as a mediator of antibody-dependent cellular cytotoxicity, where it binds as an antibody to cells over-expressing HER2, leading to preferential cell death. Trastuzumab was also shown to inhibit angiogenesis of tumor cells _in vivo_ [A121]. Higher doses and longer dosing intervals show no significant benefit over standard dose schedules [A40276]. In patients with HER2 positive solid tumours, trastuzumab did not exert any clinically significant QTc interval duration.[L14015]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ovamax 120 veg. caps.', 33353, 'اوفاماكس 120 كبسولة', '4,290', '4335', 'Estrogen', 'Estrogen', NULL, 'Alpha lipoic acid+co-enzyme q10+myo-inositol+l-arginine+folic acid', 'Ams (america medic&science)', 'Cap', 'كبسولة', NULL, '1', NULL, NULL, 'supreme universal fertility. women fertility supplement. booster formula. dose: two capsules twice a day with meals.', '604776737070', 579, '2024-06-11', NULL, 0, NULL, NULL, NULL, NULL);
