INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Okava vag.douche 200 ml', 9113, 'اوكافا غسول مهبلي 200 مل', '80', '60', 'Vaginal wash', 'Vaginal wash', NULL, NULL, 'Hi-care > roventis pharma', 'Unknown', 'غير محدد', '200 ml', '1', 'مضاد للفطريات المهبلية -- التخلص من الروائح الكريهة بالمهبل -- الحفاظ علي البيئة الداخلية للمهبل', NULL, NULL, '6224000201852', 1805, '2025-08-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Lacosamet 200 mg 30 f.c. tabs.', 21521, 'لاكوساميت 200مجم 30 قرص', '323', '200', 'Anti-epileptic', 'Anti-epileptic', NULL, 'Lacosamide', 'Marcyrl co.', 'Tab', 'أقراص', '200 mg', '3', 'علاج نوبات الصرع', NULL, 'pharmacology precise mechanism of action is unknown; however lacosamide selectively enhances slow inactivation of voltage-gated sodium channels resulting in stabilization of hyperexcitable neuronal membranes and inhibition of repetitive neuronal firing. i', NULL, 1884, '2025-01-26', NULL, 0, 'In the US and Europe, lacosamide is indicated for the treatment of partial-onset seizures in children and adults.[L49191, L49196, L49201] In Canada, it is reserved for use in adults.[L49206]



It is also used as an adjunctive therapy in the treatment of primary generalized tonic-clonic seizures in patients four years of age and older.[L49191, L49201]



The extended-release capsules of lacosamide are indicated for the treatment of partial-onset seizures in adults and in pediatric patients weighing at least 50 kg.[L49196]', 'Caused by neuronal hyperexcitability, seizures in epilepsy involve sustained firing of sodium-dependent action potentials. The slow inactivation process, intrinsic to voltage-gated sodium channel functioning, has been implicated in the paroxysmal depolarizing shifts associated with epileptic activity.[A262616] The exact mechanism of action of lacosamide is not fully known; however, in vitro electrophysiological studies have shown that lacosamide selectively enhances the slow inactivation of voltage-gated sodium channels, shifting the slow inactivation curve to more hyperpolarized potentials and augmenting the maximal fraction of channels in the slow inactivated state.[A262686] This results in the stabilization of hyperexcitable neuronal membranes and inhibition of repetitive neuronal firing.[A262616, L49191] Lacosamide does not affect the fast component of voltage-gated sodium currents, unlike traditional sodium channel blockers.[A262616]', 'Lacosamide is an antiepileptic drug with high oral potency, stereoselectivity,[A262686] and anticonvulsant effects.[A262616] By blocking sensory neuronal voltage-gated sodium channels that mediate neuropathic pain responses, lacosamide was shown to possess analgesic activity.[A4071, A262616] Lacosamide is a chiral functionalized amino acid. The S-stereoisomer does not exhibit antiepileptic activity.[A262616]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Dimaklaric 0.15/0.03mg 21 tab.', 17667, 'ديماكلاريك 0.15/0.03 مجم 21 قرص', '51', '29.5', 'Contraceptives', 'Contraceptives', NULL, 'Desogestrel+ethinyl estradiol', 'Acdima international trading', 'Tab', 'أقراص', '0.03mg', '1', 'دواء لمنع الحمل -- يؤخذ تحت اشراف طبي', NULL, 'mechanism of action of desogestrel : desogestrel is a third generation progestins which is used as a female contraceptive. desogestrel is generally used in combination with ethinyl estradiol. desogestrel is a potent inhibitor of ovulation.', NULL, 1844, '2025-10-13', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Unizefo syrup 120 ml', 28786, 'يونيزيفو شراب 120 مل', '20', '9', 'Multivitamins', 'Multivitamins', NULL, 'Ferrous sulfate+zinc sulphate+vitamin b12', 'Unipharma co.', 'Syrup', 'شراب', '120 ml', '1', 'مكمل غذائي -- مصدر للفيتامينات -- مقوي للمناعة -- مقوي للاعصاب', NULL, NULL, NULL, 1643, '2024-04-24', NULL, 0, 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Braxone cream 60 gm', 15468, 'براكسون كريم 60 جم', '50', NULL, 'Moisturizing cream', 'Moisturizing cream', NULL, 'Honey+propolis+aloe vera ext.+olive oil+chamomile+vitamin e +panthenol+jojoba oil', 'Egyptian company for cosmetics > infinity pharm', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, 'braxone fast healing cream speeds up the healing process & provides optimal healing environment. it has debridement effect & anti-inflammatory effect. indications skin dryness ï¿½ nipple fissure ï¿½ cracked skin. ingredients honey ï¿½ propolis ï¿½ aloe ve', '6224000547639', 1609, '2022-12-03', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('L-carnitine fumarate 1000 mg 90 caplets (illegal import)', 21668, 'ال-كارنتين فيوماريت 1000مجم 90 كبسولة مستورد', '1,300', '490', 'Antioxidant', 'Antioxidant', NULL, 'L-carnitine fumarate', 'Puritans pride', 'Cap', 'كبسولة', '1000 mg', '1', 'مكمل غذائى يستخدم لعلاج اعتلال الدماغ. الارتباك وعلاج بعض حالات العقم عند الرجال وزيادة الخصوبة.', NULL, NULL, NULL, 1468, '2024-07-29', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sodium bicarbonate 5% (mottahedoon) i.v. inf. 10 ml', 27134, 'صوديوم بيكربونات 5% المتحدون محلول وريدي 10 مل', '4', NULL, 'Alkalinizer', 'Alkalinizer', NULL, 'Sodium bicarbonate', 'Vacsera', 'Unknown', 'غير محدد', '5%', '1', NULL, NULL, NULL, NULL, 677, '2022-08-01', NULL, 0, 'Sodium bicarbonate is used for the treatment of metabolic acidosis which may occur in severe renal disease, uncontrolled diabetes, circulatory insufficiency due to shock or severe dehydration, extracorporeal circulation of blood, cardiac arrest and severe primary lactic acidosis. Also is indicated in severe diarrhea which is often accompanied by a significant loss of bicarbonate. Further indicated in the treatment of certain drug intoxications, including barbiturates (where dissociation of the barbiturateprotein complex is desired), in poisoning by salicylates or methyl alcohol and in hemolytic reactions requiring alkalinization of the urine to diminish nephrotoxicity of blood pigments.', 'Sodium bicarbonate is a systemic alkalizer, which increases plasma bicarbonate, buffers excess hydrogen ion concentration, and raises blood pH, thereby reversing the clinical manifestations of acidosis. It is also a urinary alkalizer, increasing the excretion of free bicarbonate ions in the urine, thus effectively raising the urinary pH. By maintaining an alkaline urine, the actual dissolution of uric acid stones may be accomplished. Sodium bicarbonate acts as an antacid and reacts chemically to neutralize or buffer existing quantities of stomach acid but has no direct effect on its output. This action results in increased pH value of stomach contents, thus providing relief of hyperacidity symptoms. [PharmGKB]', 'Intravenous sodium bicarbonate therapy increases plasma bicarbonate, buffers excess hydrogen ion concentration, raises blood pH and reverses the clinical manifestations of acidosis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Marnys propolis 1000 mg with echinacea 30 caps.', 22317, 'مارنيز بروبولس مع خلاصة الاشينسيا لتقوية المناعة كبسولة', '400', NULL, 'Multivitamins', 'Multivitamins', NULL, 'Propolis+echinacea+wheat germ oil', 'Marnys > walfarm pharmaceuticals', 'Cap', 'كبسولة', '1000 mg', '3', NULL, NULL, 'marnysï- propolis 1000mg with echinacea is an excellent food supplement for adults who want to adjust their defences and breathing especially during seasonal changes. dose: 1 capsule per day before breakfast.', NULL, 846, '2023-06-16', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Papaphyl cream 20 gm', 24548, 'بابافيل كريم 20جم', '35', NULL, NULL, NULL, NULL, 'Papaya extract+chlorophyllin+carbamide+allantoin', 'Medizen pharmaceutical industries > imaginex egypt co.', 'Cream', 'كريم', '20 gm', '1', NULL, NULL, NULL, NULL, 669, '2022-07-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Depezac 90 mg 4 delayed release tab.', 17273, 'ديبيزاك 90مجم 4 قرص', '12', NULL, 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Fluoxetine', 'Epci', 'Tab', 'أقراص', '90 mg', '1', NULL, NULL, 'about fluoxetine selective serotonin reuptake inhibitor antidepressant. mechanism of action of fluoxetine fluoxetine preferentially inhibits the re-uptake of serotonin (5 ht) in the cns nerve terminals .this will result in an increased concentration of ne', NULL, 1098, '2022-07-08', NULL, 0, 'Fluoxetine is indicated for both acute and maintenance treatment of major depressive disorder, obsessive compulsive disorder, and bulimia nervosa; however, it is only indicated for acute treatment of panic disorder independent of whether agoraphobia is present.[L7664] Fluoxetine may also be used in combination with olanzapine to treat depression related to Bipolar I Disorder, and treatment resistant depression.[L7664] Fluoxetine is additionally indicated for the treatment of female patients with premenstrual dysphoric disorder (PMDD).[L40833]', 'The monoaminergic hypothesis of depression emerged in 1965 and linked depression with dysfunction of neurotransmitters such as noradrenaline and serotonin.[L7721] Indeed, low levels of serotonin have been observed in the cerebrospinal fluid of patients diagnosed with depression.[A181793] As a result of this hypothesis, drugs that modulate levels of serotonin such as fluoxetine were developed.[L7721]



Fluoxetine is a selective serotonin reuptake inhibitor (SSRI) and as the name suggests, it exerts it''s therapeutic effect by inhibiting the presynaptic reuptake of the neurotransmitter serotonin.[A181673] As a result, levels of 5-hydroxytryptamine (5-HT) are increased in various parts of the brain.[L7721] Further, fluoxetine has high affinity for 5-HT transporters, weak affinity for noradrenaline transporters and no affinity for dopamine transporters indicating that it is 5-HT selective.[L7721]



Fluoxetine interacts to a degree with the 5-HT<sub>2C</sub> receptor and it has been suggested that through this mechanism, it is able to increase noradrenaline and dopamine levels in the prefrontal cortex.[L7721]', 'Fluoxetine blocks the serotonin reuptake transporter in the presynaptic terminal, which ultimately results in sustained levels of 5-hydroxytryptamine (5-HT) in certain brain areas.[L7721] However, fluoxetine binds with relatively poor affinity to 5-HT, dopaminergic, adrenergic, cholinergic, muscarinic, and histamine receptors which explains why it has a far more desirable adverse effect profile compared to earlier developed classes of antidepressants such as tricyclic antidepressants.[L7721][A181673] ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Hepta panthenol light body lotion 100 ml', 6593, 'هيبتا بانثينول لايت بودي لوشن 100 مل', '110', NULL, 'Skin care.moisturizing', 'Skin care.moisturizing', NULL, 'Panthenol+vitamin c', 'Hepta', 'Lotion', 'لوشن', '100 ml', '1', NULL, NULL, NULL, NULL, 676, '2025-08-08', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zithrokan 200mg/5ml pd. for oral susp. 15 ml', 12889, 'زيثروكان 200مجم/5مل معلق 15 مل', '62', '43', 'Antibiotic.macrolide', 'Antibiotic.macrolide', NULL, 'Azithromycin', 'Hikma pharma', 'Susp', 'معلق', '200mg', '1', 'مضاد حيوي', NULL, 'description: azithromycin is a semi-synthetic macrolide antibiotic of the azalide class. dose: adult: po- respiratory tract skin and soft tissue infections- 500 mg once daily for 3 days. or 500mg on day 1 followed by 250mg on days 2 to 5. typhoid- 500 mg', '6221000001413', 3284, '2024-08-07', NULL, 0, 'Azithromycin should be used only to treat or prevent infections that are proven or strongly suspected to be caused by susceptible bacteria in order to prevent the development antimicrobial resistance and maintain the efficacy of azithromycin [FDA label].



Azithromycin is indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the microorganisms listed in the specific conditions below. Recommended dosages, duration of therapy and considerations for various patient populations may vary among these infections. Refer to the FDA label and "Indications" section of this drug entry for detailed information [FDA label]. 



**Adults**:



Acute bacterial exacerbations of chronic obstructive pulmonary disease due to _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Acute bacterial sinusitis due to _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Community-acquired pneumonia due to _Chlamydophila pneumoniae_, _Haemophilus influenzae_, _Mycoplasma pneumoniae_ or _Streptococcus pneumoniae_ in patients appropriate for oral therapy



Pharyngitis/tonsillitis caused by _Streptococcus pyogenes_ as an alternative to first-line therapy in individuals who cannot use first-line therapy.

Uncomplicated skin and skin structure infections due to _Staphylococcus aureus_, _Streptococcus pyogenes_, or _Streptococcus agalactiae_. Abscesses usually require surgical drainage.



Urethritis and cervicitis due to _Chlamydia trachomatis_ or _Neisseria gonorrhoeae_.



Genital ulcer disease in men due to _Haemophilus ducreyi_ (chancroid). Due to the small number of women included in clinical trials, the efficacy of azithromycin in the treatment of chancroid in women has not been established.



**Pediatric Patients**



Acute otitis media caused by _Haemophilus influenzae_, _Moraxella catarrhalis_ or _Streptococcus pneumoniae_



Community-acquired pneumonia due to _Chlamydophila pneumoniae_, _Haemophilus influenzae_, _Mycoplasma pneumoniae_ or _Streptococcus pneumoniae_ in patients appropriate for oral therapy.



Pharyngitis/tonsillitis caused by _Streptococcus pyogenes_ as an alternative to first-line therapy in individuals who cannot use first-line therapy.', 'In order to replicate, bacteria require a specific process of protein synthesis, enabled by ribosomal proteins [A6505]. Azithromycin binds to the 23S rRNA of the bacterial 50S ribosomal subunit. It stops bacterial protein synthesis by inhibiting the transpeptidation/translocation step of protein synthesis and by inhibiting the assembly of the 50S ribosomal subunit [FDA label], [A14179]. This results in the control of various bacterial infections [A174193], [FDA label].   The strong affinity of macrolides, including azithromycin, for bacterial ribosomes, is consistent with their broad‐spectrum antibacterial activities [A174193].





Azithromycin is highly stable at a low pH, giving it a longer serum half-life and increasing its concentrations in tissues compared to erythromycin [A174175]. 



', 'Macrolides stop bacterial growth by inhibiting protein synthesis and translation, treating bacterial infections [A174175].

Azithromycin has additional immunomodulatory effects and has been used in chronic respiratory inflammatory diseases for this purpose [A174172].



