INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6252, 'Albustix 4mg 30 tab.', 'البوستيكس 4مجم 30 قرص', '51', '34.5', 'Antihypertensive.arbs', 'Candesartan cilexetil', 'Sigma > concord pharmaceutical industries', 'Tab', 'أقراص', '4mg', '3', NULL, NULL, '6223005630254', NULL, 1172, '2024-10-30', 0, 'May be used as a first line agent to treat uncomplicated hypertension, isolated systolic hypertension and left ventricular hypertrophy. May be used as a first line agent to delay progression of diabetic nephropathy. Candesartan may be also used as a second line agent in the treatment of congestive heart failure, systolic dysfunction, myocardial infarction and coronary artery disease in those intolerant of ACE inhibitors.', 'Candesartan selectively blocks the binding of angiotensin II to AT1 in many tissues including vascular smooth muscle and the adrenal glands. This inhibits the AT1-mediated vasoconstrictive and aldosterone-secreting effects of angiotensin II and results in an overall decrease in blood pressure. Candesartan is greater than 10,000 times more selective for AT1 than AT2. Inhibition of aldosterone secretion may increase sodium and water excretion while decreasing potassium excretion.', 'Candesartan cilexetil is an ARB prodrug that is rapidly converted to candesartan, its active metabolite, during absorption from the gastrointestinal tract. Candesartan confers blood pressure lowering effects by antagonizing the hypertensive effects of angiotensin II via the RAAS. RAAS is a homeostatic mechanism for regulating hemodynamics, water and electrolyte balance. During sympathetic stimulation or when renal blood pressure or blood flow is reduced, renin is released from granular cells of the juxtaglomerular apparatus in the kidneys. Renin cleaves circulating angiotensinogen to angiotensin I, which is cleaved by angiotensin converting enzyme (ACE) to angiotensin II. Angiotensin II increases blood pressure by increasing total peripheral resistance, increasing sodium and water reabsorption in the kidneys via aldosterone secretion, and altering cardiovascular structure. Angiotensin II binds to two receptors: type-1 angiotensin II receptor (AT1) and type-2 angiotensin II receptor (AT2). AT1 is a G-protein coupled receptor (GPCR) that mediates the vasoconstrictive and aldosterone-secreting effects of angiotensin II. Studies performed in recent years suggest that AT2 antagonizes AT1-mediated effects and directly affects long-term blood pressure control by inducing vasorelaxation and increasing urinary sodium excretion. Angiotensin receptor blockers (ARBs) are non-peptide competitive inhibitors of AT1. ARBs block the ability of angiotensin II to stimulate pressor and cell proliferative effects. Unlike ACE inhibitors, ARBs do not affect bradykinin-induced vasodilation. The overall effect of ARBs is a decrease in blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6253, 'Syringe 3 cm adult', 'سرنجة 3 سم كبار', '3', '2', NULL, NULL, 'Company', 'Syringe', 'حقنة', NULL, '1', NULL, NULL, NULL, NULL, 943, '2022-08-17', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6254, 'Mitashe 3mg/3ml 3 i.v. amps.', 'ميتاشي 3مجم/3مل 3 امبولات', '254', NULL, '5-ht3 antagonist.anti-emetic', 'Granisetron', 'Mash premiere', 'Amp', 'أمبول', '3mg', '3', NULL, NULL, NULL, NULL, 880, '2022-08-17', 0, 'For the prevention of nausea and vomiting associated with initial and repeat courses of emetogenic cancer therapy (including high dose cisplatin), postoperation, and radiation (including total body irradiation and daily fractionated abdominal radiation).', 'Granisetron is a potent, selective antagonist of 5-HT<sub>3</sub> receptors. The antiemetic activity of the drug is brought about through the inhibition of 5-HT3 receptors present both centrally (medullary chemoreceptor zone) and peripherally (GI tract). This inhibition of 5-HT3 receptors in turn inhibits the visceral afferent stimulation of the vomiting center, likely indirectly at the level of the area postrema, as well as through direct inhibition of serotonin activity within the area postrema and the chemoreceptor trigger zone.', 'Granisetron is a selective inhibitor of type 3 serotonergic (5-HT<sub>3</sub>) receptors. Granisetron has little or no affinity for other serotonin receptors, including 5-HT 1 , 5-HT 1A , 5-HT 1B/C , or 5-HT 2 ; for alpha 1 -, alpha 2 -, or beta-adrenoreceptors; for dopamine D 2 receptors; for histamine H 1 receptors; for benzodiazepine receptors; for picrotoxin receptors; or for opioid receptors. In most human studies, granisetron has had little effect on blood pressure, heart rate, or electrocardiogram (ECG). The drug is structurally and pharmacologically related to ondansetron, another selective inhibitor of 5-HT<sub>3</sub> receptors. The serontonin 5-HT<sub>3</sub> receptors are located on the nerve terminals of the vagus in the periphery, and centrally in the chemoreceptor trigger zone of the area postrema. The temporal relationship between the emetogenic action of emetogenic drugs and the release of serotonin, as well as the efficacy of antiemetic agents suggest that chemotherapeutic agents release serotonin from the enterochromaffin cells of the small intestine by causing degenerative changes in the GI tract. The serotonin then stimulates the vagal and splanchnic nerve receptors that project to the medullary vomiting center, as well as the 5-HT3 receptors in the area postrema, thus initiating the vomiting reflex, causing nausea and vomiting.', 'DrugBank', 0, 0, 0);
