INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12624, 'Wincef 1 gm i.m. / i.v. vial', 'وينسيف 1جرام فيال حقن عضل/وريد', '94', '65.5', 'Antibiotic cephalosporin fourth generation', 'Cefepime', 'T3a pharma >adwia', 'Vial', 'فيال', '1 gm', '1', 'مضاد حيوي', NULL, '6223002977468', NULL, 4255, '2024-09-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Cefepime is a fourth-generation cephalosporin antibiotic developed in 1994. Cefepime is active against Gram-positive and Gram-negative bacteria, and has greater activity against both compared to third-generation antibiotics.[A2457,A249050] Cefepime is normally used to treat severe nosocomial pneumonia and infections caused by multi-resistant microorganisms such as _Pseudomonas aeruginosa_, and is also indicated for the empirical treatment of febrile neutropenia.[A249050] The popularity of its third-generation predecessors, its clinical efficacy, and the high prevalence of multidrug-resistant bacteria might be some of the factors leading to an increase in the use of cefepime. The activity of cefepime against Enterobacteriaceae, _Pseudomonas aeruginosa_, and _Staphylococcus aureus_ is due to its high stability toward beta-lactamases.[A249050] In general, cefepime seems to be well tolerated; however, patients treated with this antibiotic, especially those with renal impairment, may develop neurotoxicity.[A249050,L42095,L42100]', '["J01DE01", "J01RA06"]', '[]');
