INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14183, 'Angitect 60mg 20 f.c.tab.', 'انجيتكت 60 مجم 20 قرص', '12', NULL, 'Antihypertensive.nonselective calcium channel blocker', 'Diltiazem', 'Marcyrl co.', 'Tab', 'أقراص', '60mg', '2', NULL, 'about diltiazem a benzothiazepine derivative calcium channel blocker anti hypertensive anti-anginal class iv antiarrhythmic. mechanism of action of diltiazem diltiazem is a benzothiazepine; ca channel blocker. it exerts it`s antianginal antihypertensive a', NULL, NULL, 1896, '2022-07-09', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diltiazem is a benzothiazepine derivative with antihypertensive and vasodilating properties. Approved in 1982 by the FDA, it is a member of the non-dihydropyridine calcium channel blockers drug class. It works through various mechanisms of action, but it primarily works by inhibiting the calcium influx into cardiac and vascular smooth muscle during depolarization.[L10556] Compared to dihydropyridine drugs, such as [nifedipine], that preferentially act on vascular smooth muscle and [verapamil] that directly acts on the heart muscle, diltiazem displays an intermediate specificity to target both the cardiac and vascular smooth muscle.[T28] Being a potent vasodilator, diltiazem is used clinically as an antihypertensive, anti-arrhythmic, and as an anti-anginal agent [L6289] for the management of cardiovascular conditions such as hypertension, chronic stable angina, atrial fibrillation, atrial flutter. Apart from its main FDA-approved indications, diltiazem has also been used for numerous off-label indications, such as anal fissures (in topical formulations), migraine prophylaxis, pulmonary hypertension, and rest-related cramps in the lower extremities.[L6289] Typically available in extended-release oral and intravenous formulations, diltiazem is marketed under various brand names with Cardizem and Tiazac being the most common ones.', '["C08DB01", "C05AE03"]', '[]');
