INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14287, 'Apexidone 2mg 20 f.c.tab.', 'ابيكسيدون 2مجم 20 قرص', '75', NULL, 'Psychiatric.antipsychotics', 'Risperidone', 'Multi-apex', 'Tab', 'أقراص', '2mg', '2', NULL, 'about risperidone second-generation antipsychotic benzisoxazole derivative antimanic atypical antipsychotic. mechanism of action of risperidone risperidone exerts its antipsychotic activity by blocking dopamine and serotonin (5ht2) receptors. the drug has', '6223003200879', NULL, 1414, '2022-12-02', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Risperidone is a second-generation antipsychotic (SGA) medication used in the treatment of a number of mood and mental health conditions including schizophrenia and bipolar disorder.[L12885] It is one of the most widely used SGAs. [Paliperidone], another commonly used SGA, is the primary active metabolite of risperidone (i.e. 9-hydroxyrisperidone).[L12885]

Schizophrenia and various mood disorders are thought to be caused by an excess of dopaminergic D2 and serotonergic 5-HT2A activity, resulting in overactivity of central mesolimbic pathways and mesocortical pathways, respectively. Risperidone is thought to reduce this overactivity through inhibition of dopaminergic D2 receptors and serotonergic 5-HT2A receptors in the brain.[L12885]

Risperidone binds with a very high affinity to 5-HT2A receptors, approximately 10-20 fold greater than the drug''s binding affinity to D2 receptors, and carries lesser activity at several off-targets which may responsible for some of its undesirable effects.[L12885]', '["N05AX08"]', '[]');
