INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14739, 'Averopreg 100mg/5ml syrup 60 ml', 'افيروبريج 100 مجم / 5 مل شراب 60 مل', '56', NULL, 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Averroes pharma-egypt', 'Syrup', 'شراب', '100mg', '1', NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', NULL, NULL, 780, '2022-07-29', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pregabalin is structurally similar to gamma-aminobutyric acid (GABA) - an inhibitory neurotransmitter.[A173995] It may be used to manage neuropathic pain, postherpetic neuralgia, and fibromyalgia among other conditions.[A187190] Although as per the FDA Label the mechanism of action has not been definitively defined, there is evidence that pregabalin exerts its effects by binding to the α2δ subunit of voltage-dependent calcium channels.[A187190][L7066] Pregabalin is marketed by Pfizer under the trade name Lyrica and Lyrica Cr (extended release).[L1006][L7066] It may have dependence liability if misused but the risk appears to be highest in patients with current or past substance use disorders.[A31161]', '["N03AX16"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14740, 'Mannitol 20% (mottahedoon) i.v. inf. 500 ml (rubber cap)', 'مانيتول 20% المتحدون محلول وريدي 500 مل بغطاء مطاطي', '58', '32', 'Osmotic diuretic', 'Mannitol', 'Al mottahedoon pharma', 'Cap', 'كبسولة', '20%', '1', NULL, NULL, NULL, NULL, 769, '2024-09-30', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Mannitol is an osmotic diuretic that is metabolically inert in humans and occurs naturally, as a sugar or sugar alcohol, in fruits and vegetables. Mannitol elevates blood plasma osmolality, resulting in enhanced flow of water from tissues, including the brain and cerebrospinal fluid, into interstitial fluid and plasma. As a result, cerebral edema, elevated intracranial pressure, and cerebrospinal fluid volume and pressure may be reduced. Mannitol may also be used for the promotion of diuresis before irreversible renal failure becomes established; the promotion of urinary excretion of toxic substances; as an Antiglaucoma agent; and as a renal function diagnostic aid.

On October 30, 2020, mannitol was approved by the FDA as add-on maintenance therapy for the control of pulmonary symptoms associated with cystic fibrosis in adult patients and is currently marketed for this indication under the name BRONCHITOL® by Chiesi USA Inc.[L20024]', '["B05CX04", "A06AD16", "R05CB16", "B05BC01", "V04CX04"]', '[]');
