INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (14827, 'Azopt 1% eye drops 5 ml', 'ازوبت 1% قطرة 5 مل', '57', NULL, 'Antiglaucoma', 'Brinzolamide', 'Novartis > novartis scientific office', 'Drops', 'نقط', '1%', '1', NULL, 'about brinzolamide a carbonic anhydrase(||) enzyme inhibitor antiglaucoma. mechanism of action of brinzolamide brinzolamide is a carbonic anhydrase enzyme inhibitor.it inhibits the enzyme present in the ciliary body epithelium of eye and reduces the forma', '7612797425640', NULL, 1594, '2022-12-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Brinzolamide is a highly specific, non-competitive, reversible carbonic anhydrase inhibitor. Carbonic anhydrase (CA) is an enzyme found in many tissues of the body including the eye. It catalyzes the reversible reaction involving the hydration of carbon dioxide and the dehydration of carbonic acid. In humans, carbonic anhydrase exists as a number of isoenzymes, the most active being carbonic anhydrase II (CA-II). Inhibition of carbonic anhydrase in the ciliary processes of the eye decreases aqueous humor secretion, presumably by slowing the formation of bicarbonate ions with subsequent reduction in sodium and fluid transport. The result is a reduction in intraocular pressure. Brinzolamide is indicated in the treatment of elevated intraocular pressure in patients with ocular hypertension or open-angle glaucoma.', '["S01EC54", "S01EC04", "G01AE10"]', '[]');
