INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vidilol 12.5 mg 10 tab', 29287, 'فيديلول 12.5مجم 10 اقراص', '8', NULL, 'Antihypertensive.beta blocker.unselective', 'Antihypertensive.beta blocker.unselective', NULL, 'Carvedilol', 'Memphis', 'Tab', 'أقراص', '12.5 mg', '1', NULL, NULL, NULL, NULL, 824, '2022-07-29', NULL, 0, 'Carvedilol is indicated to treat mild to severe heart failure, left ventricular dysfunction after myocardial infarction with ventricular ejection fraction ≤40%, or hypertension.[L7889,L7892]', 'Carvedilol inhibits exercise induce tachycardia through its inhibition of beta adrenoceptors.[A182306] Carvedilol''s action on alpha-1 adrenergic receptors relaxes smooth muscle in vasculature, leading to reduced peripheral vascular resistance and an overall reduction in blood pressure.[A1929,A182306] At higher doses, calcium channel blocking and antioxidant activity can also be seen.[A1929] The antioxidant activity of carvedilol prevents oxidation of low density lipoprotein and its uptake into coronary circulation.[A1929]', 'Carvedilol reduces tachycardia through beta adrenergic antagonism and lowers blood pressure through alpha-1 adrenergic antagonism.[L7889,L7892] It has a long duration of action as it is generally taken once daily and has a broad therapeutic index as patients generally take 10-80mg daily.[L7889,L7892] Patients taking carvedilol should not abruptly stop taking this medication as this may exacerbate coronary artery disease.[L7889,L7892]', 'DrugBank');
