INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vilazoprine 40 mg 10 tabs', 29316, 'فيلازوبرين 40مجم 10 اقراص', '89', NULL, 'Antidepressant.selective serotonin reuptake inhibitor', 'Antidepressant.selective serotonin reuptake inhibitor', NULL, 'Vilazodone', 'Eva pharma > horus for pharmaceutical products', 'Tab', 'أقراص', '40 mg', '1', NULL, NULL, NULL, NULL, 715, '2022-07-30', NULL, 0, 'Vilazodone is approved for treatment of major depressive disorder.[Label,A38477,A177622]', 'Vilazodone selectively inhibits serotonin reuptake in the central nervous system as well as acting as a partial agonist of 5HT-1A receptors[Label,A38477]. The exact mechanism for how these effects translate to its antidepressant effects are not known[Label], though there is an association between these effects and antidepressive activity[A6947].', 'Vilazodone increases serotonin levels in the brain by inhibiting the reuptake of serotonin while acting as a partial agonist on serotonin-1A receptors[Label,A38477,A6947]. Due to this activity vilazodone has sometimes been referred to as a selective partial agonist and reuptake inhibitor (SPARI)[Label,A6947].', 'DrugBank');
