INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Aranesp 15mcg 1 pref. syringe', 14354, 'ارانسب 15 مكجم سرنجة معبأة', '250', '250', 'Hematopoietic.erythropoiesis-stimulating agent (esa)', 'Hematopoietic.erythropoiesis-stimulating agent (esa)', NULL, 'Darbepoetin alfa', 'Amgen > multipharma', 'Syringe', 'حقنة', '15mcg', '1', NULL, NULL, 'darbepoetin alfa is a synthetic form of erythropoietin. it stimulates erythropoiesis (increases red blood cell levels) and is used to treat anemia commonly associated with chronic renal failure and cancer chemotherapy.', NULL, 1227, '2024-11-08', NULL, 0, 'For the treatment of anemia (from renal transplants or certain HIV treatment)', 'Darbepoetin alfa stimulates erythropoiesis by the same mechanism as endogenous erythropoietin. Erythropoietin interacts with

progenitor stem cells to increase red cell production. Binding of erythropoietin to the erythropoietin receptor leads to receptor dimerization, which facilitates activation of JAK-STAT signaling pathways within the cytosol. Activated STAT (signal transducers and activators of transcription) proteins are then translocated to the nucleus where they serve as transcription factors which regulate the activation of specific genes involved in cell division or differentiation.', 'Darbepoetin alfa is used in the treatment of anemia. It is involved in the regulation of erythrocyte differentiation and the maintenance of a physiological level of circulating erythrocyte mass.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Salmocalcin 50 i.u./ml amp.', 26485, 'سالموكالسين 50 وحدة امبول', '46', NULL, 'Osteoporosis.antiresorptives', 'Osteoporosis.antiresorptives', NULL, 'Calcitonin', 'Sigma tec', 'Amp', 'أمبول', NULL, '1', NULL, NULL, NULL, NULL, 695, '2022-07-16', NULL, 0, 'Investigated for use/treatment in myocardial infarction, heart disease, and asthma.', 'While current drugs for treating HF and MI have a single mechanism of action, CGRP appears to work on several levels: for example, it (1) increases local blood flow enhancing hemodynamic proficiency; (2) protects heart muscle cells from the damage due to lack of oxygen (ischemia); and, (3) modulates the immune system by boosting anti-inflammatory cytokine expression while suppressing pro-inflammatory cytokines, which reduces the inflammatory response to provide a cardioprotective effect. All these actions are meant to minimize heart muscle damage and subsequent scar tissue formation, while promoting the healing process.', NULL, 'DrugBank');
