INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Megaflow gel 60 gm tube', 32211, 'ميجافلو جل انبوبة 60 جم', '140', '120', 'Hydrogel', 'Hydrogel', NULL, 'Medical grade honey+cmc+lanolin+vit c+vit e', 'Smartec > omegamax', 'Gel', 'جل', '60 gm', '1', 'ملطف ومرطب للجلد', NULL, NULL, NULL, 4404, '2025-07-10', NULL, 0, 'For use as a source of clean air [FDA Label].', 'Air is approximately 21% oxygen which enters the body through the lungs, crossing the alveolar membrane to reach systemic circulation [T36]. One there it is bound by hemoglobin and transported to tissues thoughout the body where it is used as a terminal electron acceptor in oxidative phosphorylation. This allows efficient generation of adenosine triphosphate, the primary storage molecule for energy, in the mitochondria.', 'Acts as a source of clean air to support respiration [FDA Label].', 'DrugBank');
