INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7479, 'Locasten 1% cream 20 gm', 'لوكاستن كريم 20 جرام', '29', '19', 'Antifungals.imidazoles', 'Clotrimazole', 'Alexandria', 'Cream', 'كريم', '1%', '1', 'مضاد فطريات', 'how does it work? clotrimazole cream 1% contains the active ingredient clotrimazole which is an antifungal medicine used to treat infections with fungi and yeasts. mechanism: clotrimazole kills fungi and yeasts by interfering with their cell membranes. it', '6223000173169', NULL, 3335, '2024-10-09', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'This drug is a broad spectrum antimycotic or antifungal agent. Clotrimazole''s antimycotic properties were discovered in the late 1960s [A174094]. Clotrimazole falls under the _imidazole_ category of _azole_ antifungals, possessing broad-spectrum antimycotic activity [A174094].  It is available in various preparations, including creams, pessaries, and troche formulations (slowly dissolving tablets).  As well as its antifungal activity, clotrimazole has become a drug of interest in treating several other diseases such as sickle cell disease, malaria and some cancers [A174094].  The minimal side effect profile of this drug and its uncomplicated metabolic profile have led it to gain widespread acceptance for the treatment of mycotic outbreaks such as vaginal yeast infections as well as athlete''s foot [A174097].', '["D01AC01", "A01AB18", "G01AF02", "G01AF20"]', '[]');
