INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7923, 'Memexa 10 mg 20 f.c.tabs.', 'ميمكسا 10مجم 20 قرص', '158', '118', 'Dopamine d2 agonist', 'Memantine', 'Marcyrl co. > copad pharma', 'Tab', 'أقراص', '10 mg', '2', NULL, NULL, '6224008933052', NULL, 2130, '2025-12-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Initially approved by the FDA in 2013, memantine is an N-methyl-D-aspartate (NMDA) receptor antagonist used in the management of Alzheimer''s Disease (AD).  It is different from many other Alzheimer''s Disease medications, as it works by a different mechanism than the cholinesterase enzyme inhibitors normally employed in the management of Alzheimer''s disease [A1639].  Memantine blocks the effects of glutamate, a neurotransmitter in the brain that leads to neuronal excitability and excessive stimulation in Alzheimer''s Disease [T556].

