INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7511, 'Vocodam ear drop 15 ml', 'فوكودام نقط اذن 15 مل', '35', NULL, 'Soothing', 'Hydrogen peroxide+urea+tea tree oil+garlic oil+coconut oil+panthnol+glycerin', 'Dam pharm', 'Drop', 'نقط', '15 ml', '1', NULL, NULL, NULL, NULL, 1421, '2025-09-23', 0, 'Indicated to be used as a disinfectant and sterilizer. ', 'The production of free hydroxyl radicals in the Fenton reaction is thought to be the basis of biocidal actions of hydrogen peroxide. Free radicals eventually lead to oxidative damage proteins and membrane lipids _in vivo_ [A32371]. The oxidizing radical as the ferryl radical induces DNA oxidation [A32371].', 'Hydrogen peroxide exhibits antimicrobial properties against most forms of microorganisms, including dormant forms with known high resistance profiles, such as bacterial spores and protozoal cysts. It acts as an oxidative biocide to generate free radical species to induce DNA, protein and membrane lipid damage via oxidation. ', 'DrugBank', 0, 0, 0);
