INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (15841, 'Cardio mep 150mg/3ml 6 i.v. amp.', 'كارديو ميب 150 مجم / 3 مل 6 امبولات', '78', '54', 'Antiarrhythmias.class iii', 'Amiodarone', 'Mepaco', 'Amp', 'أمبول', '150mg', '6', NULL, 'description an antianginal and antiarrhythmic drug. it increases the duration of ventricular and atrial muscle action by inhibiting na k-activated myocardial adenosine triphosphatase. there is a resulting decrease in heart rate and in vascular resistance.', '6223002216031', NULL, 900, '2024-11-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Amiodarone is a benzofuran derivative, anti-arrhythmic drug used commonly in a variety of settings.[A36817] Most known for its approved indication in life-threatening ventricular arrhythmias, it is also used off-label in the outpatient and inpatient setting for atrial fibrillation. Because of its ability to cause serious toxicity and possibly death, amiodarone use should be reserved for its approved indications, according to prescribing information.[L3561,L11265,L11286]', '["C01BD01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (15842, 'Cardiocare 2.5mg 60 s.r. capsules', 'كارديوكير 2.5مجم 60 كبسولة', '15', NULL, 'Anti-ischemic', 'Nitroglycerin', 'Arab caps > acdima international trading', 'Capsule', 'كبسولة', '2.5mg', '1', 'للذبحة الصدرية', NULL, NULL, NULL, 11330, '2023-02-02', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Nitroglycerin, also known as glyceryl trinitrate,[A180169] is an organic nitrate and a vasodilating agent [L38369] that was first discovered in 1847.[A249965] Originally used to dynamite, its antianginal effects were identified in the late 1860s after it produced headaches in factory workers while workers with angina pectoris or heart failure experienced relief from chest pain.[A180166,A180172] Its use as a treatment for angina dates back to 1879 and is still used to treat and prevent angina.[A249970] Nitroglycerin causes vasodilation in both arteries and veins.[A180172]

Nitroglycerin is used in a variety of different conditions, including angina pectoris due to coronary artery disease,[L7102,L7141,L38369,L42320] peri-operative hypertension, congestive heart failure,[L7099] and chronic anal fissure.[L42445] It is also used to induce intraoperative hypotension.[L7099]', '["C01DA02", "C01DA52", "C05AE01"]', '[]');
