INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Megadroxil 250mg/5ml oral susp. 60 ml', 22469, 'ميجادروكسيل مضاد حيوي 250مجم/5مل شراب 60مل', '11', NULL, 'Antibiotic.cephalosporin.first-generation', 'Antibiotic.cephalosporin.first-generation', NULL, 'Cefadroxil', 'Mash premiere', 'Susp', 'معلق', '250mg', '1', NULL, NULL, 'about cefadroxil first generation cephalosporin para-hydroxy derivative of cefalexin oral broad-spectrum antibiotic mechanism of action cefadroxil is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram neg', NULL, 805, '2022-07-16', NULL, 0, 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank');
