INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cefaxoral 500mg 8 caps.', 16000, 'سيفاكسورال 500 مجم 8 اقراص', '10', NULL, 'Antibiotic.cephalosporin.first-generation', 'Antibiotic.cephalosporin.first-generation', NULL, 'Cefadroxil', 'T3a pharma', 'Cap', 'كبسولة', '500mg', '1', NULL, NULL, NULL, NULL, 811, '2022-07-24', NULL, 0, 'For the treatment of the following infections (skin, UTI, ENT) caused by; <i>S. pneumoniae, H. influenzae, staphylococci, S. pyogenes</i> (group A beta-hemolytic streptococci), <i>E. coli, P. mirabilis, Klebsiella</i> sp, coagulase-negative staphylococci and <i>Streptococcus pyogenes</i>', 'Like all beta-lactam antibiotics, cefadroxil binds to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, causing the inhibition of the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefadroxil interferes with an autolysin inhibitor.', 'Cefadroxil, a first-generation cephalosporin antibiotic, is used to treat urinary tract infections, skin and skin structure infections, pharyngitis, and tonsillitis.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Jadenu 180mg 30 f.c. tabs.', 21118, 'جادينو 180 مجم 30 قرص', '4,640', '3680', 'Iron chelating agents', 'Iron chelating agents', NULL, 'Deferasirox', 'Novartis', 'Tab', 'أقراص', '180mg', '3', NULL, NULL, 'about deferasirox a synthetic achiral tridentate triazole derived from salicylic acid oral iron chelating agents. indication for the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and', NULL, 1509, '2024-06-04', NULL, 0, 'For the treatment of chronic iron overload due to blood transfusions (transfusional hemosiderosis) in patients 2 years of age and older.', 'Two molecules of deferasirox are capable of binding to 1 atom of iron. Deferasirox works in treating iron toxicity by binding trivalent (ferric) iron (for which it has a strong affinity), forming a stable complex which is eliminated via the kidneys.', 'Deferasirox is an orally active chelator that is selective for iron (as Fe3+). It is a tridentate ligand that binds iron with high affinity in a 2:1 ratio. Although deferasirox has very low affinity for zinc and copper there are variable decreases in the serum concentration of these trace metals after the administration of deferasirox. The clinical significance of these decreases is uncertain.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cefbiolor 250 mg 10 caps.', 16003, 'سيفبيولور 250 مجم 10 كبسولات', '16', NULL, 'Antibiotic.cephalosporin.second-generation', 'Antibiotic.cephalosporin.second-generation', NULL, 'Cefaclor', 'Sigma > sabaa', 'Cap', 'كبسولة', '250 mg', '1', NULL, NULL, 'about cefaclor a second-generation cephalosporin antibiotic oral broad-spectrum antibiotics. mechanism of action cefaclor is a 2nd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibi', NULL, 814, '2022-07-18', NULL, 0, 'For the treatment of certain infections caused by bacteria such as pneumonia and ear, lung, skin, throat, and urinary tract infections.', 'Cefaclor, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins. It is possible that cefaclor interferes with an autolysin inhibitor.', 'Cefaclor is a second generation cephalosporin antibiotic with a spectrum resembling first-generation cephalosporins. <i>In vitro</i> tests demonstrate that the bactericidal action of the cephalosporins results from inhibition of cell-wall synthesis. As indicated by _in vitro_ and _in vivo_ clinical studies, cefaclor was shown to be effective against most strains of Gram positive aerobes - Staphylococci (including coagulase-positive, coagulase-negative, and penicillinase-producing strains), <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i> (group A &szlig;-hemolytic streptococci), as well as Gram-negative aerobes - <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including &szlig;-lactamase-producing ampicillin-resistant strains), <i>Klebsiella sp</i>, and <i>Proteus mirabilis</i>.', 'DrugBank');
