INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Trusty soap 100 gm', 28596, 'تراستي صابون 100 جرام لحب الشباب', '50', '35.95', 'Anti-acne', 'Anti-acne', NULL, 'Salicylic acid+lemon oil+zinc acetate+tea tree oil+panthenol+lavender oil+liquorice+triclosan+titani', 'Egyptian company for cosmetics > pearla pharm co.', 'Soap', 'صابون', '100 gm', '1', 'لعلاج حالات حب الشباب بالوجه', NULL, 'for all forms of acne.', NULL, 1249, '2023-12-23', NULL, 0, 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ampicillin-el nasr 500 mg vial', 14030, 'امبيسللين 500مجم فيال', '3', NULL, 'Penicillins.monocomponent', 'Penicillins.monocomponent', NULL, 'Ampicillin', 'El nasr', 'Amp', 'أمبول', '500 mg', '1', NULL, NULL, 'description : semi-synthetic derivative of penicillin that functions as an orally active broad-spectrum antibiotic . indication : for treatment of infection (respiratory gi uti and meningitis) due to e. coli p. mirabilis enterococci shigella s. typhosa an', NULL, 940, '2022-10-29', NULL, 0, 'For treatment of infection (Respiratory, GI, UTI and meningitis) due to E. coli, P. mirabilis, enterococci, Shigella, S. typhosa and other Salmonella, nonpenicillinase-producing N. gononhoeae, H. influenzae, staphylococci, streptococci including streptoc', 'By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, Ampicillin inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that Ampicillin interferes with an autolysin inhibitor.', 'Ampicillin is a penicillin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms. The name "penicillin" can either refer to several variants of penicillin available, or to the group of antibiotics derived from the penicillins. Ampicillin has <i>in vitro</i> activity against gram-positive and gram-negative aerobic and anaerobic bacteria. The bactericidal activity of Ampicillin results from the inhibition of cell wall synthesis and is mediated through Ampicillin binding to penicillin binding proteins (PBPs). Ampicillin is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended spectrum beta-lactamases.', 'DrugBank');
