INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tenocam 20mg 10 caps.', 28014, 'تينوكام 20مجم 10 كبسولات', '9', NULL, 'Nsaid.oxicam', 'Nsaid.oxicam', NULL, 'Tenoxicam', 'Kahira', 'Cap', 'كبسولة', '20mg', '1', NULL, NULL, 'mechanism of action of tenoxicam the anti-inflammatory effects of tenoxicam may result from the inhibition of the enzyme cyclooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis (prostaglandins sensitize pain receptors their inhi', '6221068101292', 1099, '2022-12-31', NULL, 0, 'For the treatment of rheumatoid arthritis, osteoarthritis, backache, and pain.', 'The antiinflammatory effects of tenoxicam may result from the inhibition of the enzyme cycooxygenase and the subsequent peripheral inhibition of prostaglandin synthesis. As prostaglandins sensitize pain receptors, their inhibition accounts for the peripheral analgesic effects of tenoxicam. Antipyresis may occur by central action on the hypothalamus, resulting in peripheral dilation, increased cutaneous blood flow, and subsequent heat loss.', 'Tenoxicam, an antiinflammatory agent with analgesic and antipyretic properties, is used to treat osteoarthritis and control acute pain.', 'DrugBank');
