INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nizatine 300mg 20 caps.', 23649, 'نيزاتين 300مجم 20 كبسولة', '30', NULL, 'Peptic ulcer.anti-histamine.h2 antagonist', 'Peptic ulcer.anti-histamine.h2 antagonist', NULL, 'Nizatidine', 'Hi-pharm', 'Cap', 'كبسولة', '300mg', '2', NULL, NULL, NULL, '6224001050336', 774, '2023-01-08', NULL, 0, 'For the treatment of acid-reflux disorders (GERD), peptic ulcer disease, active benign gastric ulcer, and active duodenal ulcer.', 'Nizatidine competes with histamine for binding at the H<sub>2</sub>-receptors on the gastric basolateral membrane of parietal cells. Competitive inhibition results in reduction of basal and nocturnal gastric acid secretions. The drug also decreases the gastric acid response to stimuli such as food, caffeine, insulin, betazole, or pentagastrin.', 'Nizatidine is a competitive, reversible inhibitor of histamine at the histamine H2-receptors, particularly those in the gastric parietal cells. By inhibiting the action of histamine on stomach cells, nizatidine reduces stomach acid production. Nizatidine had no demonstrable antiandrogenic action. Full-dose therapy for the problems treated by nizatidine lasts no longer than 8 weeks. It has been demonstrated that treatment with a reduced dose of nizatidine is effective as maintenance therapy following healing of active duodenal ulcers.', 'DrugBank');
