INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ringer (el nasr) i.v. inf. 500 ml (polypropylene bag)', 26127, 'رينجر النصر محلول وريدي 500 مل (بولي بروبيلين باج)', '14', NULL, 'Ringer', 'Ringer', NULL, 'Calcium chloride+potassium chloride+sodium chloride', 'El nasr', 'Unknown', 'غير محدد', '500 ml', '1', NULL, NULL, 'description ringerï-s injection usp is a sterile nonpyrogenic solution for fluid and electrolyte replenishment in single dose containers for intravenous administration. it contains no antimicrobial agents. the ph may have been adjusted with sodium hydroxi', '6223003950354', 1025, '2024-06-14', NULL, 0, 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Shatbiotic 90mg/5ml pd for oral susp. 60 ml', 26913, 'شاتبيوتك 90مجم/5مل شراب معلق 60مل', '36', NULL, 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Ceftibuten', 'Biomed pharmaceuticals', 'Susp', 'معلق', '90mg', '1', NULL, NULL, 'ceftibuten is a third-generation cephalosporin antibiotic. it is an orally administered agent with two dosage forms capsule or oral suspension. indications ceftibuten is used to treat acute bacterial exacerbations of chronic bronchitis (abecb) acute bacte', NULL, 660, '2022-08-02', NULL, 0, 'Indicated for the treatment of acute bacterial exacerbations of chronic bronchitis (ABECB), acute bacterial otitis media, pharyngitis, and tonsilitis.', 'Ceftibuten exerts its bactericidal action by binding to essential target proteins of the bacterial cell wall. This binding leads to inhibition of cell-wall synthesis.', 'Ceftibuten is an antibiotic with bactericidal actions. ', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zarotex 0.1% gel 15 gm', 12786, 'زاروتكس جل 15 جرام', '25', NULL, 'Psoriasis', 'Psoriasis', NULL, 'Tazarotene', 'Delta pharma > andalous pharma', 'Gel', 'جل', '0.1%', '1', 'جل يساعد على تجديد خلايا البشرة وتقشير الجلد، ويستخدم فى علاج حب الشباب والصدفية', NULL, 'indication used to treat psoriasis acne and sun damaged skin (photodamage). mechanism of action although the exact mechanism of tazarotene action is not known studies have shown that the active form of the drug (tazarotenic acid) binds to all three member', '6224000396077', 3336, '2023-01-23', NULL, 0, 'Used to treat psoriasis, acne and sun damaged skin (photodamage).', 'Although the exact mechanism of tazarotene action is not known, studies have shown that the active form of the drug (tazarotenic acid) binds to all three members of the retinoic acid receptor (RAR) family: RARa, RARb, and RARg, but shows relative selectivity for RARb, and RARg and may modify gene expression. It also has affinity for RXR receptors.', 'Following topical application, tazarotene undergoes esterase hydrolysis to form its active metabolite, tazarotenic acid. When treating acne tazarotene may be taken in conjunction with an oral antibiotic. Tazarotene has been shown in peer-reviewed double blinded studies to reduce: mottling and hyperpigmentation, sallowness, fine wrinkling and coarse wrinkling in sun damaged skin. Histological studies have shown that long term (greater than 1 year) use of Tazarotene is associated with a significant reduction in atypical melanocytes and keratocytes - cells considered to be precursors of skin cancer. Some studies have shown long term use of Tazarotene to be associated with increased collagen production and better organization of skin collagen bundles.', 'DrugBank');
