INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8160, 'Midodrine 1% oral drops 10ml', 'ميدودرين 1% نقط فم 10 مل', '67', '51.5', 'Alpha1-agonist', 'Midodrine', 'El nile.', 'Drops', 'نقط', '1%', '1', NULL, NULL, '6221077062317', NULL, 2407, '2024-11-09', 0, 'For the treatment of symptomatic orthostatic hypotension (OH).', 'Midodrine undergoes metabolism to form its pharmacologically active metabolite, desglymidodrine. Desglymidodrine acts as an agonist at the alpha<sub>1</sub>-adrenergic receptors expressed in the arteriolar and venous vasculature. Activation of alpha<sub>1</sub>-adrenergic receptor signaling pathways lead to an increase in the vascular tone and elevation of blood pressure. Desglymidodrine is reported to have negligible effect on the cardiac beta-adrenergic receptors.', 'Midodrine is a prodrug, i.e., the therapeutic effect of orally administered midodrine is due to the major metabolite desglymidodrine formed by deglycination of midodrine. Administration of midodrine results in a rise in standing, sitting, and supine systolic and diastolic blood pressure in patients with orthostatic hypotension of various etiologies. Standing systolic blood pressure is elevated by approximately 15 to 30 mmHg at 1 hour after a 10-mg dose of midodrine, with some effect persisting for 2 to 3 hours. Midodrine has no clinically significant effect on standing or supine pulse rates in patients with autonomic failure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8161, 'Midodrine 2.5mg 20 tab', 'ميدودرين 2.5مجم 20 قرص', '50', '38', 'Orthostatic hypotension', 'Midodrine', 'El nile.', 'Tab', 'أقراص', '2.5mg', '2', 'لحالات ضغط الدم المنخفض', NULL, '6221077014019', NULL, 9252, '2024-11-09', 0, 'For the treatment of symptomatic orthostatic hypotension (OH).', 'Midodrine undergoes metabolism to form its pharmacologically active metabolite, desglymidodrine. Desglymidodrine acts as an agonist at the alpha<sub>1</sub>-adrenergic receptors expressed in the arteriolar and venous vasculature. Activation of alpha<sub>1</sub>-adrenergic receptor signaling pathways lead to an increase in the vascular tone and elevation of blood pressure. Desglymidodrine is reported to have negligible effect on the cardiac beta-adrenergic receptors.', 'Midodrine is a prodrug, i.e., the therapeutic effect of orally administered midodrine is due to the major metabolite desglymidodrine formed by deglycination of midodrine. Administration of midodrine results in a rise in standing, sitting, and supine systolic and diastolic blood pressure in patients with orthostatic hypotension of various etiologies. Standing systolic blood pressure is elevated by approximately 15 to 30 mmHg at 1 hour after a 10-mg dose of midodrine, with some effect persisting for 2 to 3 hours. Midodrine has no clinically significant effect on standing or supine pulse rates in patients with autonomic failure.', 'DrugBank', 0, 0, 0);
