INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8243, 'Mitoxantrone 20 mg conc. for i.v. inf.(n/a)', 'ميتوكسانترون 2مجم/مل مركز للحقن الوريدي', '460', NULL, 'Antineoplastic', 'Mitoxantrone', 'Sandoz > b.p pharma', 'Unknown', 'غير محدد', '20 mg', '1', 'علاج الاورام', 'about mitoxantrone an anthracenedione derivative a type ii topoisomerase inhibitor anti-neoplastic. mechanism of action of mitoxantrone this drug shows its anticancer activity by different mechanisms. drug intercalates the dna cause inter/intra strand cro', NULL, NULL, 1048, '2023-04-27', 0, 'For the treatment of secondary (chronic) progressive, progressive relapsing, or worsening relapsing-remitting multiple sclerosis', 'Mitoxantrone, a DNA-reactive agent that intercalates into deoxyribonucleic acid (DNA) through hydrogen bonding, causes crosslinks and strand breaks. Mitoxantrone also interferes with ribonucleic acid (RNA) and is a potent inhibitor of topoisomerase II, an enzyme responsible for uncoiling and repairing damaged DNA. It has a cytocidal effect on both proliferating and nonproliferating cultured human cells, suggesting lack of cell cycle phase specificity.', 'Mitoxantrone has been shown in vitro to inhibit B cell, T cell, and macrophage proliferation and impair antigen presentation, as well as the secretion of interferon gamma, TNFa, and IL-2.', 'DrugBank', 0, 0, 0);
