INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8346, 'Motilium 1mg/ml susp. 100ml', 'موتيليوم معلق 100 مل', '16', NULL, 'Antiemetic', 'Domperidone', 'Mina pharm > janssen cilag', 'Susp', 'معلق', '1mg', '1', 'مضاد للقئ', 'about domperidone antidopaminergic motility stimulant piperidine derivative antiemetic anti-vertigo. mechanism of action of domperidone domperidone is a potent dopamine receptor antagonist. it acts centrally and blocks the dopamine receptor in the chemore', '6222003701744', NULL, 1579, '2022-12-06', 0, 'For management of dyspepsia, heartburn, epigastric pain, nausea, and vomiting.', 'Domperidone acts as a gastrointestinal emptying (delayed) adjunct and peristaltic stimulant. The gastroprokinetic properties of domperidone are related to its peripheral dopamine receptor blocking properties. Domperidone facilitates gastric emptying and decreases small bowel transit time by increasing esophageal and gastric peristalsis and by lowering esophageal sphincter pressure. Antiemetic: The antiemetic properties of domperidone are related to its dopamine receptor blocking activity at both the chemoreceptor trigger zone and at the gastric level. It has strong affinities for the D2 and D3 dopamine receptors, which are found in the chemoreceptor trigger zone, located just outside the blood brain barrier, which - among others - regulates nausea and vomiting', 'Domperidone is a specific blocker of dopamine receptors. It speeds gastrointestinal peristalsis, causes prolactin release, and is used as antiemetic and tool in the study of dopaminergic mechanisms.', 'DrugBank', 0, 0, 0);
