INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8666, 'Neofloxin 400mg 16 f.c. tab.', 'نيوفلوكسين 400مجم 16 قرص', '43', '32.4', 'Antibiotic.quinolone', 'Norfloxacin', 'Alexandria', 'Tab', 'أقراص', '400mg', '2', NULL, 'about norfloxacin a synthetic second generation synthetic fluoroquinolone (quinolone) broad spectrum antibiotic. mechanism of action of norfloxacin norfloxacin is a bacteriostatic at low concentration and bactericidal at high concentration. it act by inhi', '6223000170557', NULL, 922, '2024-08-30', 0, 'For the treatment of urinary tract infection', 'The bactericidal action of Norfloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination. Norfloxacin is a broad-spectrum antibiotic agent that is shown to be effective against various Gram-positive and Gram-negative bacterial species. The fluorine atom at the 6 position increases potency against gram-negative organisms, and the piperazine moiety at the 7 position is responsible for anti-pseudomonal activity', 'Norfloxacin is a quinolone/fluoroquinolone antibiotic. Norfloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian.', 'DrugBank', 0, 0, 0);
