INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (16093, 'Cellcept 250 mg 100 caps.', 'سيلسيبت 250 مجم 100 كبسولة', '1,460', '910', 'Immunosuppressants', 'Mycophenolate mofetil', 'F.hoffman la roche', 'Cap', 'كبسولة', '250 mg', '1', NULL, 'indication for the prophylaxis of organ rejection in patients receiving allogeneic renal cardiac or hepatic transplants. mycophenolate mofetil should be used concomitantly with cyclosporine and corticosteroids. mechanism of action mycophenolate mofetil is', '7640128011569', NULL, 2114, '2024-11-05', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Mycophenolate mofetil, also known as MMF or CellCept, is a prodrug of mycophenolic acid, and classified as a reversible inhibitor of inosine monophosphate dehydrogenase (IMPDH).[A180805] This drug is an immunosuppressant combined with drugs such as [Cyclosporine] and corticosteroids to prevent organ rejection after hepatic, renal, and cardiac transplants.[L7363] It is marketed by Roche Pharmaceuticals and was granted FDA approval for the prophylaxis of transplant rejection in 1995.[A180826] In addition to the above uses, mycophenolate mofetil has also been studied for the treatment of nephritis and other complications of autoimmune diseases. Unlike another immunosuppressant class, the calcineurin inhibitors, MMF generally does not cause nephrotoxicity or fibrosis.[A180799,A180805]

Previously, mycophenolic acid (MPA) was administered to individuals with autoimmune diseases beginning in the 1970s, but was discontinued due to gastrointestinal effects and concerns over carcinogenicity.[A180826] The new semi-synthetic 2-morpholinoethyl ester of MPA was synthesized to avoid the gastrointestinal effects associated with the administration of MPA. It demonstrates an increased bioavailability, a higher efficacy, and reduced gastrointestinal effects when compared to MPA.[A180826]', '[]', '[]');
