INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (9313, 'Optocetine 0.5% eye drops', 'اوبتوسيتين 0.5% قطرة للعين', '4', NULL, 'Antibiotic', 'Chloramphenicol', 'Cid', 'Drops', 'نقط', '0.5%', '1', NULL, 'description an antibiotic first isolated from cultures of streptomyces venequelae in 1947 but now produced synthetically. it has a relatively simple structure and was the first broad-spectrum antibiotic to be discovered. it acts by interfering with bacter', '6221043011578', NULL, 816, '2023-04-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'An antibiotic first isolated from cultures of _Streptomyces venequelae_ in 1947 but now produced synthetically. It has a relatively simple structure and was the first broad-spectrum antibiotic to be discovered. It acts by interfering with bacterial protein synthesis and is mainly bacteriostatic. (From Martindale, The Extra Pharmacopoeia, 29th ed, p106)

