INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17265, 'Depanx 20mg 10 f.c. tab.', 'ديبانكس 20 مجم 10 قرص', '24', NULL, 'Psychiatric.antidepressants', 'Paroxetine', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '20mg', '1', NULL, 'about paroxetinesa selective serotonin reuptake inhibitor (ssri) anti-depressant.smechanism of action of paroxetinesparoxetine blocks the re-uptake of neurotransmitter serotonin from the synapse in to the presynaptic nerve terminal in the cns. this will p', NULL, NULL, 919, '2022-07-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Paroxetine is a selective serotonin reuptake inhibitor (SSRI) drug commonly known as Paxil. It has a variety of uses, including the treatment of anxiety disorders, major depression, posttraumatic stress disorder, and symptoms of menopause, among others.[T653] It was approved by the FDA in the early 1990s and marketed by SmithKline Beecham.[L7712,L7715] A unique feature of this drug is that it is highly potent and selective in its inhibition of serotonin reuptake and has little effect on other neurotransmitters.[A31914] Because of its potent inhibition of serotonin reuptake, paroxetine is more likely to cause withdrawal effects upon cessation. Paroxetine is well tolerated in most patients with a similar adverse effect profile to other members of its drug class.[A31914] The controlled release formulation was designed to decrease the likelihood of nausea that is sometimes associated with paroxetine.[L7700,L7742]', '["N06AB05"]', '[]');
