INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18123, 'Egynir 300mg 10 cap', 'ايجينير 300مجم 10 كبسولات', '100', '69.5', 'Antibiotic.cephalosporin.third-generation', 'Cefdinir', 'Sigma tec pharmaceutical industries > egyphar', 'Cap', 'كبسولة', '300mg', '1', NULL, NULL, '6224001026690', NULL, 1300, '2025-01-05', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Cefdinir, also known as Omnicef, is a semi-synthetic, broad-spectrum antibiotic belonging to the third generation of the cephalosporin class. It has been proven to be effective for the treatment of common bacterial infections in the ear, sinus, throat, lungs, and skin. Cefdinir was approved by the FDA in 1997 to treat a variety of mild to moderate infections and was initially marketed by Abbvie.[A180739] Because of its chemical structure, it is effective against organisms that are resistant to first-line cephalosporin therapy due to the production of beta-lactamase enzymes.[A180724,A180727]', '["J01DD15"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18124, 'Egypress 10mcg/dose nasal spray 2.5 ml', 'ايجيبريس 10مكجم سبراي للانف', '75', NULL, 'Urinary incotinence.antidiuretic', 'Desmopressin acetate', 'Sigma > egyptian pharmacists company', 'Spray', 'بخاخ', '10mcg', '1', 'تشخيص وعلاج مرض السكر الكاذب', 'about desmopressin synthetic analogue of natural pituitary hormone 8-arginine vasopressin (adh) antidiuretic hormone in diabetes insipidus hemostatic agent antihemophilic agent. mechanism of action of desmopressin the drug exerts it`s antidiuretic action', NULL, NULL, 1171, '2022-05-19', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Desmopressin (dDAVP), a synthetic analogue of 8-arginine vasopressin (ADH), is an antidiuretic peptide drug modified by deamination of 1-cysteine and substitution of 8-L-arginine by 8-D-arginine. ADH is an endogenous pituitary hormone that has a crucial role in the control of the water content in the body. Upon release from the stimulation of increased plasma osmolarity or decreased circulating blood volume, ADH mainly acts on the cells of the distal part of the nephron and the collecting tubules in the kidney [T28]. The hormone interacts with V1, V2 or V3 receptors with differing signal cascade systems.

Desmopressin displays enhanced antidiuretic potency, fewer pressor effects due to V2-selective actions, and a prolonged half-life and duration of action compared to endogenous ADH [A31661]. It has been employed clinically since 1972 and is available in various formulations including intranasal solution, intravenous solution, oral tablet and oral lyophilisate [A31662]. Desmopressin is indicated for the treatment of polyuric conditions including primary nocturnal enuresis, nocturia, and diabetes insipidus. It was also newly approved for the treatment of mild classical hemophilia and von Willebrand''s disease for minor surgeries. The active ingredient in most formulations is desmopressin acetate. Nocdurna, or desmopressin acetate, was approved by the FDA on June 21st, 2018 for the treatment of nocturia due to nocturnal polyuria in adults. It is available as a sublingual tablet.', '["H01BA02"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (18125, 'Egypro 5 mg 30 tablets', 'ايجيبرو 5 مجم 30 قرص', '63', '40.5', 'Antihypertensive.beta blocker.selective b1', 'Bisoprolol fumarate', 'Egpi', 'Tablet', 'أقراص', '5 mg', '3', 'لعلاج ضغط الدم المرتفع', NULL, '6223003932039', NULL, 11777, '2025-03-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Bisoprolol is a cardioselective β1-adrenergic blocking agent used to treat high blood pressure.[A180472,L7219] It is considered a potent drug with a long-half life that can be used once daily to reduce the need for multiple doses of antihypertensive drugs.[A180472] Bisoprolol is generally well tolerated, likely due to its β1-adrenergic receptor selectivity and is a useful alternative to non-selective β-blocker drugs in the treatment of hypertension such as [Carvedilol] and [Labetalol]. It may be used alone or in combination with other drugs to manage hypertension[L7219] and can be useful in patients with chronic obstructive pulmonary disease (COPD) due to its receptor selectivity.[A180562]', '["C07FX04", "C07BB07", "C07AB07", "C09BX05", "C09BX02", "C09BX04", "C07FB07"]', '[]');
