INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bronchrooz syrup 120 ml', 30237, 'برونكروز شراب 120 مل', '55', '35', 'Anti cough', 'Anti cough', NULL, 'English ivy extract', 'Brand nutrition > gomer pharma', 'Syrup', 'شراب', '120 ml', '1', 'مكمل غذائى يستخدم فى علاج حالات الكحة المصحوبة بالبلغم، حيث يساعد على إذابة البلغم وتخفيف لزوجته، وبالتالي يدعم عمل الجهاز التنفسي وتخفيف اعراض البرد', NULL, 'description: english ivy (hedera helix) each 1 ml syrup contains 4.5 mg. ivy leaf extract are considered to have mucolytic spasmolytic bronchodilatory and anti-inflammatory effects used as an expectorant and to reduce the frequency and intensity of cough', NULL, 1662, '2024-11-27', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vitamin c plus sedico 10 eff. sachets', 29498, 'فيتامين سي بلس سيديكو 10 اكياس فوارة', '16', NULL, 'Vitamins', 'Vitamins', NULL, 'Vitamin c+zinc', 'Sedico', 'Sachet', 'أكياس', NULL, '1', NULL, NULL, 'vitamin c 1000 mg zinc 10 mg', NULL, 1277, '2022-06-18', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nolice 0.4gm/100 ml lotion 60 ml', 23683, 'نولايس 0.4جم/100مل لوشن 60مل', '7', NULL, 'Scabicide', 'Scabicide', NULL, 'Phenothrin', 'Eva pharma', 'Lotion', 'لوشن', '0.4gm', '1', NULL, NULL, 'phenothrin is a chemical that kills insects (an insecticide). it is a man-made (synthetic) chemical similar to the natural insecticides called pyrethrins. phenothrin is one of a group of insecticides known as pyrethroids. it is also used to control scabie', NULL, 738, '2022-08-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Chartoreg 40 mg 28 tab.', 2472, 'شارتوريج 40 مجم 28 قرص', '134', '102', 'Antihypertensive.angiotensin blocker', 'Antihypertensive.angiotensin blocker', NULL, 'Telmisartan', 'Utopia', 'Tab', 'أقراص', '40 mg', '2', NULL, NULL, NULL, '6223012330154', 2997, '2025-06-11', NULL, 0, 'Used alone or in combination with other classes of antihypertensives for the treatment of hypertension.[L44652] Also used in the treatment of diabetic nephropathy in hypertensive patients with type 2 diabetes mellitus, as well as the treatment of congestive heart failure (only in patients who cannot tolerate ACE inhibitors).[A255892]', 'Telmisartan interferes with the binding of angiotensin II to the angiotensin II AT<sub>1</sub>-receptor by binding reversibly and selectively to the receptors in vascular smooth muscle and the adrenal gland. As angiotensin II is a vasoconstrictor, which also stimulates the synthesis and release of aldosterone, blockage of its effects results in decreases in systemic vascular resistance. Telmisartan does not inhibit the angiotensin converting enzyme, other hormone receptors, or ion channels.[L44652] Studies also suggest that telmisartan is a partial agonist of PPAR&gamma;, which is an established target for antidiabetic drugs. This suggests that telmisartan can improve carbohydrate and lipid metabolism, as well as control insulin resistance without causing the side effects that are associated with full PPAR&gamma; activators.[A1492]', 'Telmisartan is an orally active nonpeptide angiotensin II antagonist that acts on the AT<sub>1</sub> receptor subtype. It has the highest affinity for the AT<sub>1</sub> receptor among commercially available ARBs and has minimal affinity for the AT<sub>2</sub> receptor.[L44652] New studies suggest that telmisartan may also have PPAR&gamma; agonistic properties that could potentially confer beneficial metabolic effects, as PPAR&gamma; is a nuclear receptor that regulates specific gene transcription, and whose target genes are involved in the regulation of glucose and lipid metabolism, as well as anti-inflammatory responses. This observation is currently being explored in clinical trials.[A1492] Angiotensin II is formed from angiotensin I in a reaction catalyzed by angiotensin-converting enzyme (ACE, kininase II). Angiotensin II is the principal pressor agent of the renin-angiotensin system, with effects that include vasoconstriction, stimulation of synthesis and release of aldosterone, cardiac stimulation, and renal reabsorption of sodium. Telmisartan works by blocking the vasoconstrictor and aldosterone secretory effects of angiotensin II.[A1490,L44652]', 'DrugBank');
