INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Covapravir 200 mg 40 caps.', 32218, 'كوفابرافير 200مجم 40 كبسولة', '461', '420', 'Antiviral', 'Antiviral', NULL, 'Molnupiravir', 'Eipico', 'Cap', 'كبسولة', '200 mg', '4', NULL, NULL, NULL, NULL, 778, '2025-12-17', NULL, 0, '[N4-hydroxycytidine] and its prodrug molnupiravir are being studied for its activity against a number of viral infections including influenza, MERS-CoV, and SARS-CoV-2.[A193014, A193029]



Molnupiravir is approved in the UK for reducing the risk of hospitalization and death in mild to moderate COVID-19 cases for patients at increased risk of severe disease (eg. with obesity, diabetes mellitus, heart disease, or are over 60 years old).[L39050,L39055]



In the US, molnupiravir is authorized for emergency use for the treatment of high-risk adults With mild to moderate COVID-19.[L39588]', 'Molnupiravir is hydrolyzed _in vivo_ to N4-hydroxycytidine, which is phosphorylated in tissue to the active 5’-triphosphate form, and incorporated into the genome of new virions, resulting in the accumulation of inactivating mutations, known as viral error catastrophe.[A193014,A193029] A [remdesivir] resistant mutant mouse hepatitis virus has also been shown to have increased sensitivity to N4-hydroxycytidine.[A193014]', NULL, 'DrugBank');
