INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Be-klairs skin cleansing soap 100 gm', 15046, 'بي-كليرس صابون منظف للبشرة 100 جم', '60', NULL, 'Soap', 'Soap', NULL, 'Titanium dioxide+chamomile ext.+tea tree oil+eucalyptus+thymol+lavender oil+black seed ext.+eruca sativa oil', 'Egyptian co. for cosmetics > new pharm', 'Soap', 'صابون', '100 gm', '1', NULL, NULL, 'skin cleansing soap - spf 50+ - with anti-bacterial activity - contains titanium dioxide as sunscreen', NULL, 678, '2022-08-04', NULL, 0, 'Titanium dioxide is used in most sunscreens to block UVA and UVB rays, similar to [zinc oxide].', 'Diminish the penetration of ultraviolet (UV) light through the epidermis by absorbing UV radiation within a specific wavelength range. The amount and wavelength of UV radiation absorbed are affected by the molecular structure of the sunscreen agent. ', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Sodium chloride 0.9 % (el nile) i.v. inf. 500 ml', 27162, 'صوديوم كلوريد 0.9% 500 مل محلول', '11', NULL, 'Normal saline', 'Normal saline', NULL, 'Sodium chloride', 'El nile.', 'Unknown', 'غير محدد', '0.9 %', '1', NULL, NULL, NULL, '6221067010922', 947, '2022-12-08', NULL, 0, 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank');
