INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Psorin 25mg 10 caps.', 25460, 'سورين 25مجم 10 كبسول', '40', NULL, 'Psoriasis', 'Psoriasis', NULL, 'Acitretin', 'International drug agency (idi)', 'Cap', 'كبسولة', '25mg', '1', NULL, NULL, 'description: an oral retinoid effective in the treatment of psoriasis. it is the major metabolite of etretinate with the advantage of a much shorter half-life when compared with etretinate. indication : for the treatment of severe psoriasis in adults. mec', NULL, 880, '2022-07-13', NULL, 0, 'For the treatment of severe psoriasis in adults.', 'The mechanism of action of acitretin is unknown, however it is believed to work by targeting specific receptors (retinoid receptors such as RXR and RAR) in the skin which help normalize the growth cycle of skin cells.', 'Acitretin is a retinoid. Retinoids have a structure similar to vitamin A and are involved in the normal growth of skin cells. Acitretin works by inhibiting the excessive cell growth and keratinisation (process by which skin cells become thickened due to the deposition of a protein within them) seen in psoriasis. It therefore reduces the thickening of the skin, plaque formation and scaling.', 'DrugBank');
